///     :__\  \    * `,'*         \  \  :  \   *  8`;'*  *      ///
///         `^'     \ :/           `^'  `-^-'   \v/ :  \/       ///
///=============================================================///
///=============================================================///
pragma solidity ^0.8;

import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";


/// @author Steakhouse 
/// @title ERC721 NFT Staking Contract
/// @notice Staking Contract that uses the Synthetix Staking model to distribute ERC20 token rewards in a dynamic way,
/// proportionally based on the amount of ERC721 tokens staked by each staker at any given time. Made specially for TTT

contract NFTStaking is ReentrancyGuard, Pausable, Ownable, IERC721Receiver {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    /* ========== STATE VARIABLES ========== */



    IERC20 immutable public rewardsToken;
    address[] public collectionAddresses;
    mapping(address => uint256) public collectionRatio;
    mapping(address => bool) public isAllowedCollection;
    mapping(address => uint256) public totalStaked;


    mapping(address => mapping(address => uint256)) public collectionToUserToBalances;
    mapping(address => uint256) public rewardPerTokenStored;
    mapping(address => uint256) public lastUpdateTime;

    uint256 public periodFinish = 0;
    uint256 public rewardsDuration = 28 days;
    uint256 public rewardRate = 0;

    mapping(address => mapping(address => uint256)) public collectionToUserToRewardPerTokenPaid;
    mapping(address => uint256) public rewards;
    mapping(address => mapping(address => uint256)) public collectionToUserToRewards;

    mapping(address => mapping(address => uint256[])) public userToCollectionToStakedNFTs;

    // fee
   uint256 public chargeStake;
    uint256 public chargeWithdraw;
    uint256 public chargeClaim;
    address public immutable chargePayee;

    /* ========== CONSTRUCTOR ========== */

    constructor(
        address _rewardsToken,
        address[] memory _collections,
        uint256[] memory _ratios,
        address _chargePayee,
        uint256 _chargeStake,
        uint256 _chargeWithdraw,
        uint256 _chargeClaim
    )  Ownable() {
        rewardsToken = IERC20(_rewardsToken);
        collectionAddresses = _collections;
        chargePayee = _chargePayee;
        for (uint256 i = 0; i < _collections.length; i++) {
            collectionRatio[_collections[i]] = _ratios[i];
            isAllowedCollection[_collections[i]] = true;
        }
        chargeStake = _chargeStake;
        chargeWithdraw = _chargeWithdraw;
        chargeClaim = _chargeClaim;
    }

    /* ========== VIEWS ========== */

    function balanceOf(address collection, address account) external view returns (uint256) {
        return collectionToUserToBalances[collection][account];
    }

    function lastTimeRewardApplicable() public view returns (uint256) {
        return block.timestamp < periodFinish ? block.timestamp : periodFinish;
    }

    function rewardPerToken(address collection) public view returns (uint256) {
        if (totalStaked[collection] == 0) {
            return rewardPerTokenStored[collection];
        }
        return
            rewardPerTokenStored[collection]
            .add(
                lastTimeRewardApplicable()
                .sub(lastUpdateTime[collection])
                .mul(rewardRate)
                .mul(1e18)
                .mul(collectionRatio[collection])
                .div(100)
                .div(totalStaked[collection])
            );
    }

    function earned(address collection, address account) public view returns (uint256) {
        return collectionToUserToBalances[collection][account]
        .mul(rewardPerToken(collection).sub(collectionToUserToRewardPerTokenPaid[collection][account]))
        .div(1e18)
        .add(rewards[account]);
    }

    function getRewardForDuration() external view returns (uint256) {
        return rewardRate.mul(rewardsDuration);
    }

    /* ========== FEE FUNCTIONS ========== */
    function setChargeFee(uint256 _stake, uint256 _withdraw, uint256 _claim) public {
        require(chargePayee != address(0), "Fee setter is not set");
        require(msg.sender == chargePayee, "only Fee setter can set fee");
        chargeStake = _stake;
        chargeWithdraw = _withdraw;
        chargeClaim = _claim;
    }


    function claimChargeFee() public {
        require(chargePayee != address(0), "Fee setter is not set");
        require(msg.sender == chargePayee, "only Fee setter can claim fee");
        payable(msg.sender).transfer(address(this).balance);
    }

    /* ========== MUTATIVE FUNCTIONS ========== */

    function stake(address collection, uint256[] memory ids) external payable nonReentrant whenNotPaused updateReward(collection, msg.sender) {
        require(msg.value == chargeStake * ids.length, "stake fee is not enough");
        require(ids.length > 0, "Cannot stake 0 nft");
        require(isAllowedCollection[collection], "Collection is not allowed");

        totalStaked[collection] = totalStaked[collection].add(ids.length);
        collectionToUserToBalances[collection][msg.sender] = collectionToUserToBalances[collection][msg.sender].add(ids.length);
        for (uint256 i = 0; i < ids.length; i++) {
            IERC721(collection).safeTransferFrom(msg.sender, address(this), ids[i]);
            userToCollectionToStakedNFTs[msg.sender][collection].push(ids[i]);

        }
        emit Staked(msg.sender, collection, ids.length);
    }

function getUserStakedNFTsForCollection(address collection, address user) public view returns (uint256[] memory) {
    return userToCollectionToStakedNFTs[user][collection];
}

function withdraw(address collection, uint256[] memory ids) public payable nonReentrant updateReward(collection, msg.sender) {
    require(msg.value == chargeWithdraw * ids.length, "withdraw fee is not enough");
    require(ids.length > 0, "Cannot withdraw 0 nft");

    totalStaked[collection] = totalStaked[collection].sub(ids.length);
    collectionToUserToBalances[collection][msg.sender] = collectionToUserToBalances[collection][msg.sender].sub(ids.length);

    uint256[] storage stakedNFTs = userToCollectionToStakedNFTs[msg.sender][collection];

    for (uint256 i = 0; i < ids.length; i++) {
        require(stakedNFTs.length > 0, "No staked NFT to withdraw");
        bool found = false;

        for (uint256 j = 0; j < stakedNFTs.length; j++) {
            if (stakedNFTs[j] == ids[i]) {
                // Swap the element with the last element
                stakedNFTs[j] = stakedNFTs[stakedNFTs.length - 1];
                found = true;
                break;
            }
        }

        require(found, "NFT not staked by the user");
        stakedNFTs.pop();
        IERC721(collection).safeTransferFrom(address(this), msg.sender, ids[i]);
    }

    emit Withdrawn(msg.sender, collection, ids.length);
}




    function getReward() public payable nonReentrant {
        require(msg.value == chargeClaim, "claim fee is not enough");
        for (uint256 i = 0; i < collectionAddresses.length; i++) {
            _updateReward(collectionAddresses[i], msg.sender);
        }
        uint256 reward = rewards[msg.sender];
        if (reward > 0) {
            rewards[msg.sender] = 0;
            rewardsToken.safeTransfer(msg.sender, reward);
            emit RewardPaid(msg.sender, reward);
        }
    }

    /* ========== RESTRICTED FUNCTIONS ========== */

    function notifyRewardAmount(uint256 reward) external onlyOwner {
        for (uint256 i = 0; i < collectionAddresses.length; i++) {
            _updateReward(collectionAddresses[i], address(0));
        }
        if (block.timestamp >= periodFinish) {
            rewardRate = reward.div(rewardsDuration);
        } else {
            uint256 remaining = periodFinish.sub(block.timestamp);
            uint256 leftover = remaining.mul(rewardRate);
            rewardRate = reward.add(leftover).div(rewardsDuration);
        }

        uint balance = rewardsToken.balanceOf(address(this));
        require(rewardRate <= balance.div(rewardsDuration), "Provided reward too high");

        for (uint256 i = 0; i < collectionAddresses.length; i++) {
            lastUpdateTime[collectionAddresses[i]] = block.timestamp;
        }
        periodFinish = block.timestamp.add(rewardsDuration);
        emit RewardAdded(reward);
    }

    // Added to support recovering LP Rewards from other systems such as BAL to be distributed to holders
    function recoverERC20(address tokenAddress, uint256 tokenAmount) external onlyOwner {
        IERC20(tokenAddress).safeTransfer(owner(), tokenAmount);
        emit Recovered(tokenAddress, tokenAmount);
    }

    function setRewardsDuration(uint256 _rewardsDuration) external onlyOwner {
        require(
            block.timestamp > periodFinish,
            "Previous rewards period must be complete before changing the duration for the new period"
        );
        rewardsDuration = _rewardsDuration;
        emit RewardsDurationUpdated(rewardsDuration);
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    /* ========== MODIFIERS ========== */

    modifier updateReward(address collection, address account) {
        _updateReward(collection, account);
        _;
    }

    function _updateReward(address collection, address account) internal {
        rewardPerTokenStored[collection] = rewardPerToken(collection);
        lastUpdateTime[collection] = lastTimeRewardApplicable();
        if (account != address(0)) {
            rewards[account] = earned(collection, account);
            collectionToUserToRewardPerTokenPaid[collection][account] = rewardPerTokenStored[collection];
        }
    }

    function onERC721Received(address operator, address from, uint256 tokenId, bytes memory data) external pure returns (bytes4) {
        return IERC721Receiver.onERC721Received.selector;
    }

