{{
  "language": "Solidity",
  "sources": {
    "contracts/CSLoot.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.16;

import {console} from "lib/forge-std/src/console.sol";
import {Strings} from "@openzeppelin/contracts/utils/Strings.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {Managerial} from "./Managerial.sol";
import {Rarity, RangeNames, Slot, Attribute, LootBagType, ItemDescriptor} from "./interfaces/Globals.sol";
import {Inventories} from "./interfaces/Inventories.sol";
import {ILoot} from "./interfaces/ILoot.sol";
import {IStadium} from "./interfaces/IStadium.sol";
import {VRFCoordinatorV2Interface} from "@chainlink/contracts/src/v0.8/interfaces/VRFCoordinatorV2Interface.sol";
import {VRFConsumerBaseV2} from "@chainlink/contracts/src/v0.8/VRFConsumerBaseV2.sol";

contract CSLoot is ILoot, Managerial, VRFConsumerBaseV2 {
  using SafeERC20 for IERC20;

  uint8 private constant REQUEST_CONFIRMATIONS = 3;
  uint32 public constant CALLBACK_GAS_LIMIT = 2_000_000;

  VRFCoordinatorV2Interface private immutable coordinator;
  uint64 private immutable chainlinkSubscriptionId;
  bytes32 private immutable chainlinkKeyHash;

  address public inventoriesContract;
  address public stadiumContract;
  address public immutable currencyAddress;
  address public immutable treasuryAddress;

  uint public constant MAX = 1;
  uint public constant MIN = 0;

  uint32 public winnerBoxTotalWeight;
  uint32 public loserBoxTotalWeight;
  uint32 public blendedBoxTotalWeight;

  uint32[20] public winnerBoxRangeEnds;
  uint32[20] public loserBoxRangeEnds;
  uint32[20] public blendedBoxRangeEnds;

  uint8[6] public identsPerSlot;

  mapping(Slot => mapping(Rarity => mapping(Attribute => uint16[2]))) public rollRanges;
  mapping(LootBagType => mapping(Slot => mapping(Rarity => uint8))) public namedItemAmount;
  mapping(Slot => Attribute[2]) public slotToAttributes;

  struct Gear {
    Rarity rarity;
    Slot slot;
    AttributeValue[] maxValues;
  }

  struct AttributeValue {
    Attribute attribute;
    uint maxValue;
  }

  struct CatchupStatus {
    uint16 purchased;
    uint16 received;
    uint16 revealed;
  }

  mapping(uint256 => CatchupStatus) public tokenToCatchupStatus;
  struct CatchupConfig {
    bool enabled;
    uint8 percentage;
    uint256 price;
  }
  CatchupConfig public currentCatchupConfig;

  struct ChainLinkRequest {
    bool fulfilled; // whether the request has been successfully fulfilled
    bool revealed; // whether the request has been successfully fulfilled
    bool exists; // whether a requestId exists
    uint256 gladiatorId;
    uint256 randomSeed;
    address buyer;
  }

  mapping(uint256 => ChainLinkRequest) public chainlinkRequestIdToStatus;

  event CatchupBoxPurchased(
    uint256 indexed tokenId,
    uint256 indexed dayIndex,
    address indexed buyer,
    uint256 requestId
  );

  event RequestFulfilled(uint256 requestId, uint256[] randomWords);
  event CatchupBoxRevealed(uint256 requestId, uint256 indexed gladiatorId, bool result);

  event LootDropped(
    bytes32 indexed battleId,
    uint256 indexed tokenId,
    LootBagType indexed lootBagType,
    ItemDescriptor stats
  );

  event LootEquipped(
    bytes32 indexed battleId,
    uint256 indexed tokenId,
    LootBagType indexed lootBagType,
    ItemDescriptor stats
  );

  constructor(
    address _inventoriesContract,
    address _stadiumContract,
    address _currencyAddress,
    address _treasuryAddress,
    uint64 _subscriptionId,
    address _vrfCoordinator,
    bytes32 _keyHash
  ) VRFConsumerBaseV2(_vrfCoordinator) {
    require(_inventoriesContract != address(0), "invalid inv");
    require(_stadiumContract != address(0), "invalid inv");
    require(_currencyAddress != address(0), "invalid currency");
    require(_treasuryAddress != address(0), "invalid treasury");
    inventoriesContract = _inventoriesContract;
    stadiumContract = _stadiumContract;
    currencyAddress = _currencyAddress;
    treasuryAddress = _treasuryAddress;

    coordinator = VRFCoordinatorV2Interface(_vrfCoordinator);
    chainlinkSubscriptionId = _subscriptionId;
    chainlinkKeyHash = _keyHash;

    _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    _grantRole(MANAGER_ROLE, msg.sender);
  }

  function setIdentCountPerSlot(uint8[6] memory newIdents) public onlyManagers {
    require(newIdents[uint(Slot.NOTHING)] == 0, "Illegal ident for Nothing");
    for (uint i = 1; i < newIdents.length; i++) {
      require(newIdents[i] > 0, "Zero idents for non-Nothing slot");
      identsPerSlot[i] = newIdents[i];
    }
  }

  function setLootRanges(
    uint32[20] memory newRanges,
    uint32 newTotalWeight,
    LootBagType lootbagType
  ) public onlyManagers {
    require(
      newRanges[uint(RangeNames.TRINKET_LEGENDARY)] == newTotalWeight,
      string.concat("Last index must be same as newTotalWeight: ", Strings.toString(newTotalWeight))
    );

    uint32 previous = 0;
    for (uint i = 0; i < newRanges.length; i++) {
      require(newRanges[i] > previous, "Cannot overlap previous");
      previous = newRanges[i];
      if (lootbagType == LootBagType.NORMAL) {
        winnerBoxRangeEnds[i] = newRanges[i];
      } else {
        loserBoxRangeEnds[i] = newRanges[i];
      }
    }

    if (lootbagType == LootBagType.NORMAL) {
      winnerBoxTotalWeight = newTotalWeight;
    } else {
      loserBoxTotalWeight = newTotalWeight;
    }
  }

  function _calculateIdentForSlot(uint256 randomNumber, Slot slot) internal view returns (uint8 _identForSlot) {
    uint8 countOfIdents = identsPerSlot[uint(slot)];
    return uint8(randomNumber % countOfIdents);
  }

  function _calculateLoot(uint256 randomNumber, LootBagType bagType) internal view returns (Slot, Rarity, uint8) {
    uint32[20] memory ranges = bagType == LootBagType.NORMAL ? winnerBoxRangeEnds : loserBoxRangeEnds;
    uint32 totalWeight = bagType == LootBagType.NORMAL ? winnerBoxTotalWeight : loserBoxTotalWeight;

    uint randomNumberInRange = (randomNumber % totalWeight) + 1;

    // Init chosen range to nothing.
    RangeNames chosenGear = RangeNames.NONE;

    // If your random number is < the current threshold, then that's the range that you're in.
    for (uint256 i = 0; i < ranges.length; i++) {
      if (randomNumberInRange <= ranges[i]) {
        chosenGear = RangeNames(i);
        break;
      }
    }

    assert(chosenGear != RangeNames.NONE);

    // Now that we've chosen a range, map that to the correct Slot/Rarity values
    // Init to nothing.
    Slot chosenSlot = Slot.NOTHING;
    Rarity chosenRarity = Rarity.NOTHING;

    // Dumb if/else branch to break apart into individual values
    if (chosenGear == RangeNames.WEAPON_UNCOMMON) {
      chosenSlot = Slot.WEAPON;
      chosenRarity = Rarity.UNCOMMON;
    } else if (chosenGear == RangeNames.WEAPON_RARE) {
      chosenSlot = Slot.WEAPON;
      chosenRarity = Rarity.RARE;
    } else if (chosenGear == RangeNames.WEAPON_EPIC) {
      chosenSlot = Slot.WEAPON;
      chosenRarity = Rarity.EPIC;
    } else if (chosenGear == RangeNames.WEAPON_LEGENDARY) {
      chosenSlot = Slot.WEAPON;
      chosenRarity = Rarity.LEGENDARY;
    } else if (chosenGear == RangeNames.SECONDARY_WEAPON_UNCOMMON) {
      chosenSlot = Slot.SECONDARY_WEAPON;
      chosenRarity = Rarity.UNCOMMON;
    } else if (chosenGear == RangeNames.SECONDARY_WEAPON_RARE) {
      chosenSlot = Slot.SECONDARY_WEAPON;
      chosenRarity = Rarity.RARE;
    } else if (chosenGear == RangeNames.SECONDARY_WEAPON_EPIC) {
      chosenSlot = Slot.SECONDARY_WEAPON;
      chosenRarity = Rarity.EPIC;
    } else if (chosenGear == RangeNames.SECONDARY_WEAPON_LEGENDARY) {
      chosenSlot = Slot.SECONDARY_WEAPON;
      chosenRarity = Rarity.LEGENDARY;
    } else if (chosenGear == RangeNames.HELMET_UNCOMMON) {
      chosenSlot = Slot.HELMET;
      chosenRarity = Rarity.UNCOMMON;
    } else if (chosenGear == RangeNames.HELMET_RARE) {
      chosenSlot = Slot.HELMET;
      chosenRarity = Rarity.RARE;
    } else if (chosenGear == RangeNames.HELMET_EPIC) {
      chosenSlot = Slot.HELMET;
      chosenRarity = Rarity.EPIC;
    } else if (chosenGear == RangeNames.HELMET_LEGENDARY) {
      chosenSlot = Slot.HELMET;
      chosenRarity = Rarity.LEGENDARY;
    } else if (chosenGear == RangeNames.ARMOR_UNCOMMON) {
      chosenSlot = Slot.ARMOR;
      chosenRarity = Rarity.UNCOMMON;
    } else if (chosenGear == RangeNames.ARMOR_RARE) {
      chosenSlot = Slot.ARMOR;
      chosenRarity = Rarity.RARE;
    } else if (chosenGear == RangeNames.ARMOR_EPIC) {
      chosenSlot = Slot.ARMOR;
      chosenRarity = Rarity.EPIC;
    } else if (chosenGear == RangeNames.ARMOR_LEGENDARY) {
      chosenSlot = Slot.ARMOR;
      chosenRarity = Rarity.LEGENDARY;
    } else if (chosenGear == RangeNames.TRINKET_UNCOMMON) {
      chosenSlot = Slot.TRINKET;
      chosenRarity = Rarity.UNCOMMON;
    } else if (chosenGear == RangeNames.TRINKET_RARE) {
      chosenSlot = Slot.TRINKET;
      chosenRarity = Rarity.RARE;
    } else if (chosenGear == RangeNames.TRINKET_EPIC) {
      chosenSlot = Slot.TRINKET;
      chosenRarity = Rarity.EPIC;
    } else if (chosenGear == RangeNames.TRINKET_LEGENDARY) {
      chosenSlot = Slot.TRINKET;
      chosenRarity = Rarity.LEGENDARY;
    } else {
      revert("Invalid chosen gear index");
    }

    uint8 cosmeticIdent = _calculateIdentForSlot(randomNumber, chosenSlot);
    return (chosenSlot, chosenRarity, cosmeticIdent);
  }

  function generateLoot(LootBagType bagType, uint256 someRandomness) internal view returns (ItemDescriptor memory) {
    (Slot thisSlot, Rarity thisRarity, uint8 thisIdent) = _calculateLoot(someRandomness, bagType);

    require(thisRarity != Rarity.NOTHING, "invalid nothing rarity");
    require(thisSlot != Slot.NOTHING, "invalid nothing slot");

    Attribute[2] memory attributes = slotToAttributes[thisSlot];

    Attribute primaryStat = thisSlot == Slot.TRINKET ? Attribute(random(5, someRandomness)) : attributes[0];
    if (thisSlot == Slot.TRINKET) {
      attributes[0] = primaryStat;
      assert(uint8(primaryStat) >= 1 && uint8(primaryStat) <= 5); //can be anything but speed
    }

    // Set the roll range nonce based on the unique randomness for this player
    uint256 primaryStatNonce = someRandomness;
    uint256 secondaryStatNonce = uint256(keccak256(abi.encodePacked(someRandomness, uint(121232343454565))));

    uint16 primaryStatRoll = rollFromRange(rollRanges[thisSlot][thisRarity][primaryStat], primaryStatNonce);
    assert(primaryStatRoll >= rollRanges[thisSlot][thisRarity][primaryStat][0]);
    assert(primaryStatRoll <= rollRanges[thisSlot][thisRarity][primaryStat][1]);

    uint16 secStatRoll = 0; //just a safe default.

    if (attributes[1] != Attribute.NOTHING_OR_RANDOM) {
      secStatRoll = rollFromRange(rollRanges[thisSlot][thisRarity][attributes[1]], secondaryStatNonce);
      assert(secStatRoll >= rollRanges[thisSlot][thisRarity][attributes[1]][0]);
      assert(secStatRoll <= rollRanges[thisSlot][thisRarity][attributes[1]][1]);
    }

    ItemDescriptor memory newLoot = ItemDescriptor(thisRarity, thisSlot, thisIdent, 0, 0, 0, 0, 0, 0);

    // Slither recommends: "Don't use strict equality to determine if an account has enough Ether or tokens."
    // but here we're doing a check against an enum, so enumerating through values should be fine.
    // slither-disable-start incorrect-equality
    for (uint256 i; i < 2; i++) {
      Attribute thisAttribute = attributes[i];
      uint16 thisStat = i == 0 ? primaryStatRoll : secStatRoll;
      if (thisAttribute == Attribute.ATTACK) {
        newLoot.attack += thisStat;
      } else if (thisAttribute == Attribute.DEFENSE) {
        newLoot.defense += thisStat;
      } else if (thisAttribute == Attribute.HP) {
        newLoot.hp += thisStat;
      } else if (thisAttribute == Attribute.SPECIAL_ATTACK) {
        newLoot.specialAttack += thisStat;
      } else if (thisAttribute == Attribute.SPECIAL_DEFENSE) {
        newLoot.specialDefense += thisStat;
      } else if (thisAttribute == Attribute.SPEED) {
        newLoot.speed += thisStat;
      } else if (thisSlot != Slot.TRINKET && thisAttribute != Attribute.NOTHING_OR_RANDOM) {
        revert("Invalid attr");
      }
    }
    // slither-disable-end incorrect-equality
    return newLoot;
  }

  function rewardLootViaBattle(
    bytes32 battleId,
    uint256 tokenId,
    LootBagType bagType,
    uint256 someRandomness
  ) external onlyManagers {
    rewardLoot(battleId, tokenId, bagType, someRandomness);
  }

  function rewardLoot(
    bytes32 battleId,
    uint256 tokenId,
    LootBagType bagType,
    uint256 someRandomness
  ) internal returns (bool) {
    ItemDescriptor memory newLoot = generateLoot(bagType, someRandomness);
    emit LootDropped(battleId, tokenId, bagType, newLoot);

    ItemDescriptor memory existingLoot = Inventories(inventoriesContract).getEquippedSlotForToken(
      tokenId,
      newLoot.slot
    );

    if (existingLoot.slot == Slot.NOTHING || (getQualityScoreForLoot(newLoot) > getQualityScoreForLoot(existingLoot))) {
      emit LootEquipped(battleId, tokenId, bagType, newLoot);
      Inventories(inventoriesContract).setGearForTokenAndSlot(tokenId, newLoot.slot, newLoot);
      Inventories(inventoriesContract).updateStatsForPlayer(tokenId);
      return true;
    } else {
      return false;
    }
  }

  function rollFromRange(uint16[2] memory range, uint256 nonce) internal view returns (uint16) {
    uint16 diff = range[1] - range[0];
    uint16 rando = uint16(randomWithInputUnpadded(nonce, diff));
    return range[0] + rando; //boost it back up
  }

  function _getStatsForLoot(
    ItemDescriptor memory item
  )
    internal
    view
    returns (Attribute _primaryAttribute, uint32 _primaryValue, Attribute _secondaryAttribute, uint32 _secondaryValue)
  {
    Attribute primaryAttribute = slotToAttributes[item.slot][0];
    Attribute secondaryAttribute = slotToAttributes[item.slot][1];

    if (item.slot == Slot.TRINKET) {
      primaryAttribute = determineRandomPrimaryOfTrinket(item);
      assert(primaryAttribute != Attribute.NOTHING_OR_RANDOM);
      secondaryAttribute = Attribute.SPEED;
    }

    uint32 primaryStat;
    uint32 secondaryStat;

    if (primaryAttribute == Attribute.ATTACK) {
      primaryStat = item.attack;
    } else if (primaryAttribute == Attribute.DEFENSE) {
      primaryStat = item.defense;
    } else if (primaryAttribute == Attribute.HP) {
      primaryStat = item.hp;
    } else if (primaryAttribute == Attribute.SPECIAL_ATTACK) {
      primaryStat = item.specialAttack;
    } else if (primaryAttribute == Attribute.SPECIAL_DEFENSE) {
      primaryStat = item.specialDefense;
    } else if (primaryAttribute == Attribute.SPEED) {
      primaryStat = item.speed;
    } else {
      revert("Invalid attr - primary");
    }

    if (secondaryAttribute == Attribute.NOTHING_OR_RANDOM) {
      secondaryStat = 0;
    } else if (secondaryAttribute == Attribute.ATTACK) {
      secondaryStat = item.attack;
    } else if (secondaryAttribute == Attribute.DEFENSE) {
      secondaryStat = item.defense;
    } else if (secondaryAttribute == Attribute.HP) {
      secondaryStat = item.hp;
    } else if (secondaryAttribute == Attribute.SPECIAL_ATTACK) {
      secondaryStat = item.specialAttack;
    } else if (secondaryAttribute == Attribute.SPECIAL_DEFENSE) {
      secondaryStat = item.specialDefense;
    } else if (secondaryAttribute == Attribute.SPEED) {
      secondaryStat = item.speed;
    } else {
      revert("Invalid attr - secondary");
    }

    return (primaryAttribute, primaryStat, secondaryAttribute, secondaryStat);
  }

  function determineRandomPrimaryOfTrinket(ItemDescriptor memory trinket) internal pure returns (Attribute) {
    if (trinket.hp > 0) {
      return Attribute.HP;
    } else if (trinket.attack > 0) {
      return Attribute.ATTACK;
    } else if (trinket.defense > 0) {
      return Attribute.DEFENSE;
    } else if (trinket.specialAttack > 0) {
      return Attribute.SPECIAL_ATTACK;
    } else if (trinket.specialDefense > 0) {
      return Attribute.SPECIAL_DEFENSE;
    } else {
      return Attribute.NOTHING_OR_RANDOM; //which means nothing equipped.
    }
  }

  function updateSlotAttributes(Slot slot, Attribute primary, Attribute secondary) external onlyManagers {
    if (slot == Slot.TRINKET) {
      require(primary == Attribute.NOTHING_OR_RANDOM, "Invalid trinket attr");
    }
    slotToAttributes[slot] = [primary, secondary];
  }

  function returnAttributesForSlot(Slot slot) external view returns (Attribute[2] memory) {
    return slotToAttributes[slot];
  }

  function getQualityScoreForLoot(ItemDescriptor memory item) public view returns (uint32) {
    Slot thisSlot = item.slot;

    (Attribute primaryStat, uint32 primaryValue, Attribute secondaryStat, uint32 secondaryValue) = _getStatsForLoot(
      item
    );

    uint32 primaryMax = rollRanges[thisSlot][Rarity.LEGENDARY][primaryStat][MAX];
    uint32 secondaryMax = secondaryStat == Attribute.NOTHING_OR_RANDOM
      ? 0
      : rollRanges[thisSlot][Rarity.LEGENDARY][secondaryStat][MAX];

    if (secondaryStat == Attribute.NOTHING_OR_RANDOM) {
      return uint32((10000 * 2 * primaryValue) / (2 * primaryMax));
    }

    uint256 primaryQualityScore = (2 * (primaryValue * secondaryMax));
    uint256 secondaryQualityScore = (secondaryValue * primaryMax);
    uint256 commonDenominator = secondaryMax == 0 ? primaryMax : primaryMax * secondaryMax;
    if (secondaryValue != 0) {
      primaryQualityScore += secondaryQualityScore;
    }

    return uint32((10000 * primaryQualityScore) / (2 * commonDenominator));
  }

  function updateRollRanges(
    Slot slot,
    Rarity rarity,
    Attribute attribute,
    uint16[2] calldata amounts
  ) external onlyManagers {
    require(uint8(slot) >= 1 && uint8(slot) <= 5, "Invalid slot"); //Is this actually needed? does the enum check that automatically?
    rollRanges[slot][rarity][attribute] = amounts;
  }

  function random(uint256 max, uint256 nonce) public view returns (uint256) {
    require(max > 0, "Max cannot be zero");

    uint256 randomness = uint256(
      // slither-disable-next-line weak-prng, timestamp
      //solhint-disable-next-line not-rely-on-time
      keccak256(abi.encodePacked(block.timestamp, block.difficulty, msg.sender, nonce))
    );
    return (randomness % max) + 1;
  }

  function randomWithInputUnpadded(uint256 nonce, uint256 max) private view returns (uint) {
    uint256 result = uint(
      //solhint-disable-next-line not-rely-on-time
      keccak256(abi.encodePacked(block.difficulty, block.timestamp, nonce))
    );
    return (result % max);
  }

  function setNewInventoriesContract(address newContract) external onlyManagers {
    require(newContract != address(0), "Invalid Inventories address");
    inventoriesContract = newContract;
  }

  function setNewStadiumContract(address newContract) external onlyManagers {
    require(newContract != address(0), "Invalid Stadium address");
    stadiumContract = newContract;
  }

  function setCatchupConfig(bool newState, uint8 newPct, uint256 newPrice) external onlyManagers {
    currentCatchupConfig.enabled = newState;
    currentCatchupConfig.percentage = newPct;
    currentCatchupConfig.price = newPrice;
  }

  function getPurchasableBoxAmountForDay(uint16 dob) public view returns (uint16) {
    if (dob == 0) {
      return 0;
    } else {
      uint16 diff = 100 - currentCatchupConfig.percentage;
      uint16 subtr = (dob * diff) / 100;
      return dob - subtr;
    }
  }

  function getPurchasableBoxAmountForToken(uint256 tokenId) public view returns (uint16) {
    return getPurchasableBoxAmountForDay(IStadium(stadiumContract).tokenIdToBirthday(tokenId));
  }

  // @notice Allows player to buy one or more loot boxes for their NFT.
  // @param tokenId The NFT we're buying loot boxes for
  // @param amt The amount of loot boxes to buy. (Capped, but user can buy less than the max)
  // @dev No restriction on buying loot boxes on behalf of another owner.
  // This function should be allowed to end users so marking as
  // solhint-disable-next-line custom/internal-or-protected
  function purchaseLootBox(uint256 tokenId, uint8 amt) external returns (uint256[] memory) {
    require(currentCatchupConfig.enabled, "not enabled");
    // Slither: low risk as external contract is trusted 1st party and this method is callable by managers only.
    //slither-disable-next-line calls-loop
    require(amt > 0, "need nonzero");
    uint16 currentMax = getPurchasableBoxAmountForToken(tokenId);
    uint16 purchased = tokenToCatchupStatus[tokenId].purchased;
    require(currentMax > purchased, "cant buy more");
    uint16 diff = currentMax - purchased;
    require(amt <= diff, "need valid amt");

    tokenToCatchupStatus[tokenId].purchased += amt;
    uint256 currentDay = IStadium(stadiumContract).getCurrentDay();

    uint256[] memory reqIds = new uint256[](amt);
    for (uint i = 0; i < amt; i++) {
      // Slither: low risk as the `amt` caps the length of the loop and trusting that CL won't revert so long as
      // enough LINK is in place (which we can control)
      //slither-disable-next-line calls-loop, reentrancy-benign
      uint256 _requestId = coordinator.requestRandomWords(
        chainlinkKeyHash,
        chainlinkSubscriptionId,
        REQUEST_CONFIRMATIONS,
        CALLBACK_GAS_LIMIT,
        1
      );

      chainlinkRequestIdToStatus[_requestId] = ChainLinkRequest({
        exists: true,
        fulfilled: false,
        revealed: false,
        gladiatorId: tokenId,
        buyer: msg.sender,
        randomSeed: 0
      });
      emit CatchupBoxPurchased(tokenId, currentDay, msg.sender, _requestId);
      reqIds[i] = _requestId;
    }
    IERC20(currencyAddress).safeTransferFrom(msg.sender, treasuryAddress, amt * currentCatchupConfig.price);
    return reqIds;
  }

  function fulfillRandomWords(uint256 requestId, uint256[] memory randomWords) internal override {
    ChainLinkRequest memory thisRequest = chainlinkRequestIdToStatus[requestId];
    require(thisRequest.exists, "Request not found");
    require(!thisRequest.fulfilled, "Request was already fulfilled");
    chainlinkRequestIdToStatus[requestId].fulfilled = true;
    tokenToCatchupStatus[thisRequest.gladiatorId].received += 1;
    chainlinkRequestIdToStatus[requestId].randomSeed = randomWords[0];
    emit RequestFulfilled(requestId, randomWords);
  }

  // @notice Allows user to reveal a catchbox they purchased.
  // @param requestId The vrf request id
  // @dev This function should be allowed to end users so marking as
  // solhint-disable-next-line custom/internal-or-protected
  function revealLootBox(uint256 requestId) external {
    require(!IStadium(stadiumContract).locked(), "cannot while locked");

    ChainLinkRequest memory thisRequest = chainlinkRequestIdToStatus[requestId];
    require(thisRequest.exists, "Request not found");
    require(thisRequest.fulfilled, "Request isnt fulfilled");
    require(!thisRequest.revealed, "Request was already revealed");
    require(
      IStadium(stadiumContract).getActualOwnerForGladiator(thisRequest.gladiatorId) == msg.sender,
      "must be owner"
    );
    chainlinkRequestIdToStatus[requestId].revealed = true;
    tokenToCatchupStatus[thisRequest.gladiatorId].revealed += 1;
    bool result = rewardLoot(bytes32(0), thisRequest.gladiatorId, LootBagType.BLENDED, thisRequest.randomSeed);
    emit CatchupBoxRevealed(requestId, thisRequest.gladiatorId, result);
  }
}
"
    },
    "contracts/Managerial.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.16;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

abstract contract Managerial is AccessControl, Pausable {
  bytes32 public constant MANAGER_ROLE = keccak256("MANAGER_ROLE");

  modifier onlySuperAdmin() {
    require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Caller is not a superadmin");
    _;
  }

  modifier onlyManagers() {
    require(hasRole(MANAGER_ROLE, msg.sender), "Caller is not a manager");
    _;
  }

  function addManager(address target) external onlySuperAdmin {
    require(target != address(0), "inval address");
    _grantRole(MANAGER_ROLE, target);
  }

  function removeManager(address target) external onlySuperAdmin {
    require(target != address(0), "inval address");
    _revokeRole(MANAGER_ROLE, target);
  }

  function pause() external onlyManagers {
    _pause();
  }

  function unpause() external onlyManagers {
    _unpause();
  }
}
"
