    "contracts/interfaces/Globals.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.16;

enum PlayState {
  NONE,
  TRAINING_GROUNDS,
  STADIUM,
  DEAD
}

enum LootBagType {
  NORMAL,
  REDUCED,
  BLENDED
}

struct FullMatchup {
  uint256 dayIndex;
  PlayState venue;
  uint256[2] players;
}

struct ItemDescriptor {
  Rarity rarityCode;
  Slot slot; //using for passing purposes
  uint8 ident; //named weapon
  uint16 hp;
  uint16 attack;
  uint16 defense;
  uint16 specialAttack;
  uint16 specialDefense;
  uint16 speed;
}

struct Stats {
  uint16 attack;
  uint16 defense;
  uint16 specialAttack;
  uint16 specialDefense;
  uint16 speed;
  int16 hp;
}

struct TurnInfo {
  uint8 counter;
  uint256 actorId;
  uint256 targetId;
  Stats actor;
  Stats target;
}

struct DayBreakdown {
  uint256[] winners;
  uint256[] losers;
  bool didOneGladiatorHaveBy;
}

struct DailyCombatBreakdown {
  uint16 wins;
  uint16 losses;
}

enum Slot {
  NOTHING,
  WEAPON,
  SECONDARY_WEAPON,
  HELMET,
  ARMOR,
  TRINKET
}
enum Rarity {
  NOTHING,
  UNCOMMON,
  RARE,
  EPIC,
  LEGENDARY
}

enum Attribute {
  NOTHING_OR_RANDOM,
  ATTACK,
  DEFENSE,
  HP,
  SPECIAL_ATTACK,
  SPECIAL_DEFENSE,
  SPEED
}

enum RangeNames {
  WEAPON_UNCOMMON,
  WEAPON_RARE,
  WEAPON_EPIC,
  WEAPON_LEGENDARY,
  SECONDARY_WEAPON_UNCOMMON,
  SECONDARY_WEAPON_RARE,
  SECONDARY_WEAPON_EPIC,
  SECONDARY_WEAPON_LEGENDARY,
  HELMET_UNCOMMON,
  HELMET_RARE,
  HELMET_EPIC,
  HELMET_LEGENDARY,
  ARMOR_UNCOMMON,
  ARMOR_RARE,
  ARMOR_EPIC,
  ARMOR_LEGENDARY,
  TRINKET_UNCOMMON,
  TRINKET_RARE,
  TRINKET_EPIC,
  TRINKET_LEGENDARY,
  NONE
}
"
    },
    "contracts/interfaces/ILoot.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.16;

import "./Globals.sol";

interface ILoot {
  function rewardLootViaBattle(bytes32 battleId, uint256 tokenId, LootBagType bagType, uint256 someRandomness) external;
}
"
    },
    "contracts/interfaces/IStadium.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.16;

import "./Globals.sol";

interface IStadium {
  function getPlayStateForToken(uint256 tokenId) external returns (PlayState);

  function getGladiatorFromVenue(PlayState code, uint256 index) external view returns (uint256);

  function tokenIdToOwner(uint256 tokenId) external returns (address);

  function removePlayerFromStadium(uint256 tokenId) external;

  function getCurrentDay() external view returns (uint256);

  function addressCanDoLoserMint() external view returns (bool);

  function registerNewPlayer(uint256 tokenId) external;

  function locked() external view returns (bool);

  function tokenIdToBirthday(uint256 tokenId) external view returns (uint16);

  function getGladiatorsForVenue(PlayState code) external view returns (uint256[] memory);

  function getGladiatorCountForVenue(PlayState venueCode) external view returns (uint256);

  function shuffleGladiator(PlayState code, uint256 lastItem, uint256 randomSeed) external;

  function getActualOwnerForGladiator(uint256 gladiatorId) external view returns (address);
}
"
    },
    "contracts/interfaces/Inventories.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.16;

import "./Globals.sol";

interface Inventories {
  function getEquippedSlotForToken(uint256 tokenId, Slot slot) external returns (ItemDescriptor memory);

  function setGearForTokenAndSlot(uint256 tokenId, Slot slot, ItemDescriptor calldata descriptor) external;

  function updateStatsForPlayer(uint256 tokenId) external;

  function setBaseStats(uint256 tokenId) external;

  function tokenIdToStats(
    uint256
  )
    external
    returns (uint16 attack, uint16 defense, uint16 specialAttack, uint16 specialDefense, uint16 speed, int16 hp);
}
"
    },
