// SPDX-License-Identifier: MIT

pragma solidity ^0.8.9;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";

interface IERC721 {
    function balanceOf(address user) external view returns(uint256);
    function totalSupply() external view returns(uint256);
    function currentTokenId() external view returns(uint256);
    function mint(address _to, uint256 _tokenId, string memory _hashs) external;
    function burn(uint256 tokenId) external;
    function transferFrom(address from, address to, uint _tokenId) external;
    function tokenOfOwnerByIndex(address user, uint256 tokenId) external view returns (uint);
    function tokenURI(uint256 tokenId) external view returns (string memory);
    function tokenHash(uint256 tokenId) external view returns (string memory);
    function ownerOf(uint256 tokenId) external view returns (address);
}

interface IBurnNFT{
    function depositReward(uint amount) external;
}

contract MergePart is Ownable {
    address public immutable items721;
    address public immutable ONB;
    IERC1155 public immutable box;
    IBurnNFT public burnNFT;

    mapping(uint => bool) public nonces;
    string[] public itemsFlag; // result => rank
    string[] public itemsCards; // result => card
    uint[] public insuranceFee = [20 ether, 40 ether, 60 ether, 120 ether, 300 ether];
    uint[] public percent2Win = [60000, 50000, 40000, 30000, 20000];
    uint[] public remainBox2merge = [1000, 1000, 1000, 1000];
    uint[] public amountBox2merge = [1, 5, 20, 50];
    mapping(address => mapping(uint => bool)) public isTakeBox; // user => rank => box

    struct Merge{
        uint nonce;
        uint rank;
        bool isInsurance;
        bool isLevelUp;
        bool isSuccess;
        uint boxBonus;
    }
    mapping(uint => Merge) public mergeInfos; // nonce => merge

    constructor(address _items721, address _ONB, IBurnNFT _burnNFT, IERC1155 _box) {
        items721 = _items721;
        ONB = _ONB;
        burnNFT = _burnNFT;
        box = _box;
    }

    function setItemsFlag(string[] memory itemHash) external onlyOwner {
        itemsFlag = itemHash;
    }
    function getItemsFlag() external view returns(string[] memory itemHash) {
        return itemsFlag;
    }
    function setItemsCards(string[] memory itemHash) external onlyOwner {
        itemsCards = itemHash;
    }
    function getItemsCards() external view returns(string[] memory itemHash) {
        return itemsCards;
    }

    function getBalanceRank(address user) external view returns(uint[] memory tokenIds, string[] memory tokenURIs) {
        uint balance = IERC721(items721).balanceOf(user);
        tokenIds = new uint[](balance);
        tokenURIs = new string[](balance);
        for(uint i = 0; i < balance; i++) {
            tokenIds[i] = IERC721(items721).tokenOfOwnerByIndex(user, i);
            tokenURIs[i] = IERC721(items721).tokenURI(tokenIds[i]);
        }
    }
    function random(uint nonce, uint percentDecimal) public view returns (uint) {
        return uint(keccak256(abi.encodePacked(block.difficulty, block.timestamp, nonce)))%percentDecimal;
    }

    function merge(uint nonce, uint rank, uint[] memory tokenIds, bool isInsurance) external {
        require(nonce > block.timestamp * 1000, "OpenBox::open: invalid nonce");
        require(!nonces[nonce], "OpenBox::open: nonce used");
        uint length = tokenIds.length;
        require(length % 5 == 0, "MergePart::merge: invalid tokenIds length");
        uint resultNumber = random(nonce, 100000);
        nonces[nonce] = true;

        uint rankIndex;
        uint nBurn = 5;
        uint tokenId = IERC721(items721).currentTokenId();
        mergeInfos[nonce].nonce = nonce;
        mergeInfos[nonce].rank = rank;

        if(isInsurance) {
            ERC20Burnable(ONB).transferFrom(_msgSender(), address(this), insuranceFee[rank]);
            uint onbBalance =  ERC20Burnable(ONB).balanceOf(address(this));
            uint remainAmount = onbBalance / 2;
            ERC20Burnable(ONB).burn(remainAmount);
            ERC20Burnable(ONB).approve(address(burnNFT), remainAmount);
            burnNFT.depositReward(remainAmount);
        }

        if(resultNumber <= 60000) {
            rankIndex = 1;
            mergeInfos[nonce].isSuccess = true;
            if(resultNumber < 1) {
                mergeInfos[nonce].isLevelUp = true;
                IERC721(items721).mint(_msgSender(), tokenId+1, itemsCards[rank+1]);
            }
            else IERC721(items721).mint(_msgSender(), tokenId+1, itemsCards[rank]);
            if(rank > 0 && !isTakeBox[_msgSender()][rank] && remainBox2merge[rank-1] >= amountBox2merge[rank-1]) {
                isTakeBox[_msgSender()][rank] = true;
                box.safeTransferFrom(owner(), _msgSender(), 1, amountBox2merge[rank-1], '0x');
                mergeInfos[nonce].boxBonus = amountBox2merge[rank-1];
                remainBox2merge[rank-1] -= amountBox2merge[rank-1];
            }
        }
        else {
            nBurn = 3;
            if(isInsurance) {
                nBurn = 1;
                mergeInfos[nonce].isInsurance = isInsurance;
            }
        }
        for(uint i = 0; i < nBurn; i++) {
            require(IERC721(items721).ownerOf(tokenIds[i]) == _msgSender(), "MergePart::merge: invalid tokenIds");
            string memory hash = IERC721(items721).tokenHash(tokenIds[i]);
            require(keccak256(bytes(hash)) == keccak256(bytes(itemsFlag[rank])), "MergePart::merge: invalid tokenIds rank");
            IERC721(items721).burn(tokenIds[i]);
        }
    }
    function setRemainBox2merge(uint[] memory amounts) external onlyOwner {

        remainBox2merge = amounts;
    }
    function inCaseTokensGetStuck(ERC20Burnable _token) external onlyOwner {

        uint amount = _token.balanceOf(address(this));
        _token.transfer(msg.sender, amount);
    }
}
