contract GachaMachineByGachaTicket is ReentrancyGuard, LuxOnLive, ERC1155Holder, LuxOnData, LuxOnSuperOperators, LuxOnBlacklist {
  event GachaByGachaTicket(address indexed owner, uint256 indexed gachaTicketTokenId, uint256 indexed tokenId, uint256 requestId, bool reveal);

  event SetMintGoodsInfo(address indexed mintGoodsAddress, uint256[] tokenIds);
  event SetMintAddress(address indexed mintAddress);
  event SetRandomSeedAddress(address indexed randomSeedAddress);
  event Withdraw(uint256 amount);

  event OperatorMintTokenId(uint256 indexed tokenId);

  struct UsedGoods {
    uint256 tokenId;
    uint256 amount;
  }

  struct MintGoodsInfo {
    address mintGoodsAddress;
    uint256[] tokenIds;
  }

  MintGoodsInfo private mintGoodsInfo;
  mapping(address => mapping(uint256 => bool)) private mintGoodsList;

  address private mintAddress;

  string public gachaData = "DspGachaData";

  address public randomSeedAddress;

  constructor(
    address _mintAddress,
    MintGoodsInfo memory _mintGoodsInfo,
    address dataAddress,
    address luxOnService,
    address _randomSeedAddress,
    string memory operator,
    address luxOnAdmin,
    address luxonAuthority
  ) LuxOnData(dataAddress) LuxOnLive(luxOnService) LuxOnSuperOperators(operator, luxOnAdmin) LuxOnBlacklist(luxonAuthority) {
    mintGoodsInfo = _mintGoodsInfo;
    for (uint256 i = 0; i < _mintGoodsInfo.tokenIds.length; i++) {
      mintGoodsList[_mintGoodsInfo.mintGoodsAddress][_mintGoodsInfo.tokenIds[i]] = true;
    }
    mintAddress = _mintAddress;
    randomSeedAddress = _randomSeedAddress;
  }

  //------------------ get ------------------//

  function getMintGoodsInfo() public view returns (MintGoodsInfo memory) {
    return mintGoodsInfo;
  }

  function getMintAddress() public view returns (address) {
    return mintAddress;
  }

  function getRandomSeedAddress() public view returns (address) {
    return randomSeedAddress;
  }
  //------------------ set ------------------//

  function setMintGoodsInfo(MintGoodsInfo memory _mintGoodsInfo) external onlyOwner {
    for (uint256 i = 0; i < mintGoodsInfo.tokenIds.length; i++) {
      mintGoodsList[mintGoodsInfo.mintGoodsAddress][mintGoodsInfo.tokenIds[i]] = false;
    }
    mintGoodsInfo = _mintGoodsInfo;
    for (uint256 i = 0; i < mintGoodsInfo.tokenIds.length; i++) {
      mintGoodsList[mintGoodsInfo.mintGoodsAddress][mintGoodsInfo.tokenIds[i]] = true;
    }

    emit SetMintGoodsInfo(_mintGoodsInfo.mintGoodsAddress, _mintGoodsInfo.tokenIds);
  }

  function setMintAddress(address _mintAddress) external onlyOwner {
    mintAddress = _mintAddress;
    emit SetMintAddress(_mintAddress);
  }

  function setRandomSeedAddress(address _address) public {
    randomSeedAddress = _address;
    emit SetRandomSeedAddress(_address);
  }
  //------------------ gacha ------------------//

  function gacha(UsedGoods memory _gachaInfo, bool reveal) external isLive isBlacklist(msg.sender) {
    mintPay(_gachaInfo);
    IERC721LUXON(mintAddress).mint(msg.sender, _gachaInfo.amount);
    uint256 lastTokenId = IERC721LUXON(mintAddress).nextTokenId() - 1;
    uint256[] memory tokenIds = new uint256[](_gachaInfo.amount);
    for (uint256 i = 0; i < _gachaInfo.amount; i++) {
      uint256 tokenId = lastTokenId - _gachaInfo.amount + i + 1;
      tokenIds[i] = tokenId;
      uint256 requestId = IRandomSeedNumber(randomSeedAddress).requestRandomWords(msg.sender, _gachaInfo.tokenId, tokenId);

      emit GachaByGachaTicket(msg.sender, _gachaInfo.tokenId, tokenId, requestId, reveal);
    }
  }

  function gachaOperator(address userAddress, UsedGoods memory _gachaInfo, bool reveal) external isLive onlySuperOperator {
    require(mintGoodsList[mintGoodsInfo.mintGoodsAddress][_gachaInfo.tokenId], "not valid token id");
    IERC721LUXON(mintAddress).mint(userAddress, _gachaInfo.amount);
    uint256 lastTokenId = IERC721LUXON(mintAddress).nextTokenId() - 1;
    uint256[] memory tokenIds = new uint256[](_gachaInfo.amount);
    for (uint256 i = 0; i < _gachaInfo.amount; i++) {
      uint256 tokenId = lastTokenId - _gachaInfo.amount + i + 1;
      tokenIds[i] = tokenId;
      uint256 requestId = IRandomSeedNumber(randomSeedAddress).requestRandomWords(userAddress, _gachaInfo.tokenId, tokenId);

      emit GachaByGachaTicket(userAddress, _gachaInfo.tokenId, tokenId, requestId, reveal);
      emit OperatorMintTokenId(tokenId);
    }
  }

  function withdraw() external onlyOwner nonReentrant {
    (bool success,) = msg.sender.call{value : address(this).balance}("");
    require(success, "Transfer failed.");
    emit Withdraw(address(this).balance);
  }

  //------------------ private ------------------//

  function mintPay(UsedGoods memory _gachaInfo) private {
    require(mintGoodsList[mintGoodsInfo.mintGoodsAddress][_gachaInfo.tokenId], "not valid token id");

    address gachaDataAddress = getDataAddress(gachaData);
    uint256 gachaType = DspGachaData(gachaDataAddress).getGachaType(_gachaInfo.tokenId);
    if (uint256(GachaType.Character) == gachaType) {
      GachaInfo memory gachaInfo = DspGachaData(gachaDataAddress).getGachaInfo(_gachaInfo.tokenId);
      require(gachaInfo.isValid, "not valid token id");
    } else if (uint256(GachaType.FateCore) == gachaType) {
      FateCoreGachaInfo memory fateCoreGachaInfo = DspGachaData(gachaDataAddress).getFateCoreGachaInfo(_gachaInfo.tokenId);
      require(fateCoreGachaInfo.isValid, "not valid token id");
    }
    IGachaTicket(mintGoodsInfo.mintGoodsAddress).burn(msg.sender, _gachaInfo.tokenId, _gachaInfo.amount);
  }
}
