// File: CreditDAOTokenV2.sol


pragma solidity ^0.8.0;





contract CreditDAOTokenV2 is Ownable,IERC20 {
    using SafeMath for uint256;
    using Math for uint256;

    mapping (address => uint256) private _balances;
    mapping (address => mapping (address => uint256)) private _allowances;
    uint256 private _totalSupply;
    string private _name = "Credit DAO Token";
    string private _symbol = "CD";
    uint8 private _decimals = 18;

    bool public once;
    mapping (address => bool) public operators;
    mapping (address => bool) public isFromDelivers;
    mapping (address => bool) public isToDelivers;
    address public dividend;
    address public management;
    address public foundation;
    address public dead = 0x000000000000000000000000000000000000dEaD;
    uint256 public DURATION;
    uint256 public startTime;
    mapping (uint256 => uint256) public _records;

    uint256 public maxTotalSupply = 1e8 * 1e18;

    constructor (uint256 _startTime, uint256 duration) {
        // _mint(msg.sender, 100 * 1e4 * 1e18);
        DURATION = duration;
        startTime = _startTime;
    }

    function grantOperatorRole(address[2] memory accounts) public onlyOwner {
        require(!once, 'Only once!');
        operators[accounts[0]] = true;
        operators[accounts[1]] = true;
        once = true;
    }

    function setFromDelivers(address[] memory addrs, bool flag) public onlyOwner {
        for(uint i;i<addrs.length;i++){
            isFromDelivers[addrs[i]] = flag;
        }
    }

    function setToDelivers(address[] memory addrs, bool flag) public onlyOwner {
        for(uint i;i<addrs.length;i++){
            isToDelivers[addrs[i]] = flag;
        }
    }

    function changePool(address _dividend, address _management, address _foundation, address[] memory pools) public onlyOwner {
        dividend = _dividend;
        management = _management;
        foundation = _foundation;
        for(uint i;i<pools.length;i++){
            isFromDelivers[pools[i]] = true;
            isToDelivers[pools[i]] = true;
        }
    }

    function mint(address to, uint256 amount) public {
        require(operators[msg.sender], 'No permission!');
        require(_totalSupply.add(amount)<=maxTotalSupply, 'Can\'t mint more!');
        _mint(to, amount);
    }

    function getCurrentIndex() public view returns(uint256){
        if(DURATION==0) return 0;
        (,uint256 time) = block.timestamp.trySub(startTime);
        return time.div(DURATION);
    }

    function getFeeListLatest(uint256 length) public view returns(uint256[] memory indexes, uint256[] memory fees){
        return getFeeList(getCurrentIndex(), length);
    }
    
    function getFeeList(uint256 startReverse, uint256 length) public view returns(uint256[] memory indexes, uint256[] memory fees){
        uint256 currentIndex = getCurrentIndex();
        startReverse = currentIndex.min(startReverse);
        length = startReverse.add(1).min(length);
        indexes = new uint256[](length);
        fees = new uint256[](length);
        uint256 index;
        for(uint i;i<length;i++){
            index = startReverse.sub(i);
            indexes[i] = index;
            fees[i] = _records[index];
        }
    }

    function name() public view virtual returns (string memory) {
        return _name;
    }

    function symbol() public view virtual returns (string memory) {
        return _symbol;
    }

    function decimals() public view virtual returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view virtual override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view virtual override returns (uint256) {
        return _balances[account];
    }

    function transfer(address recipient, uint256 amount) public virtual override returns (bool) {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    function allowance(address owner, address spender) public view virtual override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public virtual override returns (bool) {
        _approve(msg.sender, spender, amount);
        return true;
    }
    
    function transferFrom(address sender, address recipient, uint256 amount) public virtual override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, msg.sender, _allowances[sender][msg.sender].sub(amount, "ERC20: transfer amount exceeds allowance"));
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        _approve(msg.sender, spender, _allowances[msg.sender][spender].add(addedValue));
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        _approve(msg.sender, spender, _allowances[msg.sender][spender].sub(subtractedValue, "ERC20: decreased allowance below zero"));
        return true;
    }

    function _transfer(address sender, address recipient, uint256 amount) internal virtual {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");

        if(isFromDelivers[sender] || isToDelivers[recipient]){
            _basicTransfer(sender, recipient, amount);
        }else{
            _basicTransfer(sender, recipient, amount.sub(_feeTransfer(sender, amount)));
        }
    }

    function _feeTransfer(address sender, uint256 amount) internal virtual returns(uint256 fee) {
        fee = amount.mul(30).div(130);

        uint256 fee1 = fee.mul(50).div(100);
        _basicTransfer(sender, dividend, fee1);

        uint256 fee2 = fee.mul(20).div(100);
        _basicTransfer(sender, management, fee2);

        uint256 fee3 = fee.mul(20).div(100);
        _basicTransfer(sender, foundation, fee3);

        _basicTransfer(sender, dead, fee.sub(fee1).sub(fee2).sub(fee3));

        uint256 index = getCurrentIndex();
        _records[index] += fee;
    }

    function _basicTransfer(address sender, address recipient, uint256 amount) internal virtual {
        _beforeTokenTransfer(sender, recipient, amount);

        _balances[sender] = _balances[sender].sub(amount, "ERC20: transfer amount exceeds balance");
        _balances[recipient] = _balances[recipient].add(amount);
        emit Transfer(sender, recipient, amount);
    }

    function _mint(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: mint to the zero address");

        _beforeTokenTransfer(address(0), account, amount);

        _totalSupply = _totalSupply.add(amount);
        _balances[account] = _balances[account].add(amount);
        emit Transfer(address(0), account, amount);
    }

    function _burn(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: burn from the zero address");

        _beforeTokenTransfer(account, address(0), amount);

        _balances[account] = _balances[account].sub(amount, "ERC20: burn amount exceeds balance");
        _totalSupply = _totalSupply.sub(amount);
        emit Transfer(account, address(0), amount);
    }

    function _approve(address owner, address spender, uint256 amount) internal virtual {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _setupDecimals(uint8 decimals_) internal virtual {
        _decimals = decimals_;
    }
    
    function _beforeTokenTransfer(address from, address to, uint256 amount) internal virtual { }
}
