pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract Voting {
    struct Proposal {
        string description;
        uint voteCount;
    }

    IERC20 public weth;
    Proposal[] public proposals;

    mapping(address => uint) public stakes;
    mapping(address => bool) public hasVoted;

    constructor(IERC20 _weth) {
        weth = _weth;
    }

    function createProposal(string memory _description) public {
        proposals.push(Proposal({
            description: _description,
            voteCount: 0
        }));
    }

    function stake(uint _amount) public {
        weth.transferFrom(msg.sender, address(this), _amount);
        stakes[msg.sender] += _amount;
    }

    function vote(uint _proposal) public {
        require(!hasVoted[msg.sender], "Already voted degen.");
        require(stakes[msg.sender] > 0, "No stake to vote degen.");

        proposals[_proposal].voteCount += stakes[msg.sender];
        hasVoted[msg.sender] = true;
    }

    function withdraw() public {
        require(stakes[msg.sender] > 0, "No stakes LMO to withdraw.");
        require(!hasVoted[msg.sender], "Already voted you. are degen, cannot withdraw.");

        uint stake = stakes[msg.sender];
        stakes[msg.sender] = 0;
        weth.transfer(msg.sender, stake);
    }
}"
