    "contracts/GameItems1155.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

import {AccessControlUpgradeable} from '@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol';
import {Initializable} from '@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol';
import {UUPSUpgradeable} from '@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol';
import {ERC1155Upgradeable} from '@openzeppelin/contracts-upgradeable/token/ERC1155/ERC1155Upgradeable.sol';
import {ERC1155BurnableUpgradeable} from '@openzeppelin/contracts-upgradeable/token/ERC1155/extensions/ERC1155BurnableUpgradeable.sol';
import {ERC1155SupplyUpgradeable} from '@openzeppelin/contracts-upgradeable/token/ERC1155/extensions/ERC1155SupplyUpgradeable.sol';
import {ERC1155URIStorageUpgradeable} from '@openzeppelin/contracts-upgradeable/token/ERC1155/extensions/ERC1155URIStorageUpgradeable.sol';
import {PausableUpgradeable} from '@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol';
import {ContextMixin} from './utils/ContextMixin.sol';

interface IRandom {
	function getRandomNumber() external returns (uint);
}

struct GameItem {
	uint256 ID;
	uint256 rarity; // lower is more rare
	bool isDiscontinued;
	uint256 mintLimit;
}

contract GameItems is
	Initializable,
	ERC1155Upgradeable,
	AccessControlUpgradeable,
	PausableUpgradeable,
	ERC1155BurnableUpgradeable,
	ERC1155SupplyUpgradeable,
	ERC1155URIStorageUpgradeable,
	UUPSUpgradeable,
	ContextMixin
{
	bytes32 public constant COST_TO_MINT_SETTER_ROLE = keccak256('COST_TO_MINT_SETTER_ROLE');
	bytes32 public constant GAME_ITEM_MANAGER_ROLE = keccak256('GAME_ITEM_MANAGER_ROLE');
	bytes32 public constant PAUSER_ROLE = keccak256('PAUSER_ROLE');
	bytes32 public constant UPGRADER_ROLE = keccak256('UPGRADER_ROLE');
	bytes32 public constant URI_SETTER_ROLE = keccak256('URI_SETTER_ROLE');
	bytes32 public constant WITHDRAWER_ROLE = keccak256('WITHDRAWER_ROLE');

	address rng;
	string public name;
	string public contractURI; // contract-level metadata (https://docs.opensea.io/docs/contract-level-metadata)
	uint public costToMint;
	mapping(uint => GameItem) gameItems; // ID => GameItem
	uint[] gameItemIDs; // list of all game item IDs

	error ErrCostToMintNotSet();
	error ErrIncorrectAmountPaid(uint sent, uint required);
	error ErrNoTokenToMint();

	event GameItemUpdated(uint indexed id, uint rarity, uint mintLimit, bool isDiscontinued);
	event Mint(uint indexed gameItemID, address indexed to, string data);

	modifier needsRNG() {
		require(rng != address(0), 'rng not set');
		_;
	}

	/// @custom:oz-upgrades-unsafe-allow constructor
	constructor() {
		_disableInitializers();
	}

	function initialize() public initializer {
		__ERC1155_init('');
		__AccessControl_init();
		__Pausable_init();
		__ERC1155Burnable_init();
		__ERC1155Supply_init();
		__UUPSUpgradeable_init();

		_grantRole(DEFAULT_ADMIN_ROLE, msg.sender);

		name = 'Anthology';
	}

	function pause() public onlyRole(PAUSER_ROLE) {
		_pause();
	}

	function unpause() public onlyRole(PAUSER_ROLE) {
		_unpause();
	}

	function _authorizeUpgrade(
		address newImplementation
	) internal override onlyRole(UPGRADER_ROLE) {}

	// The following functions are overrides required by Solidity.
	function supportsInterface(
		bytes4 interfaceId
	) public view override(ERC1155Upgradeable, AccessControlUpgradeable) returns (bool) {
		return super.supportsInterface(interfaceId);
	}

	function _update(
		address from,
		address to,
		uint256[] memory ids,
		uint256[] memory values
	) internal override(ERC1155Upgradeable, ERC1155SupplyUpgradeable) {
		ERC1155SupplyUpgradeable._update(from, to, ids, values);
	}

	function setContractURI(string memory _newContractURI) public onlyRole(URI_SETTER_ROLE) {
		contractURI = _newContractURI;
	}

	function setCostToMint(uint newCost) public onlyRole(COST_TO_MINT_SETTER_ROLE) {
		require(newCost > 0, 'Cost to mint must be greater than 0');
		costToMint = newCost;
	}

	function setName(string memory _name) public onlyRole(DEFAULT_ADMIN_ROLE) {
		name = _name;
	}

	function setRNG(address _rng) public onlyRole(DEFAULT_ADMIN_ROLE) {
		rng = _rng;
	}

	function setTokenURI(uint256 tokenId, string memory tokenURI) public onlyRole(URI_SETTER_ROLE) {
		_setURI(tokenId, tokenURI);
	}

	function uri(
		uint256 tokenId
	)
		public
		view
		virtual
		override(ERC1155Upgradeable, ERC1155URIStorageUpgradeable)
		returns (string memory)
	{
		return ERC1155URIStorageUpgradeable.uri(tokenId);
	}

	function upsertGameItem(
		uint _id,
		uint _rarity,
		uint _mintLimit,
		bool _isDiscontinued
	) public onlyRole(GAME_ITEM_MANAGER_ROLE) {
		require(_id > 0, '_id must be greater than 0');
		require(_rarity > 0, '_rarity must be greater than 0');

		// check for an existing game item
		GameItem memory gameItem = GameItem(_id, _rarity, _isDiscontinued, _mintLimit);

		// set the game item
		gameItems[_id] = gameItem;

		// add the game item to the list if it doesn't exist
		if (!_gameItemInListOfIDs(_id)) {
			gameItemIDs.push(_id);
		}

		emit GameItemUpdated(
			gameItem.ID,
			gameItem.rarity,
			gameItem.mintLimit,
			gameItem.isDiscontinued
		);
	}

	function mint(
		address account,
		uint256 amount,
		string memory data
	) public payable whenNotPaused needsRNG {
		if (costToMint == 0) {
			revert ErrCostToMintNotSet();
		}
		require(amount > 0, 'Must mint at least one token');
		uint requiredCost = costToMint * amount;
		if (requiredCost != msg.value) {
			revert ErrIncorrectAmountPaid({sent: msg.value, required: requiredCost});
		}

		// mint all items the sender paid for
		uint numberMinted = 0;
		while (numberMinted < amount) {
			// get a random game item
			GameItem memory gameItem = _getRandomGameItem();

			// mint the game item
			_mint(account, gameItem.ID, 1, '0x');
			emit Mint(gameItem.ID, account, data);

			// increment the number minted
			numberMinted++;
		}
	}

	function _getRandomGameItem() internal needsRNG returns (GameItem memory gameItem) {
		uint totalRarity = 0;
		uint[] memory eligibleGameItemIDs = new uint[](gameItemIDs.length);

		for (uint i = 0; i < gameItemIDs.length; i++) {
			// skip discontinued items
			if (gameItems[gameItemIDs[i]].isDiscontinued) {
				continue;
			}
			// skip items that have reached their mint limit
			if (
				gameItems[gameItemIDs[i]].mintLimit > 0 &&
				super.totalSupply(gameItemIDs[i]) >= gameItems[gameItemIDs[i]].mintLimit
			) {
				continue;
			}
			// all remaining items are eligible for minting
			eligibleGameItemIDs[i] = gameItemIDs[i];
			totalRarity += gameItems[eligibleGameItemIDs[i]].rarity;
		}

		if (eligibleGameItemIDs.length == 0) {
			revert ErrNoTokenToMint();
		}

		uint randomNumber = IRandom(rng).getRandomNumber() % totalRarity;
		uint cumulativeRarity = 0;

		for (uint i = 0; i < eligibleGameItemIDs.length; i++) {
			cumulativeRarity += gameItems[eligibleGameItemIDs[i]].rarity;
			if (randomNumber <= cumulativeRarity) {
				return gameItems[eligibleGameItemIDs[i]];
			}
		}
	}

	function withdraw() public onlyRole(WITHDRAWER_ROLE) {
		payable(msg.sender).transfer(address(this).balance);
	}

	/**
	 * Override isApprovedForAll to auto-approve OS's proxy contract.
	 * https://docs.opensea.io/docs/polygon-basic-integration
	 */
	function isApprovedForAll(
		address _owner,
		address _operator
	) public view override returns (bool isOperator) {
		// if OpenSea's ERC1155 Proxy Address is detected, auto-return true
		if (_operator == address(0x207Fa8Df3a17D96Ca7EA4f2893fcdCb78a304101)) {
			return true;
		}
		// otherwise, use the default ERC1155.isApprovedForAll()
		return ERC1155Upgradeable.isApprovedForAll(_owner, _operator);
	}

	/**
	 * This is used instead of msg.sender as transactions won't be sent by the original token owner, but by OpenSea.
	 */
	function _msgSender() internal view override returns (address sender) {
		return ContextMixin.msgSender();
	}

	function _gameItemInListOfIDs(uint _id) internal view returns (bool) {
		for (uint i = 0; i < gameItemIDs.length; i++) {
			if (gameItemIDs[i] == _id) {
				return true;
			}
		}
		return false;
	}
}
"
    },
    "contracts/utils/ContextMixin.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

/**
 * https://github.com/maticnetwork/pos-portal/blob/master/contracts/common/ContextMixin.sol
 */
abstract contract ContextMixin {
	function msgSender() internal view returns (address payable sender) {
		if (msg.sender == address(this)) {
			bytes memory array = msg.data;
			uint256 index = msg.data.length;
			assembly {
				// Load the 32 bytes word from memory with the address on the lower 20 bytes, and mask those.
				sender := and(mload(add(array, index)), 0xffffffffffffffffffffffffffffffffffffffff)
			}
		} else {
			sender = payable(msg.sender);
		}
		return sender;
	}
}
"
    }
  },
  "settings": {
    "optimizer": {
      "enabled": true,
      "runs": 200
    },
    "evmVersion": "paris",
    "outputSelection": {
      "*": {
        "*": [
          "evm.bytecode",
          "evm.deployedBytecode",
          "devdoc",
          "userdoc",
          "metadata",
          "abi"
        ]
      }
    },
    "libraries": {}
  }
