    "src/CiphersNft.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.18;

import {IERC721} from "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import {
  ERC721,
  ERC721URIStorage
} from "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title CiphersNft contract
 * @dev This contract implements the ERC721 standard with URI support, providing a flexible
 *      NFT (non-fungible token) framework for minting, burning, and managing NFTs.
 */
contract CiphersNft is ERC721URIStorage, Ownable {
  error CiphersNft__CallerNotTheMinter();
  error CiphersNft__CallerNotTheBurner();
  error CiphersNft__MaxTotalSupplyReached();
  error CiphersNft__InvalidTokenURI();
  error CiphersNft__MintIsPaused();
  error CiphersNft__MintDeadlineReached();
  error CiphersNft__MintingNotStarted();
  error CiphersNft__ArraysAreNotEqual();
  error CiphersNft__MaxBatchMintingSizeExceeded(uint256 maxBatchMintingSize);

  /// Enum representing the current minting state of the contract.
  enum MintingState {
    READY,
    PAUSED
  }

  /// The maximum batch minting size allowed for a single batch minting operation.
  uint256 internal constant MAX_BATCH_MINTING_SIZE = 50;

  /// Timestamp in UNIX when minting starts.
  uint256 internal immutable i_mintStart;
  /// The deadline until which tokens can be minted.
  uint256 internal immutable i_mintDeadline;
  /// The maximum total supply of tokens that can be minted.
  uint256 internal immutable i_maxTotalSupply;

  /// The current minting state of the contract.
  MintingState internal s_mintingState;

  /// The URI for the contract metadata used in the storefront of OpenSea.
  string internal s_contractURI;

  /// The address with the authority to burn existing tokens.
  address internal s_burnerRole;

  /// The address with the authority to mint new tokens.
  address internal s_minterRole;

  /// The current count of tokens minted
  uint256 internal s_mintCounter;

  /// The default royalty info
  uint256 internal s_royalty;

  /// Mapping to track the number of times each token has been minted.
  mapping(uint256 tokenID => uint256 mintCount) internal s_mintCountTokenID;

  /// Mapping to store the timestamp when each token was minted.
  mapping(uint256 tokenID => uint256 mintTimestamp) internal s_mintTimestamp;

  /// Event emitted when a new minter address is set
  event NewMinterAddress(address indexed minterAddress);
  /// Event emitted when a new burner address is set.
  event NewBurnerAddress(address indexed burnerAddress);
  /// Event emitted when a token is minted.
  event Mint(
    uint256 indexed tokenID, uint256 indexed mintCount, uint256 timestamp, string tokenURI
  );
  /// Event emitted when the royalty is set.
  event RoyaltySet(uint256 indexed royalty);
  /// Event emitted when the contract is deployed.
  event Deployed();

  /// Modifier to make sure only the 's_minter' address can call a function.
  modifier onlyMinter() {
    if (msg.sender != s_minterRole) revert CiphersNft__CallerNotTheMinter();
    _;
  }

  /**
   * @dev Contract constructor for initializing an ERC721 token with ownership management.
   *
   * Initializes the ERC721 token with the provided contract name and symbol, and sets the maximum
   * total supply and initial minter address. The contract owner is set to the deployer of the contract.
   *
   * @param contractName The name of the ERC721 contract.
   * @param contractSymbol The symbol (ticker) of the ERC721 contract.
   * @param contractUri The URI for the contract metadata used in the storefront of opensea.
   * @param maxTotalSupply The maximum total supply of tokens that can be minted.
   * @param mintDeadline The deadline until which tokens can be minted.
   * @param mintStart The timestamp in UNIX when minting starts.
   * @param owner The initial address that will be set as the owner of this contract.
   * @param minter The initial address with the authority to mint new tokens.
   * @param burner The initial address with the authority to burn existing tokens.
   */
  constructor(
    string memory contractName,
    string memory contractSymbol,
    string memory contractUri,
    uint256 maxTotalSupply,
    uint256 mintDeadline,
    uint256 mintStart,
    address owner,
    address minter,
    address burner
  ) ERC721(contractName, contractSymbol) Ownable(owner) {
    i_maxTotalSupply = maxTotalSupply;
    i_mintDeadline = mintDeadline;
    i_mintStart = mintStart;

    s_contractURI = contractUri;
    s_minterRole = minter;
    s_burnerRole = burner;
    s_mintingState = MintingState.READY;
    s_royalty = 0;

    emit Deployed();
  }

  /// Wrapper function for `mint` function to save gas when batch minting.
  /// See `_mint` for more details.
  function mint(string memory tokenUri, uint256 tokenID, address owner) external onlyMinter {
    _onlyWhenMintingIsAllowed();
    _mint(tokenUri, tokenID, owner);
  }

  /**
   * @dev Mints multiple new tokens in a batch using the specified token URIs, token IDs, and owner addresses.
   * Only accessible to authorized minter. See `mint` function for more details.
   *
   * Extra Requirements:
   * - Token URIs, token IDs, and owners arrays must have the same length.
   * - The length of arrays must not exceed the maximum batch minting size.
   *
   * @param tokenURIs The array of URIs on IPFS associated with the minted tokens.
   * @param tokenIDs The array of identifiers for the minted tokens.
   * @param owners The array of addresses that will be assigned ownership of the minted tokens.
   */
  function batchMint(string[] memory tokenURIs, uint256[] memory tokenIDs, address[] memory owners)
    external
    onlyMinter
  {
    _onlyWhenMintingIsAllowed();
    if (
      tokenURIs.length != tokenIDs.length || tokenURIs.length != owners.length
        || tokenIDs.length != owners.length
    ) {
      revert CiphersNft__ArraysAreNotEqual();
    }
    if (tokenURIs.length > MAX_BATCH_MINTING_SIZE) {
      revert CiphersNft__MaxBatchMintingSizeExceeded(MAX_BATCH_MINTING_SIZE);
    }

    for (uint256 i = 0; i < tokenURIs.length; i++) {
      _mint(tokenURIs[i], tokenIDs[i], owners[i]);
    }
  }

  /**
   * @dev Mints a new token with the specified token URI, token ID, and owner address.
   * Only accessible to the designated minter.
   * Emits a `Mint` event indicating the successful minting of a token.
   *
   * Requirements:
   * - TokenID is not already minted.
   * - The provided token URI must not be empty.
   * - The minter must be authorized to call this function (see `onlyMinter` modifier).
   * - The minting state must be `READY` (see `onlyWhenMintingIsAllowed` function).
   *
   * @param tokenUri The URI on IPFS associated with the minted token.
   * @param tokenID The identifier for the minted token.
   * @param owner The address that will be assigned ownership of the minted token.
   */
  function _mint(string memory tokenUri, uint256 tokenID, address owner) internal {
    if (bytes(tokenUri).length == 0) revert CiphersNft__InvalidTokenURI();
    uint256 mintCount = _incrementMintCounter();
    _safeMint(owner, tokenID);
    _setTokenURI(tokenID, tokenUri);
    s_mintCountTokenID[tokenID] = mintCount;
    s_mintTimestamp[tokenID] = block.timestamp;

    emit Mint(tokenID, mintCount, block.timestamp, tokenUri);
  }

  /// Approves the burner to burn the token with ID `tokenID`.
  function approveBurn(uint256 tokenID) external {
    approve(s_burnerRole, tokenID);
  }

  /**
   * @dev Burns a specific NFT token. Most likely used by a bridge in the future.
   *
   * Requirements:
   * - The caller must have the 'burner' role.
   * - Owner of the token must have approved the burner.
   *
   * @param tokenID The ID of the NFT token to be burned.
   */
  function burn(uint256 tokenID) external {
    if (msg.sender != s_burnerRole) {
      revert CiphersNft__CallerNotTheBurner();
    }
    if (!_isApprovedOrOwner(msg.sender, tokenID)) {
      revert ERC721InsufficientApproval(_msgSender(), tokenID);
    }
    _burn(tokenID);
  }

  /// Gets and increments the mintCounter by `1`.
  function _incrementMintCounter() internal returns (uint256 mintCount) {
    s_mintCounter++;
    mintCount = s_mintCounter;
    if (mintCount > i_maxTotalSupply) revert CiphersNft__MaxTotalSupplyReached();
  }

  /**
   * @dev Checks whether minting is allowed based on the provided deadline and the contract's minting state.
   *
   * Requirements:
   * - The minting state must be `READY`.
   * - The current block timestamp must be before the provided deadline.
   */
  function _onlyWhenMintingIsAllowed() internal view {
    if (s_mintingState != MintingState.READY) revert CiphersNft__MintIsPaused();
    if (block.timestamp > i_mintDeadline) revert CiphersNft__MintDeadlineReached();
    if (block.timestamp < i_mintStart) revert CiphersNft__MintingNotStarted();
  }

  //////////////////////////
  /// Overrides functions //
  //////////////////////////

  /// Creates a standard method to publish and detect what interfaces a smart contract implements.
  function supportsInterface(bytes4 interfaceId)
    public
    view
    override(ERC721URIStorage)
    returns (bool)
  {
    return super.supportsInterface(interfaceId);
  }

  function _burn(uint256 tokenId) internal override(ERC721URIStorage) {
    super._burn(tokenId);
  }

  function tokenURI(uint256 tokenId) public view override(ERC721URIStorage) returns (string memory) {
    return super.tokenURI(tokenId);
  }

  //////////////////////////
  ///     Setters        ///
  //////////////////////////

  /// Sets the minter address to 'newMinter'.
  function setMinter(address newMinter) external onlyOwner {
    s_minterRole = newMinter;
    emit NewMinterAddress(newMinter);
  }

  /// Sets the burner address to 'newBurner'.
  function setBurner(address newBurner) external onlyOwner {
    s_burnerRole = newBurner;
    emit NewBurnerAddress(newBurner);
  }

  /// Sets the minting state to `paused`. Used by onlyMinter when something goes wrong.
  function pauseMinting() external onlyMinter {
    s_mintingState = MintingState.PAUSED;
  }

  /// Sets the minting state to `mintingState`.
  function setMintingState(MintingState mintingState) external onlyOwner {
    s_mintingState = mintingState;
  }

  /// Sets the royalty to `newRoyalty`.
  function setRoyalty(uint256 newRoyalty) external onlyOwner {
    s_royalty = newRoyalty;
    emit RoyaltySet(newRoyalty);
  }

  //////////////////////////
  ///     Getters        ///
  //////////////////////////

  /// Returns the contractURI for this contract.
  function contractURI() public view returns (string memory) {
    return s_contractURI;
  }

  /// Getter for the mintCount of token with ID `tokenID`.
  function getMintCount(uint256 tokenID) external view returns (uint256) {
    return s_mintCountTokenID[tokenID];
  }

  /// Getter for the mintCounter
  function getMintCounter() external view returns (uint256) {
    return s_mintCounter;
  }

  /// Getter for `s_minter`.
  function getMinter() external view returns (address) {
    return s_minterRole;
  }

  /// Getter for `s_mintingState`.
  function getMintingState() external view returns (MintingState) {
    return s_mintingState;
  }

  /// Getter for mint timestamp in UNIX
  function getMintTimestamp(uint256 tokenID) external view returns (uint256) {
    return s_mintTimestamp[tokenID];
  }

  /// Getter for `s_royalty`.
  function getRoyalty() external view returns (uint256) {
    return s_royalty;
  }

  /// Getter for `i_mintDeadline`.
  function getMintDeadline() external view returns (uint256) {
    return i_mintDeadline;
  }

  /// Getter for `i_mintStart`.
  function getMintStart() external view returns (uint256) {
    return i_mintStart;
  }

  /// Getter for `i_maxTotalSupply`.
  function getMaxTotalSupply() external view returns (uint256) {
    return i_maxTotalSupply;
  }
}
"
