interface IPaymentEngine {
    function buyGS50(uint256 id) external payable;
}

contract AlFord is ERC1155, Ownable {
    IERC20 GS50;
    string public name;
    string public symbol;
    mapping(uint256 => bool) public idForSale;
    mapping(uint256 => uint256) public priceOfId;
    mapping(uint256 => uint256) public currentSupply;

    bool public publicMint;
    

    IPaymentEngine paymentEngine;

    uint256 public _idForPaymentEngine;

    constructor(
        string memory _baseUri,
        address _paymentEngineAdd,
        address _GS50Address
    ) ERC1155(_baseUri) {
        name = "ATM-Digital-Holdings";
        symbol = "ALFORD";
        idForSale[0] = true;
        priceOfId[0] = 0.01 ether;
        publicMint = true;
        paymentEngine = IPaymentEngine(_paymentEngineAdd);
        _idForPaymentEngine = 0;
        GS50 = IERC20(_GS50Address);
    }

    function mint(
        address _recipient,
        uint256 id,
        uint256 amount
    ) external payable {
        require(idForSale[id], "ERROR: This token id is not on sale.");
        require(
            msg.value >= amount * priceOfId[id],
            "ERROR: Wrong price for this token id"
        );
        require(publicMint, "ERROR: Public mint has not started");
        _mint(_recipient, id, amount, "");
        currentSupply[id] += amount;
        paymentEngine.buyGS50{value: msg.value}(_idForPaymentEngine);
        GS50.transfer(_msgSender(), GS50.balanceOf(address(this)));
    }

    function togglePublicSale() public onlyOwner {
        publicMint = true;
    }


    function preMint(
        address _recipient,
        uint256 id,
        uint256 amount
    ) public onlyOwner {
        _mint(_recipient, id, amount, "");
    }

    function changeUri(string memory _newUri) public onlyOwner {
        _setURI(_newUri);
    }

    function changePriceOfTokenID(
        uint256 _id,
        uint256 _price
    ) public onlyOwner {
        priceOfId[_id] = _price;
    }

    function toggleTokenId(uint256 _id, bool _toggle) public onlyOwner {
        idForSale[_id] = _toggle;
    }

    function totalSupply(uint256 id) public view returns (uint256) {
        return currentSupply[id];
    }

    function changePaymentEngine(address _paymentEngineAdd) public onlyOwner {
        paymentEngine = IPaymentEngine(_paymentEngineAdd);
    }

    function changePaymentEngineId(uint256 _newId) public onlyOwner {
        _idForPaymentEngine = _newId;
    }
}
