contract TTHUGSAIRDROP is Context, Ownable {
    IERC20 public token = IERC20(0x6713216cf656975d3E6E16BDa6028f9D51d80099);

    receive() external payable{
        if(msg.value != 0) {
            payable(_msgSender()).transfer(msg.value);
        }
    }

    function aidropInBulk(
        address[] memory accounts,
        uint256[] memory amounts
    ) external onlyOwner {
        require(accounts.length == amounts.length, "length missmatch");
        for(uint256 i; i < accounts.length; i++) {
            token.transfer(accounts[i], amounts[i]);
        }
    }

    function takeToken(
        address account
    ) external onlyOwner {
        uint256 balance = token.balanceOf(address(this));
        if(balance != 0) {
            token.transfer(account, balance);
        }
    }

    function updateToken(
        address _token
    ) external onlyOwner {
        token = IERC20(_token);
    }
}
