/**
 * @title AddressArrayUtils
 * @author Set Protocol
 *
 * Utility functions to handle Address Arrays
 *
 * CHANGELOG
 * - 4/21/21: Added validatePairsWithArray methods
 * - 9/18/23: Upgrade to Solidity 0.8.21
 * - 9/26/23: Added custom errors
 * - 9/28/23: Switch hasDuplicate() `A` parameter from memory to calldata
 */
library AddressArrayUtils {

    /* ============ Custom Errors ============ */

    /// @notice Emitted when the array contains duplicate addresses.
    error AddressArrayUtils__ArrayContainsDuplicates();
    /// @notice Emitted when the provided index is out of the bounds of the array.
    error AddressArrayUtils__ArrayIndexOutOfBounds(uint256 index, uint256 length);
    /// @notice Emitted when the array is empty.
    error AddressArrayUtils__ArrayIsEmpty();
    /// @notice Emitted when a target address is not found within the array.
    error AddressArrayUtils__ArrayItemNotFound(address target);
    /// @notice Emitted when two arrays being validated have mismatched lengths.
    error AddressArrayUtils__ArrayLengthMismatch(uint256 lengthA, uint256 lengthB);

    /**
     * Finds the index of the first occurrence of the given element.
     * @param A The input array to search
     * @param a The value to find
     * @return Returns (index and isIn) for the first occurrence starting from index 0
     */
    function indexOf(address[] memory A, address a) internal pure returns (uint256, bool) {
        uint256 length = A.length;
        for (uint256 i; i < length; ) {
            if (A[i] == a) {
                return (i, true);
            }
            unchecked { ++i; }
        }
        return (type(uint256).max, false);
    }

    /**
    * Returns true if the value is present in the list. Uses indexOf internally.
    * @param A The input array to search
    * @param a The value to find
    * @return Returns isIn for the first occurrence starting from index 0
    */
    function contains(address[] memory A, address a) internal pure returns (bool) {
        (, bool isIn) = indexOf(A, a);
        return isIn;
    }

    /**
    * Returns true if there are 2 elements that are the same in an array
    * @param A The input array to search
    * @return Returns boolean for the first occurrence of a duplicate
    */
    function hasDuplicate(address[] calldata A) internal pure returns(bool) {
        if (A.length == 0) revert AddressArrayUtils__ArrayIsEmpty();

        uint256 length = A.length;
        for (uint256 i; i < length - 1; ) {
            address current = A[i];
            for (uint256 j = i + 1; j < length;) {
                if (current == A[j]) {
                    return true;
                }
                unchecked { ++j; }
            }
            unchecked { ++i; }
        }
        return false;
    }

    /**
     * @param A The input array to search
     * @param a The address to remove
     * @return Returns the array with the object removed.
     */
    function remove(address[] memory A, address a)
        internal
        pure
        returns (address[] memory)
    {
        (uint256 index, bool isIn) = indexOf(A, a);
        if (!isIn) {
            revert AddressArrayUtils__ArrayItemNotFound(a);
        } else {
            (address[] memory _A,) = pop(A, index);
            return _A;
        }
    }

    /**
     * @param A The input array to search
     * @param a The address to remove
     */
    function removeStorage(address[] storage A, address a)
        internal
    {
        (uint256 index, bool isIn) = indexOf(A, a);
        if (!isIn) {
            revert AddressArrayUtils__ArrayItemNotFound(a);
        } else {
            uint256 lastIndex = A.length - 1; // If the array would be empty, the previous line would throw, so no underflow here
            if (index != lastIndex) { A[index] = A[lastIndex]; }
            A.pop();
        }
    }

    /**
    * Removes specified index from array
    * @param A The input array to search
    * @param index The index to remove
    * @return Returns the new array and the removed entry
    */
    function pop(address[] memory A, uint256 index)
        internal
        pure
        returns (address[] memory, address)
    {
        uint256 length = A.length;
        if (index >= length) revert AddressArrayUtils__ArrayIndexOutOfBounds(index, length);
        address[] memory newAddresses = new address[](length - 1);
        for (uint256 i; i < index; ) {
            newAddresses[i] = A[i];
            unchecked { ++i; }
        }
        for (uint256 j = index + 1; j < length;) {
            newAddresses[j - 1] = A[j];
            unchecked { ++j; }
        }
        return (newAddresses, A[index]);
    }

    /**
     * Returns the combination of the two arrays
     * @param A The first array
     * @param B The second array
     * @return Returns A extended by B
     */
    function extend(address[] memory A, address[] memory B) internal pure returns (address[] memory) {
        uint256 aLength = A.length;
        uint256 bLength = B.length;
        address[] memory newAddresses = new address[](aLength + bLength);
        for (uint256 i; i < aLength; ) {
            newAddresses[i] = A[i];
            unchecked { ++i; }
        }
        for (uint256 j; j < bLength;) {
            newAddresses[aLength + j] = B[j];
            unchecked { ++j; }
        }
        return newAddresses;
    }

    /**
     * Validate that address and uint array lengths match. Validate address array is not empty
     * and contains no duplicate elements.
     *
     * @param A         Array of addresses
     * @param B         Array of uint
     */
    function validatePairsWithArray(address[] calldata A, uint256[] calldata B) internal pure {
        uint256 aLength = A.length;
        uint256 bLength = B.length;
        if (aLength != bLength)
            revert AddressArrayUtils__ArrayLengthMismatch(aLength, bLength);
        _validateLengthAndUniqueness(A);
    }

    /**
     * Validate that address and bool array lengths match. Validate address array is not empty
     * and contains no duplicate elements.
     *
     * @param A         Array of addresses
     * @param B         Array of bool
     */
    function validatePairsWithArray(address[] calldata A, bool[] calldata B) internal pure {
        uint256 aLength = A.length;
        uint256 bLength = B.length;
        if (aLength != bLength)
            revert AddressArrayUtils__ArrayLengthMismatch(aLength, bLength);
        _validateLengthAndUniqueness(A);
    }

    /**
     * Validate that address and string array lengths match. Validate address array is not empty
     * and contains no duplicate elements.
     *
     * @param A         Array of addresses
     * @param B         Array of strings
     */
    function validatePairsWithArray(address[] calldata A, string[] calldata B) internal pure {
        uint256 aLength = A.length;
        uint256 bLength = B.length;
        if (aLength != bLength) revert AddressArrayUtils__ArrayLengthMismatch(aLength, bLength);
        _validateLengthAndUniqueness(A);
    }

    /**
     * Validate that address array lengths match, and calling address array are not empty
     * and contain no duplicate elements.
     *
     * @param A         Array of addresses
     * @param B         Array of addresses
     */
    function validatePairsWithArray(address[] calldata A, address[] calldata B) internal pure {
        uint256 aLength = A.length;
        uint256 bLength = B.length;
        if (aLength != bLength) revert AddressArrayUtils__ArrayLengthMismatch(aLength, bLength);
        _validateLengthAndUniqueness(A);
    }

    /**
     * Validate that address and bytes array lengths match. Validate address array is not empty
     * and contains no duplicate elements.
     *
     * @param A         Array of addresses
     * @param B         Array of bytes
     */
    function validatePairsWithArray(address[] calldata A, bytes[] calldata B) internal pure {
        uint256 aLength = A.length;
        uint256 bLength = B.length;
        if (aLength != bLength) revert AddressArrayUtils__ArrayLengthMismatch(aLength, bLength);
        _validateLengthAndUniqueness(A);
    }

    /**
     * Validate address array is not empty and contains no duplicate elements.
     *
     * @param A          Array of addresses
     */
    function _validateLengthAndUniqueness(address[] calldata A) internal pure {
        if (A.length == 0) revert AddressArrayUtils__ArrayIsEmpty();
        if (hasDuplicate(A)) revert AddressArrayUtils__ArrayContainsDuplicates();
    }
}
