pragma solidity 0.8.21;

import { Ownable2Step } from "@openzeppelin/contracts/access/Ownable2Step.sol";

import { IManagerIssuanceHook } from "../interfaces/IManagerIssuanceHook.sol";
import { ISetToken } from "../interfaces/ISetToken.sol";

/**
 * @title SupplyCapIssuanceHook
 * @author Set Protocol
 *
 * Issuance hook that checks new issuances won't push SetToken totalSupply over supply cap.
 *
 * CHANGELOG:
 * - 4/27/23: Removed OZ SafeMath & upgrade Solidity to v0.8.20
 * - 9/13/23: Switch from Ownable to Ownable2Step
 * - 9/26/23: Added custom errors
 */
contract SupplyCapIssuanceHook is Ownable2Step, IManagerIssuanceHook {

    /* ============ Custom Errors ============ */

    /// @notice Emitted when the quantity to be issued exceeds the supply cap.
    error SupplyCapIssuanceHook__SupplyCapExceeded(address emitter, uint256 issueQuantity, uint256 cap);

    /* ============ Events ============ */

    event SupplyCapUpdated(uint256 _newCap);

    /* ============ State Variables ============ */

    // Cap on totalSupply of Sets
    uint256 public supplyCap;

    /* ============ Constructor ============ */

    /**
     * Constructor, overwrites owner and original supply cap.
     *
     * @param _initialOwner     Owner address, overwrites Ownable logic which sets to deployer as default
