pragma solidity 0.8.21;

import { AddressArrayUtils } from "../lib/AddressArrayUtils.sol";
import { ISanctionsList } from "../interfaces/external/ISanctionsList.sol";
import { ISetToken } from "../interfaces/ISetToken.sol";
import { SupplyCapIssuanceHook } from "./SupplyCapIssuanceHook.sol";

/**
 * @title SupplyCapPermissionedIssuanceHook
 * @author Amun
 *
 * Issuance hook that checks
 * 1) New issuances won't push SetToken totalSupply over supply cap
 * 2) An address is not sanctioned
 * 3) If whitelist enabled, address is in whitelist
 * 4) If blacklist enabled, address is not in blacklist
 */
contract SupplyCapPermissionedIssuanceHook is SupplyCapIssuanceHook {
    using AddressArrayUtils for address[];

    /* ============ Custom Errors ============ */

    /// @notice Emitted when the provided caller address is blacklisted.
    error SupplyCapPermissionedIssuanceHook__AddressNotPermitted(address emitter, address caller);
    /// @notice Emitted when the provided caller address has been sanctioned.
    error SupplyCapPermissionedIssuanceHook__AddressSanctioned(address emitter, address caller);

    /* ============ Events ============ */

    event WhitelistUpdated(address indexed _caller, bool _status);
    event BlacklistUpdated(address indexed _caller, bool _status);
    event WhitelistStatusUpdated(bool indexed _status);
    event BlacklistStatusUpdated(bool indexed _status);

    /* ============ Constants ============ */

    address public immutable SANCTIONS_CONTRACT;

    /* ============ State Variables ============ */

    // Mapping of addresses allowed to call function
    mapping(address => bool) public whitelist;

    // Mapping of addresses not allowed to call function
    mapping(address => bool) public blacklist;

    // Boolean indicating if whitelist is enabled
    bool public whitelistStatus;

    // Boolean indicating if blacklist is enabled
    bool public blacklistStatus;

    /* ============ Constructor ============ */

    /**
     * Constructor, overwrites owner and original supply cap.
     *
     * @param _initialOwner      Owner address, overwrites Ownable logic which sets to deployer as default
     * @param _supplyCap         Supply cap for Set (in wei of Set)
     */
    constructor(
        address _initialOwner,
        uint256 _supplyCap,
        address _sanctionsOracle
    )
        SupplyCapIssuanceHook(
            _initialOwner, _supplyCap
        )
    {
        SANCTIONS_CONTRACT = _sanctionsOracle;
    }

    /* ============ External Functions ============ */

    /**
     * Adheres to IManagerIssuanceHook interface, and checks to make sure the current issue call won't push total supply over cap.
     */
    function invokePreIssueHook(
        ISetToken _setToken,
        uint256 _issueQuantity,
        address _sender,
        address /*_to*/
    )
        external
        view
        override
    {
        _validateAllowedCaller(_sender);

        uint256 totalSupply = _setToken.totalSupply();
        if (totalSupply + _issueQuantity > supplyCap) revert SupplyCapIssuanceHook__SupplyCapExceeded(address(this), _issueQuantity, supplyCap);
    }

    /**
     * Adheres to IManagerIssuanceHook interface
     */
    function invokePreRedeemHook(
        address _sender
    )
        external
        view
        override
    {
        _validateAllowedCaller(_sender);
    }

    /**
     * ONLY OWNER: Updates supply cap
     */
    function updateSupplyCap(uint256 _newCap) external virtual override onlyOwner {
        supplyCap = _newCap;
        emit SupplyCapUpdated(_newCap);
    }

    /**
     * ONLY OWNER: Toggle ability for passed addresses to call only allowed caller functions
     *
     * @param _callers           Array of caller addresses to toggle status
     * @param _statuses          Array of statuses for each caller
     */
    function updateWhitelist(address[] calldata _callers, bool[] calldata _statuses) external onlyOwner {
        _callers.validatePairsWithArray(_statuses);
        uint256 callersLen = _callers.length;
        for (uint256 i; i < callersLen;) {
            address caller = _callers[i];
            bool status = _statuses[i];
            whitelist[caller] = status;
            emit WhitelistUpdated(caller, status);
            unchecked { ++i; }
        }
    }

    /**
     * ONLY OWNER: Toggle ability for passed addresses to not allow them to call functions
     *
     * @param _callers           Array of caller addresses to toggle status
     * @param _statuses          Array of statuses for each caller
     */
    function updateBlacklist(address[] calldata _callers, bool[] calldata _statuses) external onlyOwner {
        _callers.validatePairsWithArray(_statuses);
        uint256 callersLen = _callers.length;
        for (uint256 i; i < callersLen;) {
            address caller = _callers[i];
            bool status = _statuses[i];
            blacklist[caller] = status;
            emit BlacklistUpdated(caller, status);
            unchecked { ++i; }
        }
    }

    /**
     * ONLY OWNER: Toggle whether anyone can call function, bypassing the whitelist
     *
     * @param _status           Boolean indicating whether whitelist is enabled or not.
     */
    function updateWhitelistStatus(bool _status) external onlyOwner {
        whitelistStatus = _status;
        emit WhitelistStatusUpdated(_status);
    }

    /**
     * ONLY OWNER: Toggle whether blacklist is enabled
     *
     * @param _status           Boolean indicating whether blacklist is enabled or not.
     */
    function updateBlacklistStatus(bool _status) external onlyOwner {
        blacklistStatus = _status;
        emit BlacklistStatusUpdated(_status);
    }

    /* ============ Internal Functions ============ */

    /**
     * Validate if passed address is allowed to call function.
     * If caller is included in the sanctions list, the transaction will revert.
     * If both whitelistStatus and blacklistStatus are set to false, any address can call function.
     */
    function _validateAllowedCaller(address _caller) internal view {
        ISanctionsList sanctionsList = ISanctionsList(SANCTIONS_CONTRACT);
        if (sanctionsList.isSanctioned(_caller)) revert SupplyCapPermissionedIssuanceHook__AddressSanctioned(address(this), _caller);
        if ((blacklistStatus && blacklist[_caller]) || (whitelistStatus && !whitelist[_caller])) {
            revert SupplyCapPermissionedIssuanceHook__AddressNotPermitted(address(this), _caller);
        }
    }
}"
