    "contracts/Summoner.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "./interfaces/IAnito.sol";

contract Summoner is Ownable, Pausable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    IAnito public anitoNFT;
    IERC20 public laroToken;
    IERC20 public gintoToken;
    IERC20 public busdToken;

    address signer;
    address treasury;
    mapping(uint256 => bool) usedNonces;

    event SummoningExecuted(
        uint256 tokenId,
        uint256 _anito1,
        uint256 _anito2,
        uint256 rarityStoneType,
        uint256 classStoneType,
        uint256 statStoneType1,
        uint256 statStoneType2,
        uint256 nonce
    );

    constructor(
        address _anitoNFT,
        address _signer,
        address _treasury,
        address _busd,
        address _laro,
        address _ginto
    ) {
        anitoNFT = IAnito(_anitoNFT);
        busdToken = IERC20(_busd);
        laroToken = IERC20(_laro);
        gintoToken = IERC20(_ginto);
        signer = _signer;
        treasury = _treasury;
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    function setSigner(address _signer) public onlyOwner {
        signer = _signer;
    }

    function setTreasury(address _treasury) public onlyOwner {
        treasury = _treasury;
    }

    function summon(
        uint256 _anito1,
        uint256 _anito2,
        uint256 rarityStoneType,
        uint256 classStoneType,
        uint256 statStoneType1,
        uint256 statStoneType2,
        uint256 usd_amount,
        uint256 laro_amount,
        uint256 ginto_amount,
        uint256 nonce,
        bytes memory _sig
    ) public nonReentrant whenNotPaused {
        require(anitoNFT.ownerOf(_anito1) == msg.sender, "Not owned anito");
        require(anitoNFT.ownerOf(_anito2) == msg.sender, "Not owned anito");
        bytes32 message = prefixed(
            keccak256(
                abi.encodePacked(
                    msg.sender,
                    _anito1,
                    _anito2,
                    rarityStoneType,
                    classStoneType,
                    statStoneType1,
                    statStoneType2,
                    usd_amount,
                    laro_amount,
                    ginto_amount,
                    nonce
                )
            )
        );
        require(recoverSigner(message, _sig) == signer, "Invalid signer");
        require(!usedNonces[nonce], "nonce already used");

        if (usd_amount > 0) {
            busdToken.safeTransferFrom(msg.sender, address(this), usd_amount);
            busdToken.transfer(treasury, usd_amount);
        }

        if (laro_amount > 0) {
            laroToken.safeTransferFrom(msg.sender, address(this), laro_amount);
            laroToken.transfer(treasury, laro_amount);
        }

        if (ginto_amount > 0) {
            gintoToken.safeTransferFrom(
                msg.sender,
                address(this),
                ginto_amount
            );
            gintoToken.transfer(treasury, ginto_amount);
        }

        anitoNFT.executeSummoning(
            msg.sender,
            _anito1,
            _anito2,
            rarityStoneType,
            classStoneType,
            statStoneType1,
            statStoneType2
        );

        emit SummoningExecuted(
            anitoNFT.totalSupply(),
            _anito1,
            _anito2,
            rarityStoneType,
            classStoneType,
            statStoneType1,
            statStoneType2,
            nonce
        );
    }

    function splitSignature(
        bytes memory sig
    ) internal pure returns (uint8, bytes32, bytes32) {
        require(sig.length == 65, "Incorrect signature length");

        bytes32 r;
        bytes32 s;
        uint8 v;

        assembly {
            //first 32 bytes, after the length prefix
            r := mload(add(sig, 0x20))
            //next 32 bytes
            s := mload(add(sig, 0x40))
            //final byte, first of next 32 bytes
            v := byte(0, mload(add(sig, 0x60)))
        }

        return (v, r, s);
    }

    function recoverSigner(
        bytes32 message,
        bytes memory sig
    ) internal pure returns (address) {
        uint8 v;
        bytes32 r;
        bytes32 s;

        (v, r, s) = splitSignature(sig);

        return ecrecover(message, v, r, s);
    }

    function prefixed(bytes32 hash) internal pure returns (bytes32) {
        return
            keccak256(
                abi.encodePacked("\x19Ethereum Signed Message:\n32", hash)
            );
    }
}
"
