    "contracts/ERC721BeaconFactory.sol": {
      "content": "// SPDX-License-Identifier: Unlicense

pragma solidity ^0.8.9;

import "./ERC721CreatorImplementation.sol";
import "@openzeppelin/contracts/proxy/beacon/BeaconProxy.sol";
import "@openzeppelin/contracts/proxy/beacon/UpgradeableBeacon.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

contract ERC721Factory {
  UpgradeableBeacon immutable beacon;
  uint32 private collectionId;

  event CollectionCreated(
    address indexed ownerAddress,
    address indexed collectionAddress,
    uint32 indexed collectionId,
    string _name
  );

  constructor(address _collectionLogic) {
    beacon = new UpgradeableBeacon(_collectionLogic);
    beacon.transferOwnership(msg.sender);
    collectionId = 0;
  }

  function createCollection(string memory _name) external returns (address) {
    BeaconProxy collection = new BeaconProxy(
      address(beacon),
      abi.encodeWithSelector(
        ERC721CreatorImplementation(address(0)).initialize.selector,
        _name,
        string.concat("WoV#", Strings.toString(collectionId))
      )
    );
    Ownable(address(collection)).transferOwnership(msg.sender);
    emit CollectionCreated(msg.sender, address(collection), collectionId, _name);
    collectionId++;
    return address(collection);
  }

  function getBeacon() external view returns (address) {
    return address(beacon);
  }

  function getImplementation() external view returns (address) {
    return beacon.implementation();
  }
}
"
    },
    "contracts/ERC721CreatorImplementation.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

/// @author: manifold.xyz

import "@manifoldxyz/libraries-solidity/contracts/access/AdminControlUpgradeable.sol";

import "@manifoldxyz/creator-core-solidity/contracts/core/ERC721CreatorCore.sol";
import "@manifoldxyz/creator-core-solidity/contracts/token/ERC721/ERC721Upgradeable.sol";

/**
 * @dev ERC721Creator implementation
 */
contract ERC721CreatorImplementation is
  AdminControlUpgradeable,
  ERC721Upgradeable,
  ERC721CreatorCore
{
  using EnumerableSet for EnumerableSet.AddressSet;

  /**
   * Initializer
   */
  function initialize(string memory _name, string memory _symbol) public initializer {
    __ERC721_init(_name, _symbol);
    __Ownable_init();
  }

  /**
   * @dev See {IERC165-supportsInterface}.
   */
  function supportsInterface(
    bytes4 interfaceId
  )
    public
    view
    virtual
    override(ERC721Core, ERC721CreatorCore, AdminControlUpgradeable)
    returns (bool)
  {
    return
      ERC721CreatorCore.supportsInterface(interfaceId) ||
      ERC721Core.supportsInterface(interfaceId) ||
      AdminControlUpgradeable.supportsInterface(interfaceId);
  }

  function _beforeTokenTransfer(
    address from,
    address to,
    uint256 tokenId,
