pragma solidity 0.8.4;






contract NFTStaking is Ownable, IERC721Receiver {

    // Vaults Struct
    struct VaultInfo {
            ERC721Enumerable nft;
            FathomToken token;
            string name;
            uint256 tokenRewards;
    }

    VaultInfo[] public vaults;

    // Stakes Struct
    struct Stake {
        uint24 tokenId;
        uint48 timestamp;
        address owner;
    }

    uint256 public totalStaked;

    // Multi Stakes Array
    mapping( uint256 => mapping( uint256 => Stake )) public stakes;

    event NFTStaked(address owner, uint256 tokenId, uint256 value);
    event NFTUnstaked(address owner, uint256 tokenId, uint256 value);
    event Claimed(address owner, uint256 amount);

    // Add new Collection to the Vault
    function addVault(ERC721Enumerable _nft, FathomToken _token, string calldata _name, uint256 _tokenRewards) external onlyOwner {
        vaults.push(
            VaultInfo({
                nft: _nft,
                token: _token,
                name: _name,
                tokenRewards: _tokenRewards * 100
            })
        );
    }

    // Update current Vault by index
    function updateVault(ERC721Enumerable _nft, FathomToken _token, string calldata _name, uint256 _tokenRewards, uint256 _pid) external onlyOwner {
        VaultInfo storage targetVault = vaults[_pid];
        targetVault.nft = _nft;
        targetVault.token = _token;
        targetVault.name = _name;
        targetVault.tokenRewards = _tokenRewards * 100;
    }

    // Delete current Vault by index
    function deleteVault(uint256 _pid) external onlyOwner {
       delete vaults[_pid];
    }

    // Stake NFT to specific Vault by index
    function stake(uint256 _pid, uint256[] calldata tokenIds) external {
        uint256 tokenId;
        totalStaked += tokenIds.length;
        VaultInfo storage targetVault = vaults[_pid];
        for (uint i = 0; i < tokenIds.length; i++) {
            tokenId = tokenIds[i];
            require(targetVault.nft.ownerOf(tokenId) == msg.sender, "Not your token");
            require(stakes[_pid][tokenId].tokenId == 0, "Already staked");

            targetVault.nft.transferFrom(msg.sender, address(this), tokenId);
            emit NFTStaked(msg.sender, tokenId, block.timestamp);

            stakes[_pid][tokenId] = Stake({
                owner: msg.sender,
                tokenId: uint24(tokenId),
                timestamp: uint48(block.timestamp)
            });
        }
    }

     // Unstake many NFTs from specific Vault by index
    function _unstakeMany(address account, uint256[] calldata tokenIds, uint256 _pid) internal {
        uint256 tokenId;
        totalStaked -= tokenIds.length;
        VaultInfo storage targetVault = vaults[_pid];
        for (uint i = 0; i < tokenIds.length; i++) {
            tokenId = tokenIds[i];
            Stake memory staked = stakes[_pid][tokenId];
            require(staked.owner == msg.sender, "Not an owner");

            delete stakes[_pid][tokenId];
            emit NFTUnstaked(account, tokenId, block.timestamp);
            targetVault.nft.transferFrom(address(this), account, tokenId);
        }
    }

    // Claim reward for NFT staking
    function claim(uint256[] calldata tokenIds, uint256 _pid) external {
        _claim(msg.sender, tokenIds, _pid, false);
    }

    // Claim reward for NFT staking to specific address
    function claimForAddress(address account, uint256[] calldata tokenIds, uint256 _pid) external {
        _claim(account, tokenIds, _pid, false);
    }

    // Unstake NFT from Vault by index + claim
    function unstake(uint256[] calldata tokenIds, uint256 _pid) external {
        _claim(msg.sender, tokenIds, _pid, true);
    }

    // Claim reward for NFT staking by vault index
    function _claim(address account, uint256[] calldata tokenIds, uint256 _pid, bool _unstake) internal {
        uint256 tokenId;
        uint256 earned = 0;
        VaultInfo storage targetVault = vaults[_pid];
        for (uint i = 0; i < tokenIds.length; i++) {
            tokenId = tokenIds[i];
            Stake memory staked = stakes[_pid][tokenId];
            require(staked.owner == account, "not an owner");
            uint256 stakedAt = staked.timestamp;
            earned += (targetVault.tokenRewards * 1e18) * (block.timestamp - stakedAt) / 1 days;
        
            stakes[_pid][tokenId] = Stake({
                owner: account,
                tokenId: uint24(tokenId),
                timestamp: uint48(block.timestamp)
            });

        }
        if (earned > 0) {
            earned = earned / 100;
            targetVault.token.mint(account, earned);
        }
        if (_unstake) {
            _unstakeMany(account, tokenIds, _pid);
        }
        emit Claimed(account, earned);
    }

    // Get earning information for specific vault by index
    function earningInfo(address account, uint256[] calldata tokenIds, uint256 _pid) external view returns (uint256[1] memory info) {
        uint256 tokenId;
        uint256 earned = 0;
        uint256 rewardmath = 0;
        VaultInfo storage targetVault = vaults[_pid];

        for (uint i = 0; i < tokenIds.length; i++) {
            tokenId = tokenIds[i];
            Stake memory staked = stakes[_pid][tokenId];
            require(staked.owner == account, "not an owner");
            uint256 stakedAt = staked.timestamp;
            rewardmath += (targetVault.tokenRewards * 1e18) * (block.timestamp - stakedAt) / 1 days;
            earned = rewardmath / 100;

        }
        if (earned > 0) {
            return [earned];
        }
    }

    // Balance of staked NFTs by user (Number). Should never be used inside of transaction because of gas fee.
    function balanceOf(address account,uint256 _pid) public view returns (uint256) {
        uint256 balance = 0;
        VaultInfo storage targetVault = vaults[_pid];
        uint256 supply = targetVault.nft.totalSupply();
        for(uint i = 1; i <= supply; i++) {
            if (stakes[_pid][i].owner == account) {
                balance += 1;
            }
        }
        return balance;
    }

    // Staked NFTs by user (Array of NFTs). Should never be used inside of transaction because of gas fee.
    function tokensOfOwner(address account, uint256 _pid) public view returns (uint256[] memory ownerTokens) {
        VaultInfo storage targetVault = vaults[_pid];
        uint256 supply = targetVault.nft.totalSupply();
        uint256[] memory tmp = new uint256[](supply);
        uint256 index = 0;
        for(uint tokenId = 1; tokenId <= supply; tokenId++) {
           if (stakes[_pid][tokenId].owner == account) {
                tmp[index] = stakes[_pid][tokenId].tokenId;
                index +=1;
            }
        }

        uint256[] memory tokens = new uint256[](index);
        for(uint i = 0; i < index; i++) {
        tokens[i] = tmp[i];
        }

        return tokens;
    }

    // Hook
    function onERC721Received(
            address,
            address from,
            uint256,
            bytes calldata
        ) external pure override returns (bytes4) {
        require(from == address(0x0), "Cannot send nfts to Vault directly");
        return IERC721Receiver.onERC721Received.selector;
        }

    // Set ERC20 reward amount for specific Vault by index
    function setTokenRewards(uint256 _tokenRewards, uint256 _pid) public onlyOwner {
        VaultInfo storage targetVault = vaults[_pid];
        targetVault.tokenRewards = _tokenRewards * 100;
    }
    
}
