pragma solidity ^0.8.18;

//@author ENERJYS
//@title Raffle : OG Teddies






contract RaffleG_0 is ReentrancyGuard, VRFConsumerBaseV2, ConfirmedOwner {
    using Counters for Counters.Counter;
    Counters.Counter public idCounter;

    address[] public addressPlayers;
    address[] public winners;
    uint256 public numWinners;
    uint256 public deadline;
    uint256 public startDate;
    bool public lotteryClosed;
    uint256 public ticketPrice;

    event TicketsPurchased(address addressPlayer, uint256 nbTicket);
    event RequestSent(uint256 requestId, uint32 numWords);
    event RequestFulfilled(uint256 requestId, uint256[] randomWords);

    struct RequestStatus {
        bool fulfilled; // whether the request has been successfully fulfilled
        bool exists; // whether a requestId exists
        uint256[] randomWords;
    }
    mapping(uint256 => RequestStatus)
        public s_requests; /* requestId --> requestStatus */
    VRFCoordinatorV2Interface COORDINATOR;

    // Your subscription ID.
    uint64 s_subscriptionId;

    // past requests Id.
    uint256[] public requestIds;
    uint256 public lastRequestId;

    // The gas lane to use, which specifies the maximum gas price to bump to.
    // For a list of available gas lanes on each network,
    // see https://docs.chain.link/docs/vrf/v2/subscription/supported-networks/#configurations
    // SEPOLIA : 0x474e34a077df58807dbe9c96d3c009b23b3c6d0cce433e59bbf5b34f823bc56c
    // MUMBAI : 0x4b09e658ed251bcafeebbc69400383d49f344ace09b9576fe248bb02c003fe9f
    // MAINNET : 0xcc294a196eeeb44da2888d17c0625cc88d70d9760a69d58d853ba6581a9ab0cd
    bytes32 keyHash =
        0xcc294a196eeeb44da2888d17c0625cc88d70d9760a69d58d853ba6581a9ab0cd;

    // Depends on the number of requested values that you want sent to the
    // fulfillRandomWords() function. Storing each word costs about 20,000 gas,
    // so 100,000 is a safe default for this example contract. Test and adjust
    // this limit based on the network that you select, the size of the request,
    // and the processing of the callback request in the fulfillRandomWords()
    // function.
    uint32 callbackGasLimit = 100000;

    // The default is 3, but you can set this higher.
    uint16 requestConfirmations = 3;

    // For this example, retrieve 2 random values in one request.
    // Cannot exceed VRFCoordinatorV2.MAX_NUM_WORDS.
    uint32 numWords = 1;

    /**
     * HARDCODED FOR SEPOLIA
     * COORDINATOR: 0x8103B0A8A00be2DDC778e6e7eaa21791Cd364625
     */
    /**
     * HARDCODED FOR MUMBAI
     * COORDINATOR: 0x7a1BaC17Ccc5b313516C5E16fb24f7659aA5ebed
     */
    /**
     * HARDCODED FOR MAINNET
     * COORDINATOR: 0xAE975071Be8F8eE67addBC1A82488F1C24858067
     */
    uint256 public nbTicketSell;

    struct Player {
        uint256 idPlayer;
        address addressPlayer;
        uint256 ticketsBought;
        uint256 nbOfWin;
        bool isWinner;
        bool playerExist;
    }

    mapping(address => uint256) public idByAddress;
    Player[] public playersList;

    /**
     * @dev Initializes the raffle (lottery) contract with predefined parameters.
     * @param _startDate  {uint256} - Start date of the lottery in timestamp.
     * @param _durationInMinutes {uint256} - Duration of the lottery in minutes.
     * @param _ticketPrice {uint256} - Price of a lottery ticket.
     * @param  subscriptionId {uint64} - Subscription ID for the Chainlink service.
     */
    constructor(
        uint256 _startDate,
        uint256 _durationInMinutes,
        uint256 _ticketPrice,
        uint64 subscriptionId
    )
        VRFConsumerBaseV2(0xAE975071Be8F8eE67addBC1A82488F1C24858067)
        ConfirmedOwner(msg.sender)
    {
        require(_startDate > block.timestamp, "Invalide value of startDate");
        require(_durationInMinutes > 0, "Invalide value of duration");
        require(_ticketPrice > 0, "Invalide value of ticket price");
        COORDINATOR = VRFCoordinatorV2Interface(
            0xAE975071Be8F8eE67addBC1A82488F1C24858067
        );
        s_subscriptionId = subscriptionId;
        startDate = _startDate;
        deadline = startDate + (_durationInMinutes * 1 minutes);
        lotteryClosed = false;
        ticketPrice = _ticketPrice * 1 wei;
        nbTicketSell = 0;
    }

    /**
     * @dev Updates the start date and duration of the lottery.
     * @param _startDate {uint256} - New start date of the lottery in timestamp.
     * @param _durationInMinutes {uint256} - New duration of the lottery in minutes.
     */
    function setStartDate(
        uint256 _startDate,
        uint256 _durationInMinutes
    ) public onlyOwner {
        require(_durationInMinutes > 0, "Invalide value of duration");
        require(_startDate > 0, "Invalide value of startDate");
        deadline = _startDate + (_durationInMinutes * 1 minutes);
        startDate = _startDate;
    }

    /**
     * @dev Sends a request to get random words.
     * @return requestId {uint256} - The ID of the request sent.
     */
    // Assumes the subscription is funded sufficiently.
    function requestRandomWords() internal returns (uint256 requestId) {
        // Will revert if subscription is not set and funded.
        requestId = COORDINATOR.requestRandomWords(
            keyHash,
            s_subscriptionId,
            requestConfirmations,
            callbackGasLimit,
            numWords
        );
        s_requests[requestId] = RequestStatus({
            randomWords: new uint256[](0),
            exists: true,
            fulfilled: false
        });
        requestIds.push(requestId);
        lastRequestId = requestId;
        emit RequestSent(requestId, numWords);
        return requestId;
    }

    /**
     * @dev Fulfills the request with obtained random words.
     * @param _requestId {uint256} - The ID of the request to fulfill.
     * @param _randomWords {uint256[]} - Array of random words to fulfill the request with.
     */
    function fulfillRandomWords(
        uint256 _requestId,
        uint256[] memory _randomWords
    ) internal override {
        require(s_requests[_requestId].exists, "request not found");
        s_requests[_requestId].fulfilled = true;
        s_requests[_requestId].randomWords = _randomWords;
        emit RequestFulfilled(_requestId, _randomWords);
    }

    /**
     * @dev Allows a player to buy a specified number of tickets.
     * @param _nbTickets {uint256} - The number of tickets to buy.
     */
    function buyTicket(uint256 _nbTickets) public payable nonReentrant {
        require(!lotteryClosed, "Lottery is closed");
        require(_nbTickets > 0, "Invalide value of nb tickets");
        require(block.timestamp >= startDate, "Lottery hasn't started");
        require(
            msg.value >= _nbTickets * ticketPrice,
            "Minimum ticket price is not met"
        );
        require(block.timestamp <= deadline, "Lottery deadline has passed");

        bool playerExists = false;
        uint256 existingPlayerIndex;
        for (uint256 i = 0; i < idCounter.current(); i++) {
            if (
                playersList[i].addressPlayer == msg.sender &&
                playersList[i].playerExist
            ) {
                playerExists = true;
                existingPlayerIndex = playersList[i].idPlayer;
            }
        }
        if (playerExists) {
            playersList[existingPlayerIndex].ticketsBought += _nbTickets;
        } else {
            Player memory newPlayer = Player({
                idPlayer: idCounter.current(),
                addressPlayer: msg.sender,
                nbOfWin: 0,
                ticketsBought: _nbTickets,
                isWinner: false,
                playerExist: true
            });
