     * @param _raffleContract {RaffleG_0} - Address of the raffle contract.
     * @param _saleStartTime {uint} - Start time for the sale.
     * @param _endTimeGuaranteed {uint} - End time for the guaranteed sale.
     */
    constructor(
        bytes32 _merkleRoot,
        string memory _preRevealedURI,
        RaffleG_0 _raffleContract,
        uint _saleStartTime,
        uint _endTimeGuaranteed
    ) ERC721A("OG Teddies", "OGT") {
        merkleRootGuaranteed = _merkleRoot;
        preRevealedURI = _preRevealedURI;
        raffleContract = _raffleContract;
        saleStartTime = _saleStartTime;
        endTimeGuaranteed = _endTimeGuaranteed;
        baseURI = "";
        isRevealed = false;
        isRaffleOver = false;
    }

    modifier callerIsUser() {
        require(tx.origin == msg.sender, "The caller is another contract");
        _;
    }

    /**
     * @dev Allows a whitelisted address to mint an NFT during the guaranteed sale period.
     * @param _proof {bytes32[]} - Merkle proof for the caller's address.
     */
    function whitelistMint(bytes32[] calldata _proof) external callerIsUser {
        require(
            block.timestamp >= saleStartTime,
            "Whitelist Sale has not started yet"
        );
        require(
            block.timestamp < endTimeGuaranteed,
            "Guaranteed Sale is finish"
        );
        require(
            sellingStep == Step.GuaranteedSale,
            "Whitelist sale is not activated"
        );
        require(
            isWhiteListed(msg.sender, merkleRootGuaranteed, _proof),
            "Not guaranteed mint"
        );
        require(
            amountNFTsperWalletWhitelistSale[msg.sender] + 1 <= 1,
            "You can only get 1 NFT on the Guarenteed Sale"
        );
        require(
            totalSupply() + 1 <= MAX_GUARANTEED + MAX_COLLAB_AND_TEAM,
            "Max supply exceeded"
        );
        amountNFTsperWalletWhitelistSale[msg.sender] += 1;
        _safeMint(msg.sender, 1);
    }

    /**
     * @dev Allows a raffle winner to mint their won NFTs.
     */
    function winnerRaffleSaleMint() external callerIsUser {
        require(
            sellingStep == Step.WinnerRaffleSale,
            "Public sale is not activated"
        );
        require(block.timestamp < endTimeWinnerMint, "Winner Mint is finish");
        require(
            winnerByAddress[msg.sender].addressWinner == msg.sender,
            "winner not exist"
        );
        require(winnerByAddress[msg.sender].notMinted, "Already mint ");
        require(winnerByAddress[msg.sender].numberOfWin > 0, "Nothing to mint");
        require(
            totalSupply() + winnerByAddress[msg.sender].numberOfWin <=
                MAX_SUPPLY,
            "Max supply exceeded"
        );
        winnerByAddress[msg.sender].notMinted = false;
        _safeMint(msg.sender, winnerByAddress[msg.sender].numberOfWin);
    }

    /**
     * @dev Gifts a quantity of NFTs to a specified address.
     * @param _to {address} - Address to gift NFTs to.
     * @param _quantity {uint} - Number of NFTs to gift.
     */
    function giftCollab(address _to, uint _quantity) external onlyOwner {
        require(
            totalSupply() + _quantity <= MAX_SUPPLY,
            "Reached total supply"
        );
        _safeMint(_to, _quantity);
    }

    /**
     * @dev Sets up the winners from the raffle for minting.
     * @param _endTimeWinnerMint {uint} - Time until which winners can mint.
     */
    function setWinnerStructViaRaffle(
        uint _endTimeWinnerMint
    ) external onlyOwner {
        require(
            block.timestamp < _endTimeWinnerMint,
            "invalid endTimeWinnerMint"
        );
        require(raffleContract.lotteryClosed(), "Raffle already open");
        require(
            raffleContract.getWinners().length > 0,
            "Winners not set in raffle contract"
        );
        endTimeWinnerMint = _endTimeWinnerMint;
        isRaffleOver = true;
        RaffleG_0.Player[] memory rafflePlayersList = raffleContract.getPlayersList();
        for (uint i = 0; i < rafflePlayersList.length; i++) {
            if (
                rafflePlayersList[i].isWinner &&
                rafflePlayersList[i].nbOfWin > 0
            ) {
                WinnerRaffle memory newWinner = WinnerRaffle({
                    addressWinner: rafflePlayersList[i].addressPlayer,
                    numberOfWin: rafflePlayersList[i].nbOfWin,
                    notMinted: true
                });
                winnerByAddress[rafflePlayersList[i].addressPlayer] = newWinner;
            }
        }
    }

    /**
     * @dev Sets the start time for the sale.
     * @param _saleStartTime {uint} - Start time for the sale.
     */
    function setSaleStartTime(uint _saleStartTime) external onlyOwner {
        saleStartTime = _saleStartTime;
    }

    /**
     * @dev Sets the end time for the guaranteed sale.
     * @param _endTimeGuaranteed {uint} - End time for the guaranteed sale.
     */
    function setEndTimeGuaranteed(uint _endTimeGuaranteed) external onlyOwner {
        endTimeGuaranteed = _endTimeGuaranteed;
    }

    /**
     * @dev Sets the end time for winners to mint.
     * @param _endTimeWinnerMint {uint} - End time for winners to mint.
     */
    function setEndTimeWinnerMint(uint _endTimeWinnerMint) external onlyOwner {
        endTimeWinnerMint = _endTimeWinnerMint;
    }

    /**
     * @dev Sets the raffle contract.
     * @param _raffleContract {RaffleG_0} - Address of the raffle contract.
     */
    function setRaffleContract(RaffleG_0 _raffleContract) external onlyOwner {
        raffleContract = _raffleContract;
    }

    /**
     * @dev Sets the current sale step.
     * @param _step {uint} - Step to set (0: Before, 1: GuaranteedSale, 2: WinnerRaffleSale, 3: SoldOut, 4: Reveal).
     */
    function setStep(uint _step) external onlyOwner {
        sellingStep = Step(_step);
    }

    /**
     * @dev Sets the URI for the pre-reveal stage.
     * @param _preRevealedURI {string} - URI for the pre-reveal stage.
     */
    function setPreRevealedURI(
        string memory _preRevealedURI
    ) external onlyOwner {
        preRevealedURI = _preRevealedURI;
    }

    /**
     * @dev Reveals the NFTs and sets the base URI.
     * @param baseURI_ {string} - Base URI for the NFT metadata.
     */
    function revealAndSetBaseURI(string memory baseURI_) external onlyOwner {
        isRevealed = true;
        baseURI = baseURI_;
    }

    /**
     * @dev Returns the URI for the specified token ID.
     * @param _tokenId {uint} - ID of the token to return the URI for.
     * @return {string} - URI for the specified token ID.
     */
    function tokenURI(
        uint _tokenId
    ) public view virtual override returns (string memory) {
        require(_exists(_tokenId), "URI query for nonexistent token");
        if (isRevealed) {
                return string(abi.encodePacked(baseURI, _tokenId.toString(), ".json"));
            }
        else {
                return string(abi.encodePacked(preRevealedURI, _tokenId.toString(), ".json"));
            }
    }

    /**
     * @dev Sets the Merkle root for the whitelist.
     * @param _merkleRoot {bytes32} - Merkle root for the whitelist.
     */
    function setMerkleRootGuarranteed(bytes32 _merkleRoot) external onlyOwner {
        merkleRootGuaranteed = _merkleRoot;
    }

    /**
     * @dev Checks if an address is whitelisted.
     * @param _account {address} - Address to check.
     * @param _merkleRoot {bytes32} - Merkle root for the whitelist.
     * @param _proof {bytes32[]} - Merkle proof for the address.
     * @return {bool} - Whether the address is whitelisted.
     */
    function isWhiteListed(
        address _account,
        bytes32 _merkleRoot,
        bytes32[] calldata _proof
    ) internal pure returns (bool) {
        return _verify(leaf(_account), _merkleRoot, _proof);
    }

    /**
     * @dev Returns a leaf node for the Merkle tree given an address.
     * @param _account {address} - Address to create a leaf node for.
     * @return {bytes32} - Leaf node created for the address.
     */
    function leaf(address _account) internal pure returns (bytes32) {
        return keccak256(abi.encodePacked(_account));
    }

    /**
     * @dev Verifies a Merkle proof.
     * @param _leaf {bytes32} - Leaf node to verify.
     * @param _merkleRoot {bytes32} - Merkle root for the whitelist.
     * @param _proof {bytes32[]} - Merkle proof for the leaf node.
     * @return {bool} - Whether the Merkle proof is valid.
     */
    function _verify(
        bytes32 _leaf,
        bytes32 _merkleRoot,
        bytes32[] memory _proof
    ) internal pure returns (bool) {
        return MerkleProof.verify(_proof, _merkleRoot, _leaf);
    }
}
