    "contracts/ERC20Staking.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

contract ERC20Staking is ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;
    using ECDSA for bytes32;

    IERC20 public immutable stakingToken;
    address public fulfiller;
    uint256 public treasury;
    uint256 public sumStakedTokens;
    uint256 public minStakeDuration; // in seconds

    event StakeToken(uint128 indexed stakeId, address stakerAddress, uint256 date);
    event WithdrawToken(uint128 indexed stakeId, address stakerAddress, uint256 date);
    event ReStakeToken(uint128 indexed stakeId, address stakerAddress, uint256 date);

    // Constructor function to set the staking token, the fulfiller addresses
    constructor(IERC20 _stakingToken, address _fulfiller, uint256 _minStakeDuration) Ownable() {
        stakingToken = _stakingToken;
        fulfiller = _fulfiller;
        minStakeDuration = _minStakeDuration; // in seconds
    }

    struct StakingInfo {
        address staker;
        bool isActive;
        uint256 amount;
        uint256 rewardRate;
        uint256 duration;
        uint256 stakingStartDate;
        bool isVIP;
        bool isReStake;
        uint256 numberOfDays;
    }

    // Staker info
    struct Staker {
        // Amount of tokens staked by the staker
        uint256 amountStaked;
        // Number of active staking
        uint256 numberStaked;
        // List of stakeId
        uint128[] liststakeId;
    }

    // Mapping of User Address to Staker info
    mapping(address => Staker) public stakers;

    // Mapping for stakeId and staking info
    mapping(uint128 => StakingInfo) public staked;

    // If address already has ERC20 Token/s staked, calculate the rewards.
    // Increment the amountStaked and map msg.sender to the Token Id of the staked
    // Token to later send back on withdrawal. Finally give timeOfLastUpdate the
    // value of now.
    function stake(
        uint256 _amount,
        bytes memory _message,
        bytes memory _signature
    ) external nonReentrant {
        // Wallet must own the token they are trying to stake
        require(stakingToken.balanceOf(msg.sender) >= _amount, "You don't own enough tokens!");

        // Decode message
        (
            uint128 _stakeId,
            address _staker,
            uint256 _rewardRate,
            uint256 _duration,
            bool _isVIP
        ) = _decodeMessage(_message, _signature);

        // Check if duration is too short
        require(_duration >= minStakeDuration, "Duration is too short.");

        // Check if there is another VIP stake
        if (_isVIP == true && stakers[msg.sender].numberStaked > 0) {
            for (uint256 i = 0; i < stakers[msg.sender].liststakeId.length; i++) {
                require(staked[stakers[msg.sender].liststakeId[i]].isVIP != true, "One VIP stake only.");
            }
        }

        // Check if the sender and staker in the the message are the same
        require(msg.sender == _staker, "The staker is different.");

        // Check if the stakeId is created
        require(
            staked[_stakeId].staker == 0x0000000000000000000000000000000000000000,
            "The stakeId is already used."
        );

        // Transfer the token from the wallet to the Smart contract
        stakingToken.safeTransferFrom(msg.sender, address(this), _amount);

        bool isReStake = false;
        uint256 numberOfDays = _duration / 86400;

        // Write the stake action into blockchain
        stakeAction(
            msg.sender,
            _stakeId,
            _amount,
            _rewardRate,
            _duration,
            _isVIP,
            isReStake,
            numberOfDays
        );

        emit StakeToken(_stakeId, msg.sender, block.timestamp);
    }

    // Check if user has any ERC20 Tokens Staked and if they tried to withdraw,
    // calculate the rewards and store them in the unclaimedRewards
    // decrement the amountStaked of the user and transfer the ERC20 token back to them
    function withdraw(uint128 _stakeId) external nonReentrant {
        // Make sure the user has at least one token staked before withdrawing
        require(stakers[msg.sender].numberStaked > 0, "You have no tokens staked.");

        // Wallet must own the token they are trying to withdraw
        require(staked[_stakeId].staker == msg.sender, "You don't own this stake ID!");

        // Prevent user to withdraw an ended stake
        require(staked[_stakeId].isActive == true, "Re-stake an ended stake.");

        require(
            block.timestamp >= staked[_stakeId].stakingStartDate + staked[_stakeId].duration,
            "You are still in a lock period."
        );

        uint256 withdrawAmount = staked[_stakeId].amount;
        uint256 rewards = calculateRewards(_stakeId);
        require(rewards <= treasury, "Treasury doesn't have enough.");
        treasury -= rewards;

        // Create events for the withdraw
        emit WithdrawToken(_stakeId, msg.sender, block.timestamp);

        // Write the withdraw action into blockchain
        withdrawAction(msg.sender, _stakeId, withdrawAmount);

        // Transfer the token back to the withdrawer
        stakingToken.safeTransfer(msg.sender, (withdrawAmount + rewards));
    }

    function reStake(
        uint128 _oldstakeId,
        bytes memory _message,
        bytes memory _signature
    ) external nonReentrant {
        // Make sure the user has at least one token staked before re-staking
        require(stakers[msg.sender].numberStaked > 0, "You have no tokens staked.");

        // Wallet must own the token they are trying to re-stake
        require(msg.sender == staked[_oldstakeId].staker, "You don't own this stake ID!");

        // Prevent user to re-stake an ended stake
        require(staked[_oldstakeId].isActive == true, "Re-stake an ended stake.");

        require(
            block.timestamp >=
                staked[_oldstakeId].stakingStartDate + staked[_oldstakeId].duration,
            "You are still in a lock period."
        );
        // Checking for oldstakeId

        (
            uint128 _newstakeId,
            address _staker,
            uint256 _rewardRate,
            uint256 _duration,
            bool _isVIP
        ) = _decodeMessage(_message, _signature);

        // Check if duration is too short
        require(_duration >= minStakeDuration, "Duration is too short.");

        // Check if the sender and staker in the the message are the same
        require(msg.sender == _staker, "The staker address is different.");

        // Check if the stakeId is created
        require(
            staked[_newstakeId].staker == 0x0000000000000000000000000000000000000000,
            "The stakeId is already used."
        );

        uint256 rewards = calculateRewards(_oldstakeId);
        uint256 oldAmount = staked[_oldstakeId].amount;
        require(rewards <= treasury, "Treasury doesn't have enough.");
        treasury -= rewards;
        bool isReStake = true;
        uint256 thisNumberOfDays = _duration / 86400;
        uint256 numberOfDays = thisNumberOfDays + staked[_oldstakeId].numberOfDays;
        stakeAction(
            msg.sender,
            _newstakeId,
            oldAmount + rewards,
            _rewardRate,
            _duration,
            _isVIP,
            isReStake,
            numberOfDays
        );
        withdrawAction(msg.sender, _oldstakeId, oldAmount);
        emit ReStakeToken(_newstakeId, msg.sender, block.timestamp);
    }

    //////////
    // View //
    //////////

    function getStakedId(address _user) public view returns (uint128[] memory) {
        // Check if we know this user
        if (stakers[_user].numberStaked > 0) {
            return stakers[_user].liststakeId;
        }
        // Otherwise, return empty array
        else {
            return new uint128[](0);
        }
    }

    function calculateRewards(uint128 _stakeId) public view returns (uint256) {
        if (staked[_stakeId].isActive == false) {
            return 0;
        }

        uint256 thisamount = staked[_stakeId].amount;
        uint256 thisRewardRate = staked[_stakeId].rewardRate;
        uint256 thisDuration = staked[_stakeId].duration;
        uint256 thisStakingStartDate = staked[_stakeId].stakingStartDate;

        // Calculate the APY to rewardsPerHour
        uint256 rewardsPerYear = (thisamount * thisRewardRate) / 100;
        uint256 rewardsPerHour = rewardsPerYear / 8760;

        if (block.timestamp > thisStakingStartDate + thisDuration) {
            // If the staking is ended
            return (thisDuration * rewardsPerHour) / 3600;
        } else {
            // If the staking is not ended
            uint256 timeStaked = block.timestamp - thisStakingStartDate;
            return (timeStaked * rewardsPerHour) / 3600;
        }
    }

    /////////////
    // Internal//
    /////////////

    function stakeAction(
        address _userAddress,
        uint128 _stakeId,
        uint256 _amount,
        uint256 _rewardRate,
        uint256 _duration,
        bool _isVIP,
        bool _isReStake,
        uint256 _numberOfDays
    ) internal {
        // Create stakingInfo
        StakingInfo memory stakingInfo = StakingInfo(
            _userAddress,
            true,
            _amount,
            _rewardRate,
            _duration,
            block.timestamp,
            _isVIP,
            _isReStake,
            _numberOfDays
        );

        // Add the token to the stakedTokens array
        staked[_stakeId] = stakingInfo;
        stakers[_userAddress].liststakeId.push(_stakeId);

        // Increment the amount staked for this wallet
        stakers[_userAddress].amountStaked += _amount;
        // Increment the sum amount of token
        sumStakedTokens += _amount;
        // Increment the number of stake
        stakers[_userAddress].numberStaked++;
    }

    function withdrawAction(
        address _userAddress,
        uint128 _stakeId,
        uint256 withdrawAmount
    ) internal {
        // Set isActive to false
        staked[_stakeId].isActive = false;

        // Decrement the amount staked for this wallet
        stakers[_userAddress].amountStaked -= withdrawAmount;
        // Decrement the sum amount of token
        sumStakedTokens -= withdrawAmount;
        // Decrement the number of stake
        stakers[_userAddress].numberStaked--;

        // Update the mapping of the tokenId to the be address(0) to indicate that the token is no longer staked
        for (uint256 i = 0; i < stakers[_userAddress].liststakeId.length; i++) {
            if (stakers[_userAddress].liststakeId[i] == _stakeId) {
                removeByIndexInArray(_userAddress, i);
                break;
            }
        }
    }

    // Remove an index in array
    function removeByIndexInArray(address _userAddress, uint256 _index) internal {
        uint128[] memory _liststakeId = stakers[_userAddress].liststakeId;
        stakers[_userAddress].liststakeId[_index] = _liststakeId[_liststakeId.length - 1];
        stakers[_userAddress].liststakeId.pop();
    }

    function _decodeMessage(bytes memory message, bytes memory signature)
        internal
        returns (
            uint128 uuid,
            address staker,
            uint256 rate,
            uint256 duration,
            bool isVIP
        )
    {
        (uint128 _uuid, address _staker, uint256 _rate, uint256 _duration, bool _isVIP) = abi
            .decode(message, (uint128, address, uint256, uint256, bool));

        address recovered = keccak256(abi.encodePacked(_uuid, _staker, _rate, _duration, _isVIP))
            .toEthSignedMessageHash()
            .recover(signature);

        require(recovered == fulfiller, "Invalid signer.");
        return (_uuid, _staker, _rate, _duration, _isVIP);
    }

    ///////////////
    // owner only//
    ///////////////

    // Allow owner to top up treasury
    function topUpTreasury(uint256 _amount) external onlyOwner {
        require(stakingToken.balanceOf(msg.sender) >= _amount, "You don't own enough tokens!");
        treasury += _amount;
        stakingToken.safeTransferFrom(msg.sender, address(this), _amount);
    }

    // Allow Owner of the contract to withdraw Reward Token from treasury
    function withdrawTreasury(uint256 _amount) external onlyOwner {
        require(_amount <= treasury, "Treasury doesn't have enough.");
        uint256 stuckedTokens = 0;
        if (stakingToken.balanceOf(address(this)) > (treasury + sumStakedTokens)) {
           stuckedTokens += stakingToken.balanceOf(address(this)) - (treasury + sumStakedTokens);
        }
        treasury -= _amount;
        stakingToken.safeTransfer(msg.sender, _amount + stuckedTokens);
    }

    function changeFulfiller(address _newFulfiller) external onlyOwner {
        fulfiller = _newFulfiller;
    }

    // Allow owner to disable a stake
    function disableStake(uint128 _stakeId) external onlyOwner {
        require(staked[_stakeId].staker != 0x0000000000000000000000000000000000000000, "Stake cannot found.");
        require(staked[_stakeId].isActive == true, "Stake is not active.");
        uint256 stakeAmount = staked[_stakeId].amount;
        address stakerAddress = staked[_stakeId].staker;

        withdrawAction(stakerAddress, _stakeId, stakeAmount);
    }

    // we send back accidentally sent ethers
    receive() external payable { revert(); }
}"
