      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "./interfaces/ISeishinBallNFT.sol";

contract SeishinBallStake is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    struct Stake {
        uint256 amount;
        uint256 unlockTimestamp;
        uint256 stakeCount; // how many stakes this counts for
    }

    IERC20 public stakingToken;
    ISeishinBallNFT public rewardNFT;

    uint256 public stakingAmount = 100;
    uint256 public stakingPeriod;
    uint256 public maxStakesPerUser = 10;

    mapping(address => Stake[]) public stakes;

    error AmountIsZero();
    error ExceedsMaxStakesPerUser();
    error NoClaimableNFTs();
    error NoClaimableTokens();

    event Claimed(
        address indexed user,
        uint256 indexed claimableNFTs,
        uint256 indexed claimableTokens
    );
    event Received(address, uint);
    event SetMaxStakesPerUser(uint256 indexed _maxStakesPerUser);
    event StakeTokens(address indexed user, uint256 amount);
    event StakingAmountUpdated(uint256 indexed _stakingAmount);
    event StakingPeriodUpdated(uint256 indexed _stakingPeriod);
    event Unstaked(
        address indexed user,
        uint256 indexed claimableNFTs,
        uint256 indexed claimableTokens
    );

    constructor(
        IERC20 _stakingToken,
        ISeishinBallNFT _rewardNFT,
        uint256 _stakingPeriod
    ) {
        stakingToken = _stakingToken;
        rewardNFT = _rewardNFT;
        stakingPeriod = _stakingPeriod;
    }

    /* Claim NFTs and restake */
    function claim() external nonReentrant {
        (uint256 claimableNFTs, uint256 claimableTokens) = getClaimable(
            _msgSender()
        );
        if (claimableNFTs == 0) revert NoClaimableNFTs();
        // update unlock timestamp for claimable stakes
        Stake[] storage stakerStakes = stakes[_msgSender()];
        for (uint256 i; i < stakerStakes.length; ) {
            Stake storage stake = stakerStakes[i];
            if (block.timestamp >= stake.unlockTimestamp && stake.amount > 0) {
                stake.unlockTimestamp = block.timestamp + stakingPeriod;
            }
            unchecked {
                ++i;
            }
        }
        // mint NFTs
        rewardNFT.proxyMint(claimableNFTs, _msgSender());
        emit Claimed(_msgSender(), claimableNFTs, claimableTokens);
    }

    function getActiveStakes(
        address _staker
    ) external view returns (Stake[] memory) {
        uint activeStakeCount;
        Stake[] storage stakerStakes = stakes[_staker];
        for (uint256 i; i < stakerStakes.length; ) {
            if (stakerStakes[i].amount > 0) {
                ++activeStakeCount;
            }
            unchecked {
                ++i;
            }
        }
        if (activeStakeCount == 0) return new Stake[](0);
        Stake[] memory activeStakes = new Stake[](activeStakeCount);
        uint x;
        for (uint256 i; i < stakerStakes.length; ) {
            if (stakerStakes[i].amount > 0) {
                activeStakes[x] = stakerStakes[i];
                ++x;
            }
            unchecked {
                ++i;
            }
        }
        return activeStakes;
    }

    function getAllStakes(
        address staker
    ) external view returns (Stake[] memory) {
        return stakes[staker];
    }

    function stakeTokens(uint256 _amount) external nonReentrant {
        if (_amount % stakingAmount != 0) {
            _amount = _amount - (_amount % stakingAmount);
        }
        if (_amount == 0) revert AmountIsZero();
        uint numStakes = _amount / stakingAmount;
        uint256 activeStakes = getActiveStakeCount(_msgSender());
        if (activeStakes + numStakes > maxStakesPerUser)
            revert ExceedsMaxStakesPerUser();

        stakingToken.safeTransferFrom(_msgSender(), address(this), _amount);
        stakes[_msgSender()].push(
            Stake(_amount, block.timestamp + stakingPeriod, numStakes)
        );
        emit StakeTokens(_msgSender(), _amount);
    }

    function setMaxStakesPerUser(uint256 _maxStakesPerUser) external onlyOwner {
        if (_maxStakesPerUser == 0) revert AmountIsZero();
        maxStakesPerUser = _maxStakesPerUser;
        emit SetMaxStakesPerUser(_maxStakesPerUser);
    }

    function setStakingAmount(uint256 _stakingAmount) external onlyOwner {
        if (_stakingAmount == 0) revert AmountIsZero();
        stakingAmount = _stakingAmount;
        emit StakingAmountUpdated(_stakingAmount);
    }

    function setStakingPeriod(uint256 _stakingPeriod) external onlyOwner {
        if (_stakingPeriod == 0) revert AmountIsZero();
        stakingPeriod = _stakingPeriod;
        emit StakingPeriodUpdated(_stakingPeriod);
    }

    function unstake() external nonReentrant {
        (uint256 claimableNFTs, uint256 claimableTokens) = getClaimable(
            _msgSender()
        );
        if (claimableTokens == 0) revert NoClaimableTokens();
        // set amount and unlock timestamp to 0 for claimable stakes
        Stake[] storage stakerStakes = stakes[_msgSender()];
        for (uint256 i; i < stakerStakes.length; ) {
            Stake storage stake = stakerStakes[i];
            if (block.timestamp >= stake.unlockTimestamp && stake.amount > 0) {
                stake.amount = 0;
                stake.unlockTimestamp = 0;
                stake.stakeCount = 0;
            }
            unchecked {
                ++i;
            }
        }
        // mint NFTs
        if (claimableNFTs > 0) {
            rewardNFT.proxyMint(claimableNFTs, _msgSender());
        }
        // send tokens
        stakingToken.safeTransfer(_msgSender(), claimableTokens);
        emit Unstaked(_msgSender(), claimableNFTs, claimableTokens);
    }

    function getActiveStakeCount(
        address _staker
    ) public view returns (uint256 _count) {
        Stake[] storage stakerStakes = stakes[_staker];
        for (uint256 i; i < stakerStakes.length; ) {
            if (stakerStakes[i].amount > 0) {
                _count += stakerStakes[i].stakeCount;
            }
            unchecked {
                ++i;
            }
        }
    }

    function getClaimable(
        address _staker
    ) public view returns (uint256 claimableNFTs, uint256 claimableTokens) {
        Stake[] storage stakerStakes = stakes[_staker];
        for (uint256 i; i < stakerStakes.length; ) {
            Stake storage stake = stakerStakes[i];
            // only stakes that are unlocked and have not been claimed yet
            if (block.timestamp >= stake.unlockTimestamp && stake.amount > 0) {
                claimableNFTs += stake.stakeCount;
                claimableTokens += stake.amount;
            }
            unchecked {
                ++i;
            }
        }
    }

    function getNextClaim(
        address _staker
    ) external view returns (uint256 nextClaimTimestamp) {
        Stake[] storage stakerStakes = stakes[_staker];
        for (uint256 i; i < stakerStakes.length; ) {
            Stake storage stake = stakerStakes[i];
            // only stakes that are locked
            if (
                block.timestamp < stake.unlockTimestamp &&
                stake.amount > 0 &&
                (nextClaimTimestamp == 0 ||
                    stake.unlockTimestamp < nextClaimTimestamp)
            ) {
                nextClaimTimestamp = stake.unlockTimestamp;
            }
            unchecked {
                ++i;
            }
        }
        return nextClaimTimestamp;
    }

    // Fallback function
    fallback() external payable {
        emit Received(msg.sender, msg.value);
    }

    // Receive function
    receive() external payable {
        emit Received(msg.sender, msg.value);
    }
}
