    "contracts/CGHero.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/token/common/ERC2981.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract CGHero is ERC721Enumerable, ERC2981, Ownable {
    uint256 public maxTokenIds = 1000;
    uint256 public tokenIds;
    address payable public royaltyAddress;
    uint96 public royaltyAmount;
    string _baseTokenURI;
    bool public _paused;
    mapping(address => bool) whitelist;

    modifier onlyWhenNotPaused() {
        require(!_paused, "Contract currently paused");
        _;
    }

    constructor(address payable _royaltyAddress, uint96 _royaltyAmount) ERC721("CG Hero", "CGH") {
        royaltyAddress = _royaltyAddress;
        royaltyAmount = _royaltyAmount;
        _setDefaultRoyalty(royaltyAddress, royaltyAmount);
    }

    // Function to receive Ether. msg.data must be empty
    receive() external payable {}

    // Fallback function is called when msg.data is not empty
    fallback() external payable {}

    function setWhitelist(address _whitelistAddress, bool approved) public onlyOwner {
        whitelist[_whitelistAddress] = approved;
    }

    function checkWhitelist(address _address) public view returns (bool _approved) {
        return whitelist[_address];
    }

    function setRoyalty(address payable _royaltyAddress, uint96 _royaltyAmount) public onlyOwner {
        royaltyAddress = _royaltyAddress;
        royaltyAmount = _royaltyAmount;
        _setDefaultRoyalty(royaltyAddress, royaltyAmount);
    }

    /**
     * @dev mint allows a user to mint 1 NFT per transaction
     */
    function mint() public payable onlyWhenNotPaused returns (uint256) {
        require(whitelist[msg.sender] == true || msg.sender == owner(), "Not authorized");
        require(tokenIds < maxTokenIds, "Exceeds maximum supply");
        tokenIds += 1;
        _safeMint(msg.sender, tokenIds);
        return tokenIds;
    }

    /**
     * @dev _baseURI overrides the Openzeppelin's ERC721 implementation which by default
     * returned an empty string for the baseURI
     */
    function _baseURI() internal view virtual override returns (string memory) {
        return _baseTokenURI;
    }

    function setBaseURI(string memory baseTokenURI) public onlyOwner {
        _baseTokenURI = baseTokenURI;
    }

    /**
     * @dev setPaused makes the contract paused or unpaused
     */
    function setPaused(bool val) public onlyOwner {
        _paused = val;
    }

    /**
     * @dev withdraw sends all the ether in the contract
     * to the owner of the contract
     */
    function withdraw() public onlyOwner {
        address _owner = owner();
        uint256 amount = address(this).balance;
        (bool sent,) = _owner.call{value: amount}("");
        require(sent, "Failed to send Ether");
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC721Enumerable, ERC2981)
        returns (bool)
    {
        return interfaceId == type(IERC721Enumerable).interfaceId || super.supportsInterface(interfaceId);
    }
}
"
    },
