    "contracts/Unboxing.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";
import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";
import {ITerminus} from "./ITerminus.sol";
import {IInventory} from "./IInventory.sol";
import {CGHero} from "./CGHero.sol";
import {VILToken} from "./VILToken.sol";

contract Unboxing is Ownable, ReentrancyGuard, Pausable, ERC721Holder, ERC1155Holder {
    ITerminus terminus;
    IInventory inventory;
    CGHero public nft;
    VILToken public token;
    uint256 public discoveryBoxPoolID;
    uint256 public badgePoolID;
    uint256 public badgeSlot;

    constructor(
        address _CGTerminusAddress,
        address _inventoryAddress,
        address payable _cgNFTAddress,
        address payable _vilTokenAddress,
        uint256 _discoveryBoxPoolID,
        uint256 _badgePoolID,
        uint256 _badgeSlot
    ) {
        terminus = ITerminus(_CGTerminusAddress);
        inventory = IInventory(_inventoryAddress);
        nft = CGHero(_cgNFTAddress);
        token = VILToken(_vilTokenAddress);
        discoveryBoxPoolID = _discoveryBoxPoolID;
        badgePoolID = _badgePoolID;
        badgeSlot = _badgeSlot;
    }

    // Function to receive Ether. msg.data must be empty
    receive() external payable {}

    // Fallback function is called when msg.data is not empty
    fallback() external payable {}

    /**
     * @dev
     * 1. Burn Discovery Box
     * 2. Mint Badge
     * 3. Mint CG NFT
     * 4. Attach Badge to CG NFT inventory
     * 5. Send NFT to msg.sender
     * 6. Mint VIL Tokens and send to msg.sender
     */
    function unbox(uint256 numBoxes) public nonReentrant whenNotPaused returns (bool result) {
        require(terminus.balanceOf(msg.sender, discoveryBoxPoolID) >= numBoxes, "Not enough Discovery Boxes");

        // 1. Burn Discovery Box
        terminus.burn(msg.sender, discoveryBoxPoolID, numBoxes);

        // 2. Mint Badge
        terminus.mint(address(this), badgePoolID, numBoxes, "");

        for (uint256 i = 0; i < numBoxes; i++) {
            // 3. Mint NFT
            uint256 tokenId = nft.mint();
            // 4. Attach to NFT inventory
            inventory.equip(tokenId, badgeSlot, 1155, address(terminus), badgePoolID, 1);
            // 5. Send NFT to msg.sender
            nft.transferFrom(address(this), msg.sender, tokenId);
        }

        // 6. Mint Tokens
        uint8 decimals = token.decimals();
        uint256 numVILTokens = numBoxes * 50000000 * 10 ** decimals;
        token.mint(numVILTokens);
        token.approve(address(this), numVILTokens);

        // 7. Transfer Tokens to msg.sender
        bool tokenSent = token.transferFrom(address(this), msg.sender, numVILTokens);
        require(tokenSent, "Token transfer failed");

        return true;
    }

    function setTerminusAddress(address _terminusAddress) public onlyOwner {
        terminus = ITerminus(_terminusAddress);
    }

    function getTerminusAddress() public view onlyOwner returns (address) {
        return address(terminus);
    }

    function setInventoryAddress(address _inventoryAddress) public onlyOwner {
        inventory = IInventory(_inventoryAddress);
    }

    function getInventoryAddress() public view onlyOwner returns (address) {
        return address(inventory);
    }

    function setCGNFTAddress(address payable _cgNFTAddress) public onlyOwner {
        nft = CGHero(_cgNFTAddress);
    }

    function setVILTokenAddress(address payable _vilTokenAddress) public onlyOwner {
        token = VILToken(_vilTokenAddress);
    }

    function setDiscoveryBoxPoolID(uint256 _discoveryBoxPoolID) public onlyOwner {
        discoveryBoxPoolID = _discoveryBoxPoolID;
    }

    function setBadgePoolID(uint256 _badgePoolID) public onlyOwner {
        badgePoolID = _badgePoolID;
    }

    function setBadgeSlot(uint256 _badgeSlot) public onlyOwner {
        badgeSlot = _badgeSlot;
    }
}
"
    },
    "contracts/VILToken.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract VILToken is ERC20, Ownable {
    // the max total supply is 1000000000000 Tokens
    uint256 public constant maxTotalSupply = 1000000000000 * 10 ** 18;

    mapping(address => bool) whitelist;

    constructor() ERC20("Villain Coin", "VIL") {}

    // Function to receive Ether. msg.data must be empty
    receive() external payable {}

    // Fallback function is called when msg.data is not empty
    fallback() external payable {}

    function setWhitelist(address _whitelistAddress, bool approved) public onlyOwner {
        whitelist[_whitelistAddress] = approved;
    }

    function checkWhitelist(address _address) public view onlyOwner returns (bool _approved) {
        return whitelist[_address];
    }

    /**
     * @dev Mints `amount` number of tokens
     */
    function mint(uint256 amount) public payable {
        require(whitelist[msg.sender] == true || msg.sender == owner(), "Not authorized");
        // total tokens + amount <= maxTotalSupply, otherwise revert the transaction
        require((totalSupply() + amount) <= maxTotalSupply, "Exceeds the max total supply available.");
        // call the internal function from Openzeppelin's ERC20 contract
        _mint(msg.sender, amount);
    }

    /**
     * @dev withdraws all ETH sent to this contract
     * Requirements:
     * wallet connected must be owner's address
     */
    function withdraw() public onlyOwner {
        uint256 amount = address(this).balance;
        require(amount > 0, "Nothing to withdraw, contract balance empty");

        address _owner = owner();
        (bool sent,) = _owner.call{value: amount}("");
        require(sent, "Failed to send Ether");
    }
}
