// File: ibtcpx.sol


pragma solidity ^0.8.0;






interface IStaking {
    function stake(uint256 _amount, address _recipient) external returns (bool);
}

interface IStakingHelper {
    function stake(uint256 _amount, address _recipient) external;
}

interface IPRXYERC20 {
    function burnFrom(address account_, uint256 amount_) external;
    function mintByWhitelisted(address addr, uint256 amount) external;
}

contract iBTCpxToken is Ownable, ERC20Burnable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    address public daoWallet = 0x522817505E9c558a4629f47A29A881E3c1DA205d;
    address public staking;
    address public stakingHelper;

    uint256 public stakingStartDate;
    uint256 private totalStakeAmount;
    uint256 public lastUpdateTime;
    uint256 public rewardPerTokenStored;
    //15% = 1500
    uint256 private apy = 1500;
    uint256 public userCount;

    bool public stakingStatus;
    bool public useHelper;

    address public V2_ROUTER = 0xa5E0829CaCEd8fFDD4De3c43696c57F7D7A678ff;
    address public USDC = 0x2791Bca1f2de4661ED88A30C99A7a9449Aa84174;
    address public WBTC = 0x1BFD67037B42Cf73acF2047067bd4F2C47D9BfD6;
    address public PRXY = 0xab3D689C22a2Bb821f50A4Ff0F21A7980dCB8591;
    address public BTCpx = 0x9C32185b81766a051E08dE671207b34466DD1021;

    mapping(address => uint256) public userRewardPerTokenPaid;
    mapping(address => uint256) public rewards;
    mapping(uint256 => address) public userList;
    mapping(address => bool) internal isExisting;

    event RewardAdded(uint256 reward);
    event Staked(address indexed user, uint256 amount);
    event Unstaked(address indexed user, uint256 amount);
    event RewardPaid(address indexed user, uint256 reward);
    event RecoverToken(address indexed token, uint256 indexed amount);

    modifier updateReward(address account) {
        if (block.timestamp >= stakingStartDate) {
            rewardPerTokenStored = rewardPerToken();
            lastUpdateTime = lastTimeRewardApplicable();
            if (account != address(0)) {
                rewards[account] = earned(account);
                userRewardPerTokenPaid[account] = rewardPerTokenStored;
            }
        }
        _;
    }

    constructor(uint256 _stakingStartDate) ERC20("Interest Bearing BTCpx", "iBTCpx") {
        stakingStartDate = _stakingStartDate;
        stakingStatus = true;
    }

    function setStakingStatus(bool val) public onlyOwner {
        stakingStatus = val;
    }

    function updateStaking(address _staking, bool _helper) external onlyOwner {
        require( _staking != address(0));
        if (_helper) {
            useHelper = true;
            stakingHelper = _staking;
        } else {
            useHelper = false;
            staking = _staking;
        }
        IERC20(PRXY).safeApprove(_staking, 0);
        IERC20(PRXY).safeApprove(_staking, type(uint128).max);
    }

    function updateDaoWallet(address _daoWallet) external onlyOwner {
        daoWallet = _daoWallet;
    }

    function updateApyValue(uint256 _apy) external onlyOwner {
        apy = _apy;
    }

    function lastTimeRewardApplicable() public view returns (uint256) {
        return block.timestamp;
    }

    function rewardPerToken() public view returns (uint256) {
        if (block.timestamp < stakingStartDate) {
            return 0;
        }
        if (totalSupply() == 0) {
            return rewardPerTokenStored;
        }
        return
            rewardPerTokenStored.add(
                lastTimeRewardApplicable()
                    .sub(lastUpdateTime)
                    .mul(rewardRate())
                    .mul(1e18)
                    .div(totalSupply())
            );
    }

    function earned(address account) public view returns (uint256) {
        return
            stakeAmount(account)
                .mul(rewardPerToken().sub(userRewardPerTokenPaid[account]))
                .div(1e18)
                .add(rewards[account]);
    }

    function stake(uint256 amount) external updateReward(_msgSender()) {
        require(amount > 0, "Cannot stake 0");
        require(stakingStatus, "Staking is paused");
        if (!isUserExisting(_msgSender())) {
            userList[userCount] = _msgSender();
            userCount++;
            isExisting[_msgSender()] = true;
        }
        IERC20(BTCpx).safeTransferFrom(_msgSender(), daoWallet, amount);
        _mint(_msgSender(), amount);
        emit Staked(_msgSender(), amount);
    }

    function unstake(uint256 amount) public updateReward(_msgSender()) {
        require(amount > 0, "Cannot withdraw 0");
        _burn(_msgSender(), amount);
        IERC20(BTCpx).safeTransferFrom(daoWallet, _msgSender(), amount);
        emit Unstaked(_msgSender(), amount);
    }

    function claimAndStake() public updateReward(_msgSender()) {
        uint256 reward = earned(_msgSender());
        if (reward > 0) {
            rewards[_msgSender()] = 0;
            IPRXYERC20(PRXY).mintByWhitelisted(address(this), reward);
            if (useHelper) { // use if staking warmup is 0
                IStakingHelper(stakingHelper).stake(reward, _msgSender());
            } else {
                IStaking(staking).stake(reward, _msgSender());
            }
            emit RewardPaid(_msgSender(), reward);
        }
    }

    function getReward() public updateReward(_msgSender()) {
        uint256 reward = earned(_msgSender());
        if (reward > 0) {
            rewards[_msgSender()] = 0;
            IPRXYERC20(PRXY).mintByWhitelisted(_msgSender(), reward);
            emit RewardPaid(_msgSender(), reward);
        }
    }

    function airdrop(address[] memory _addr, uint256[] memory _amount) external onlyOwner {
        for(uint256 i = 0; i < _addr.length; i++) {
            mintByOwner(_addr[i], _amount[i]);
        }
    }

    function mintByOwner(address _addr, uint256 _amount) public onlyOwner {
        _mint(_addr, _amount);
    }

    function updateContractData(
        uint256 _rewardPerTokenStored,
        uint256 _lastUpdateTime
    ) external onlyOwner {
        rewardPerTokenStored = _rewardPerTokenStored;
        lastUpdateTime = _lastUpdateTime;
    }

    function migrate(
        address _user,
        uint256 _amount,
        uint256 _rewards,
        uint256 _userRewardPerTokenPaid
    ) external onlyOwner {
        if (!isUserExisting(_user)) {
            userList[userCount] = _user;
            userCount++;
            isExisting[_user] = true;
        }
        rewards[_user] = _rewards;
        userRewardPerTokenPaid[_user] = _userRewardPerTokenPaid;
        _mint(_user, _amount);
    }

    function getTotalRewardForYear() public view returns (uint256) {
        return
            apy
                .mul(totalSupply().mul(1e10))
                .mul(getBtcPriceInUsd())
                .div(getPrxyPriceInUsd())
                .div(10000);
    }

    function getOutputAmount(uint256 amountIn, address[] memory path)
        private
        view
        returns (uint256 _output)
    {
        uint256[] memory amounts = IUniswapV2Router01(V2_ROUTER).getAmountsOut(
            amountIn,
            path
        );
        return amounts[amounts.length - 1];
    }

    function getPrxyPriceInUsd() public view returns (uint256) {
        address[] memory path = new address[](2);
        path[0] = PRXY;
        path[1] = USDC;
        uint256[] memory amounts = IUniswapV2Router01(V2_ROUTER).getAmountsOut(
            1e18,
            path
        );
        return amounts[amounts.length - 1];
    }

    function getBtcPriceInUsd() public view returns (uint256) {
        address[] memory path = new address[](2);
        path[0] = WBTC;
        path[1] = USDC;
        uint256[] memory amounts = IUniswapV2Router01(V2_ROUTER).getAmountsOut(
            1e8,
            path
        );
        return amounts[amounts.length - 1];
    }

    function decimals() public pure override returns (uint8) {
        return 8;
    }

    function getTVLInUsd() external view returns (uint256) {
        return (totalSupply().mul(getBtcPriceInUsd())).div(1 * 10**(8));
    }

    //multiplied by 10**4 make take 6 dp
    function getAPY() external view returns (uint256) {
        return apy.mul(10**4);
    }

    function rewardRate() public view returns (uint256) {
        return getTotalRewardForYear().div(31536000);
    }

    function stakeAmount(address account) public view returns (uint256) {
        return balanceOf(account);
    }

    function rewardTokenDecimals() external view returns (uint8) {
        return IERC20Metadata(PRXY).decimals();
    }

    function stakeTokenDecimals() external view returns (uint8) {
        return IERC20Metadata(BTCpx).decimals();
    }

    function stakeToken() external view returns (address) {
        return BTCpx;
    }

    function rewardToken() external view returns (address) {
        return PRXY;
    }

    function isUserExisting(address _who) public view returns (bool) {
        return isExisting[_who];
    }

    function recoverExcessToken(address token, uint256 amount)
        external
        onlyOwner
    {
        IERC20(token).safeTransfer(owner(), amount);
        emit RecoverToken(token, amount);
    }

    function recoverETH() external onlyOwner {
        payable(owner()).transfer(address(this).balance);
    }
}
