    function mintBatchOneUriUnique(
        address to, 
        uint256 nrtomint, 
        string memory URI,
        bytes memory data
    ) public {
        uint256[] memory tokenIds = new uint256[](nrtomint);
        uint256[] memory amounts = new uint256[](nrtomint);
        for (uint256 j = 0; j < nrtomint; j++) {
            uint256 tokenId = generateTokenId();
            tokenIds[j] = tokenId;
            amounts[j] = 1;
            tokenURIs[tokenId] = URI;
        }
        _mintBatch(to, tokenIds, amounts, data);
    }

    function mintBatchMultipleRecipients(
        address[] memory to,
        uint256[] memory amounts,
        string[] memory URIs,
        uint256[] memory numRecipientsPerToken,
        bytes memory data
    ) external {
        require(to.length == amounts.length, "to and amounts length mismatch");
        require(to.length == URIs.length, "to and URIs length mismatch");
        require(to.length == numRecipientsPerToken.length, "to and numRecipientsPerToken length mismatch");

        uint256 counter = 0;
        for (uint256 i = 0; i < numRecipientsPerToken.length; i++) {
            uint256 tokenId = generateTokenId();
            tokenURIs[tokenId] = URIs[i];

            for (uint256 j = 0; j < numRecipientsPerToken[i]; j++) {
                _mint(to[counter], tokenId, amounts[counter], data);
                counter++;
            }
        }
    }

    function _beforeTokenTransfer(
        address operator, 
        address from, 
        address to, 
        uint256[] memory ids, 
        uint256[] memory amounts, 
        bytes memory data
    ) internal whenNotPaused override(ERC1155, ERC1155Supply) {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
    }

    //Generate next token ID
    function generateTokenId() internal returns (uint256) {
        return nextTokenId++;
    }
}
