    "contracts/pearl/Airdrop.sol": {
      "content": "//SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.4;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@magiceden-oss/erc721m/contracts/IERC721M.sol";

contract Airdrop is AccessControl, ReentrancyGuard {

    bytes32 public constant AIRDROP_ROLE = keccak256("AIRDROP_ROLE");

    constructor() {
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(AIRDROP_ROLE, _msgSender());
    }

    function withdrawEth() external onlyRole(DEFAULT_ADMIN_ROLE) {
        payable(msg.sender).transfer(address(this).balance);
    }

    function batchTransferErc721M(address holder, address collection, address[] memory recipients, uint256[] memory amounts) external onlyRole(AIRDROP_ROLE) {

        uint256[] memory ownerTokens = IERC721M(collection).tokensOfOwner(holder);

        uint256 t = 0;
        for (uint i = 0; i < recipients.length; i++) {
            for (uint  j = 0; j < amounts[i]; j++) {
                if (t < ownerTokens.length) {
                    IERC721M(collection).transferFrom(holder, recipients[i], ownerTokens[t]);
                    t++;
                }
            }
        }
    }
}"
