    "contracts/DropCustomCollection.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.21;

import "@openzeppelin/contracts-upgradeable/access/extensions/AccessControlEnumerableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/common/ERC2981Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC1155/extensions/ERC1155SupplyUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC1155/utils/ERC1155HolderUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "./interfaces/IDropCustomCollection.sol";

/**
 * @title DropCustomCollection
 * @dev This allows artists or labels to create their own release tokens
 */

contract DropCustomCollection is
    IDropCustomCollection,
    ERC1155SupplyUpgradeable,
    ERC1155HolderUpgradeable,
    AccessControlEnumerableUpgradeable,
    ERC2981Upgradeable,
    ReentrancyGuardUpgradeable
{
    /// State Variables

    string public name;
    string public symbol;

    uint256 public numberOfReleases;

    /// @dev tokenId => uri
    mapping(uint256 => string) _uris;

    // only release role can mint tokens
    bytes32 public constant RELEASE_ADMIN_ROLE =
        keccak256("RELEASE_ADMIN_ROLE");

    /// Errors
    error CannotBeZeroAddress();
    error InvalidAmount();
    error FieldCannotBeEmpty(string field);
    error InvalidTokenId();

    /**
     * @dev Constructor
     * @param isInitializerDisabled Whether the initializer is disabled or not
     */
    constructor(bool isInitializerDisabled) {
        if (isInitializerDisabled) {
            _disableInitializers();
        }
    }

    /// External Functions
    /**
     * @dev Initializes the contract
     * @param admin The address that will be given the role of default admin. See {AccessControl}
     * @param releaseAdmins An array of addresses that will be given the role of release admin
     * @param name_ The name of the custom collection
     * @param symbol_ The symbol of the custom collection
     */
    function initialize(
        address admin,
        address[] calldata releaseAdmins,
        string calldata name_,
        string calldata symbol_
    ) external initializer {
        __ERC1155_init("");
        if (admin == address(0)) revert CannotBeZeroAddress();
        if (bytes(name_).length == 0) revert FieldCannotBeEmpty("name");
        if (bytes(symbol_).length == 0) revert FieldCannotBeEmpty("symbol");
        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        for (uint8 i = 0; i < releaseAdmins.length; i++) {
            _grantRole(RELEASE_ADMIN_ROLE, releaseAdmins[i]);
        }
        name = name_;
        symbol = symbol_;
    }

    /**
     * @dev Creates a new release.
     * @notice Only release admins can call this function
     * @param receiver The address that will receive the release token
     * @param beneficiary The address that will receive royalties on each sale
     * @param royaltyAmount The percentage of sale prices
     * that should be paid to the beneficiary for re-sales. The royalty amount must be less than
     * or equal to 20%. This is calculated by <NOMINATOR> / 10,000. e.g. For 10% royalties, pass in 1000
     * @param uri_ The URI of the token metadata
     */
    function createRelease(
        address receiver,
        address beneficiary,
        uint96 royaltyAmount,
        string calldata uri_,
        uint256 amount
    ) external nonReentrant onlyRole(RELEASE_ADMIN_ROLE) {
        if (receiver == address(0)) revert CannotBeZeroAddress();
        if (beneficiary == address(0)) revert CannotBeZeroAddress();
        if (royaltyAmount > 2_000) revert InvalidAmount();
        numberOfReleases++;
        _uris[numberOfReleases] = uri_;
        _setTokenRoyalty(numberOfReleases, beneficiary, royaltyAmount);
        _mint(receiver, numberOfReleases, amount, "");
    }

    /**
     * @dev Withdraws a Release. Potentially a release can be minted to
     * the contract address, this function allows the release to be withdrawn.
     * @notice Only the release admin can call this function
     * @notice This function bypasses the approval check, the receiver address
     * does not need to be approved to receive the release token
     * @param receiver The address that will receive the release token
     * @param tokenId The ID of the release token
     * @param amount The amount of release tokens to withdraw
     */
    function withdrawRelease(
        address receiver,
        uint256 tokenId,
        uint256 amount
    ) external onlyRole(RELEASE_ADMIN_ROLE) nonReentrant {
        _requireTokenIdExists(tokenId);
        _safeTransferFrom(address(this), receiver, tokenId, amount, "");
        emit ReleaseWithdrawn(msg.sender, tokenId, amount);
    }

    /**
     * @dev Sets the metadata URI for a release, only the default admin can call this
     * @param tokenId The ID of the token
     * @param uri_ The URI for the token
     */
    function setUri(
        uint256 tokenId,
        string calldata uri_
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _requireTokenIdExists(tokenId);
        _uris[tokenId] = uri_;
        emit URI(uri_, tokenId);
    }

    /// Public Functions

    /**
     * @dev See {IERC1155-uri}.
     */
    function uri(uint256 tokenId) public view override returns (string memory) {
        _requireTokenIdExists(tokenId);
        return _uris[tokenId];
    }

    /**
     * @dev See {IERC165-supportsInterface}.
     */
    function supportsInterface(
        bytes4 interfaceId
    )
        public
        view
        virtual
        override(
            ERC1155Upgradeable,
            ERC2981Upgradeable,
            AccessControlEnumerableUpgradeable,
            ERC1155HolderUpgradeable
        )
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    /// Internal

    /**
     * @dev Checks the token id exists
     * @param tokenId The ID of the token
     */
    function _requireTokenIdExists(uint256 tokenId) internal view {
        if (tokenId > numberOfReleases) revert InvalidTokenId();
    }
}
"
    },
    "contracts/interfaces/IDropCustomCollection.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.21;

/**
 * @title IDropCustomCollection
 * @dev Interface for DropCustomCollection
 */
interface IDropCustomCollection {
    event ReleaseWithdrawn(
        address indexed receiver,
        uint256 tokenId,
        uint256 amount
    );

    /**
     * @dev Initializes the contract
     * @param admin The address that will be given the role of default admin. See {AccessControl}
     * @param releaseAdmins An array of addresses that will be given the role of release admin
     * @param name_ The name of the custom collection
     * @param symbol_ The symbol of the custom collection
     */
    function initialize(
        address admin,
        address[] memory releaseAdmins,
        string memory name_,
        string memory symbol_
    ) external;

    /**
     * @dev Creates a new release. Called to create a release when the contract is
     * private. A check is made to ensure that the track does not have a status of Invalidated
     * @notice Only release admins can call this function
     * @param receiver The address that will receive the release token
     * @param beneficiary The address that will receive royalties on each sale
     * @param royaltyAmount The percentage of sale prices
     * that should be paid to the beneficiary for re-sales.
     *  Calculated by <NOMINATOR> / 10,000. e.g. For 10% royalties, pass in 100
     * @param uri_ The URI of the token metadata
     */
    function createRelease(
        address receiver,
        address beneficiary,
        uint96 royaltyAmount,
        string calldata uri_,
        uint256 amount
    ) external;

    /**
     * @dev Withdraws a Release. Potentially a release can be minted to
     * the contract address, this function allows the release to be withdrawn.
     * @notice Only the release admin can call this function
     * @param receiver The address that will receive the release token
     * @param tokenId The ID of the release token
     * @param amount The amount of release tokens to withdraw
     */
    function withdrawRelease(
        address receiver,
        uint256 tokenId,
        uint256 amount
    ) external;

    /**
     * @dev Sets the metadata URI for a release, only the default admin can call this
     * @param tokenId The ID of the token
     * @param uri_ The URI for the token
     */
    function setUri(uint256 tokenId, string calldata uri_) external;
}
