pragma solidity 0.8.21;

interface IController {

   /* ============ Custom Errors ============ */

    /// @notice Emitted when the number of resources and resourceIds mismatch.
    error Controller__ArrayLengthMismatch(uint256 resourcesLength, uint256 resourcesIdsLength);
    /// @notice Emitted when the caller is not a factory.
    error Controller__CallerNotFactory(address caller);
    /// @notice Emitted when a factory already exists on the Controller.
    error Controller__FactoryExists();
    /// @notice Emitted when a factory does not exist on the Controller.
    error Controller__FactoryInvalid();
    /// @notice Emitted when a fee type already exists on the Controller.
    error Controller__FeeTypeExists();
    /// @notice Emitted when a fee type does not exist on the Controller.
    error Controller__FeeTypeInvalid();
    /// @notice Emitted when the Controller is already initialized.
    error Controller__IsInitialized();
    /// @notice Emitted when a module is already enabled on the Controller.
    error Controller__ModuleExists();
    /// @notice Emitted when a module is not enabled on the Controller.
    error Controller__ModuleInvalid();
    /// @notice Emitted when a resource or resourceId is already enabled on the Controller.
    error Controller__ResourceExists();
    /// @notice Emitted when a resource is not enabled on the Controller.
    error Controller__ResourceInvalid();
    /// @notice Emitted when a SetToken is already enabled on the Controller. 
    error Controller__SetExists();
    /// @notice Emitted when a SetToken is not enabled on the Controller.
    error Controller__SetInvalid();
    /// @notice Emitted when the Controller is not initialized.
    error Controller__Uninitialized();
    /// @notice Emitted when a zero address is provided.
    error Controller__ZeroAddress();

   /* ============ Functions ============ */

    function addSet(address _setToken) external;
    function feeRecipient() external view returns(address);
    function getModuleFee(address _module, uint256 _feeType) external view returns(uint256);
    function isModule(address _module) external view returns(bool);
    function isSet(address _setToken) external view returns(bool);
    function isSystemContract(address _contractAddress) external view returns (bool);
    function resourceId(uint256 _id) external view returns(address);
}"
