pragma solidity 0.8.21;

interface ISetTokenCreator {

   /* ============ Custom Errors ============ */

    /// @notice Emitted when the number of components and units mismatch.
    error SetTokenCreator__ArrayLengthMismatch(uint256 componentsLength, uint256 unitsLength);
    /// @notice Emitted when the components provided contains duplicates.
    error SetTokenCreator__ComponentsContainDuplicates();
    /// @notice Emitted when the components provided contains a zero address.
    error SetTokenCreator__ComponentsContainZeroAddress();
    /// @notice Emitted when an the components provided does not contain elements.
    error SetTokenCreator__ComponentsEmpty();
    /// @notice Emitted when a module is not enabled on the Controller.
    error SetTokenCreator__ModuleInvalid(address module);
    /// @notice Emitted when the modules provided does not contain elements.
    error SetTokenCreator__ModulesEmpty();
    /// @notice Emitted when a unit provided is less than or equal to zero.
    error SetTokenCreator__UnitsLessThanOrEqualToZero(int256 unit, uint256 positionInArray);
    /// @notice Emitted when a zero address is provided.
    error SetTokenCreator__ZeroAddress();

   /* ============ Functions ============ */

    function create(
        address[] memory _components,
        int256[] memory _units,
        address[] memory _modules,
        address _manager,
        string memory _name,
        string memory _symbol
    )
        external
        returns (address);
}
"
