pragma solidity 0.8.21;

import { ISetToken } from "@amun/amun-protocol/contracts/interfaces/ISetToken.sol";

interface IDelegatedManager {
    /* ============ Custom Errors ============ */

    /// @notice Emitted when the asset was already added to the allow list.
    error DelegatedManager__AssetExists(address asset);
    /// @notice Emitted when the asset does not exist on the allow list.
    error DelegatedManager__AssetNotAllowed();
    /// @notice Emitted when the caller is not the methodologist.
    error DelegatedManager__CallerNotMethodologist(address caller, address methodologist);
    /// @notice Emitted when an extension tries to call/interact with a SetToken on the DelegatedManager.
    error DelegatedManager__CallingSetTokenForbidden();
    /// @notice Emitted when the extension was already added to the allow list.
    error DelegatedManager__ExtensionExists(address extension);
    /// @notice Emitted when the extension is not in the pending state.
    error DelegatedManager__ExtensionNotPending(address extension);
    /// @notice Emitted when the extension is not in the initialized state on the allow list.
    error DelegatedManager__ExtensionUninitialized(address extension);
    /// @notice Emitted when there are existing extensions on the DelegatedManager.
    error DelegatedManager__ExtensionsExist(uint256 numExtensions);
    /// @notice Emitted when the new fee split amount is invalid.
    error DelegatedManager__FeeSplitInvalid();
    /// @notice Emitted when the operator was already added to the allow list.
    error DelegatedManager__OperatorExists(address operator);
    /// @notice Emitted when the operator does not exist on the allow list.
    error DelegatedManager__OperatorNotAllowed(address operator);
    /// @notice Emitted when a zero address is provided.
    error DelegatedManager__ZeroAddress();


    /* ============ Events ============ */

    event MethodologistChanged(
        address indexed _newMethodologist
    );

    event ExtensionAdded(
        address indexed _extension
    );

    event ExtensionRemoved(
        address indexed _extension
    );

    event ExtensionInitialized(
        address indexed _extension
    );

    event OperatorAdded(
        address indexed _operator
    );

    event OperatorRemoved(
        address indexed _operator
    );

    event AllowedAssetAdded(
        address indexed _asset
    );

    event AllowedAssetRemoved(
        address indexed _asset
    );

    event UseAssetAllowlistUpdated(
        bool _status
    );

    event OwnerFeeSplitUpdated(
        uint256 _newFeeSplit
    );

    event OwnerFeeRecipientUpdated(
        address indexed _newFeeRecipient
    );

    /* ============ Functions ============ */

    function interactManager(address _module, bytes calldata _encoded) external;

    function initializeExtension() external;

    function transferTokens(address _token, address _destination, uint256 _amount) external;

    function updateOwnerFeeSplit(uint256 _newFeeSplit) external;

    function updateOwnerFeeRecipient(address _newFeeRecipient) external;

    function setMethodologist(address _newMethodologist) external;

    function transferOwnership(address _owner) external;

    function SET_TOKEN() external view returns(ISetToken); /* solhint-disable-line func-name-mixedcase */
    function owner() external view returns(address);
    function methodologist() external view returns(address);
    function operatorAllowlist(address _operator) external view returns(bool);
    function assetAllowlist(address _asset) external view returns(bool);
    function useAssetAllowlist() external view returns(bool);
    function isAllowedAsset(address _asset) external view returns(bool);
    function isPendingExtension(address _extension) external view returns(bool);
    function isInitializedExtension(address _extension) external view returns(bool);
    function getExtensions() external view returns(address[] memory);
    function getOperators() external view returns(address[] memory);
    function getAllowedAssets() external view returns(address[] memory);
    function ownerFeeRecipient() external view returns(address);
    function ownerFeeSplit() external view returns(uint256);
}"
    },
