pragma solidity 0.8.21;

interface IGlobalExtension {
    /* ============ Custom Errors ============ */

    /// @notice Emitted when an asset is not approved on the DelegatedManager.
    error GlobalExtension__AssetForbidden(address emitter, address asset);
    /// @notice Emitted when the caller is not an approved operator.
    error GlobalExtension__CallerNotApprovedOperator(address emitter, address caller);
    /// @notice Emitted when the caller is not an externally owned account.
    error GlobalExtension__CallerNotEOA(address emitter, address msgSender, address txOrigin);
    /// @notice Emitted when the caller is not enabled on the ManagerCore.
    error GlobalExtension__CallerNotManagerOnCore(address emitter, address caller);
    /// @notice Emitted when the caller is not the specified methodologist.
    error GlobalExtension__CallerNotMethodologist(
        address emitter, 
        address caller, 
        address methodologist
    );
    /// @notice Emitted when the caller is not the specified manager of the SetToken.
    error GlobalExtension__CallerNotSetTokenManager(
        address emitter, 
        address caller, 
        address manager
    );
    /// @notice Emitted when the caller is not the specified owner.
    error GlobalExtension__CallerNotOwner(
        address emitter, 
        address caller, 
        address owner
    );
    /// @notice Emitted when the extension is not initialized.
    error GlobalExtension__ExtensionUninitialized(address emittingExtension);
    /// @notice Emitted when the extension is not pending.
    error GlobalExtension__ExtensionNotPending(address emittingExtension);

    /* ============ Events ============ */

    event ExtensionRemoved(
        address indexed _setToken,
        address indexed _delegatedManager
    );

    /* ============ Functions ============ */

    function removeExtension() external;
}"
