pragma solidity 0.8.21;

interface IManagerCore {
    /* ============ Custom Errors ============ */

    /// @notice Emitted when the caller is not a valid factory.
    error ManagerCore__CallerNotFactory(address caller);
    /// @notice Emitted when the ManagerCore is already initialized.
    error ManagerCore__ContractIsInitialized();
    /// @notice Emitted when performing an operation before initialization of the ManagerCore.
    error ManagerCore__ContractUninitialized();
    /// @notice Emitted when an extension already exists on the ManagerCore.
    error ManagerCore__ExtensionExists();
    /// @notice Emitted when the provided extension is not valid.
    error ManagerCore__ExtensionInvalid();
    /// @notice Emitted when the factory already exists on the ManagerCore.
    error ManagerCore__FactoryExists();
    /// @notice Emitted when the provided factory is not valid.
    error ManagerCore__FactoryInvalid();
    /// @notice Emitted when the manager already exists on the ManagerCore.
    error ManagerCore__ManagerExists();
    /// @notice Emitted when the provided manager is not valid.
    error ManagerCore__ManagerInvalid();
    /// @notice Emitted when a zero address is provided.
    error ManagerCore__ZeroAddress();


    /* ============ Events ============ */

    event ExtensionAdded(address indexed _extension);
    event ExtensionRemoved(address indexed _extension);
    event FactoryAdded(address indexed _factory);
    event FactoryRemoved(address indexed _factory);
    event ManagerAdded(address indexed _manager, address indexed _factory);
    event ManagerRemoved(address indexed _manager);

    /* ============ Functions ============ */

    function addManager(address _manager) external;
    function isExtension(address _extension) external view returns(bool);
    function isFactory(address _factory) external view returns(bool);
    function isManager(address _manager) external view returns(bool);
    function owner() external view returns(address);
}"
