    "contracts/Staking/RewardProgram.sol": {
      "content": "// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity ^0.8.18;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "../Security2/Security2Base.sol";

contract RewardProgram is Security2Base, ReentrancyGuard {
    using ECDSALib for bytes;
    using SafeERC20 for IERC20;

    IERC20 private token;

    //Minimum staking period in seconds (1 hour)
    uint256 private MIN_STAKE_TIME = 60 minutes;

    // Events
    event Staked(address indexed user, uint256 amount, uint256 timestamp);
    event Unstaked(address indexed user, uint256 amount, uint256 timestamp);

    // Struct to store user's staking data
    struct StakerData {
        uint256 totalStaked;
        uint256 lastStakedTimestamp;
    }

    mapping(address => StakerData) private stakers;

    constructor(
        IERC20 _token,
        IECDSASignature2 _signature,
        IUAC _uac
    ) Security2Base(_signature, _uac) {
        require(
            address(_token) != address(0),
            "The address of the token cannot be 0"
        );
        token = _token;
    }

    function totalStaked() external view returns (uint256) {
        return token.balanceOf(address(this));
    }

    function stake(uint256 amount) external nonReentrant {
        uac.verifyAll(msg.sender, 2);

        require(amount > 0, "Amount must be greater than 0");
        token.safeTransferFrom(msg.sender, address(this), amount);

        // Update staker's data
        StakerData storage staker = stakers[msg.sender];
        staker.totalStaked += amount;
        staker.lastStakedTimestamp = block.timestamp;

        // Emit Staked event
        emit Staked(msg.sender, amount, block.timestamp);
    }

    function unstake(uint256 amount) external nonReentrant {
        uac.verifyAll(msg.sender, 2);

        StakerData storage staker = stakers[msg.sender];
        require(staker.totalStaked >= amount, "Not enough staked tokens");
        require(
            block.timestamp >= staker.lastStakedTimestamp + MIN_STAKE_TIME,
            "Minimum staking time not met"
        );

        // Update staker's data
        staker.totalStaked = staker.totalStaked - amount;

        token.safeTransfer(msg.sender, amount);

        // Emit Unstaked event
        emit Unstaked(msg.sender, amount, block.timestamp);
    }

    function emergencyWithdraw(
        uint256 nonce,
        uint256 timestamp,
        bytes[] memory signatures
    ) external {
        signature.verifyMessage(
            abi.encodePacked(msg.sender).hash(),
            nonce,
