    "src/auth/RolesAuthority.sol": {
      "content": "// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity >=0.8.0;

import {Auth, Authority} from "./Auth.sol";

/// @notice Role based Authority that supports up to 256 roles.
/// @author Solmate (https://github.com/transmissions11/solmate/blob/main/src/auth/authorities/RolesAuthority.sol)
/// @author Modified from Dappsys (https://github.com/dapphub/ds-roles/blob/master/src/roles.sol)
contract RolesAuthority is Auth, Authority {
    /*//////////////////////////////////////////////////////////////
                                 EVENTS
    //////////////////////////////////////////////////////////////*/

    event UserRoleUpdated(
        address indexed user,
        uint8 indexed role,
        bool enabled
    );

    event PublicCapabilityUpdated(
        address indexed target,
        bytes4 indexed functionSig,
        bool enabled
    );

    event RoleCapabilityUpdated(
        uint8 indexed role,
        address indexed target,
        bytes4 indexed functionSig,
        bool enabled
    );

    /*//////////////////////////////////////////////////////////////
                               INITIALIZER
    //////////////////////////////////////////////////////////////*/

    function RolesAuthority_init(
        address _owner,
        Authority _authority
    ) internal onlyInitializing {
        Auth_init(_owner, _authority);
    }

    /*//////////////////////////////////////////////////////////////
                            ROLE/USER STORAGE
    //////////////////////////////////////////////////////////////*/

    mapping(address => bytes32) public getUserRoles;

    mapping(address => mapping(bytes4 => bool)) public isCapabilityPublic;

    mapping(address => mapping(bytes4 => bytes32))
        public getRolesWithCapability;

    function doesUserHaveRole(
        address user,
        uint8 role
    ) public view virtual returns (bool) {
        return (uint256(getUserRoles[user]) >> role) & 1 != 0;
    }

    function doesRoleHaveCapability(
        uint8 role,
        address target,
        bytes4 functionSig
    ) public view virtual returns (bool) {
        return
            (uint256(getRolesWithCapability[target][functionSig]) >> role) &
                1 !=
            0;
    }

    /*//////////////////////////////////////////////////////////////
                           AUTHORIZATION LOGIC
    //////////////////////////////////////////////////////////////*/

    function canCall(
        address user,
        address target,
        bytes4 functionSig
    ) public view virtual override returns (bool) {
        return
            isCapabilityPublic[target][functionSig] ||
            bytes32(0) !=
            getUserRoles[user] & getRolesWithCapability[target][functionSig];
    }

    /*//////////////////////////////////////////////////////////////
                   ROLE CAPABILITY CONFIGURATION LOGIC
    //////////////////////////////////////////////////////////////*/

    function setPublicCapability(
        address target,
        bytes4 functionSig,
        bool enabled
    ) public virtual requiresAuth {
        _setPublicCapability(target, functionSig, enabled);
    }

    function setRoleCapability(
        uint8 role,
        address target,
        bytes4 functionSig,
        bool enabled
    ) public virtual requiresAuth {
        _setRoleCapability(role, target, functionSig, enabled);
    }

    /*//////////////////////////////////////////////////////////////
                       USER ROLE ASSIGNMENT LOGIC
    //////////////////////////////////////////////////////////////*/

    function setUserRole(
        address user,
        uint8 role,
        bool enabled
    ) public virtual requiresAuth {
        _setUserRole(user, role, enabled);
    }

    /*//////////////////////////////////////////////////////////////
                             INTERNAL
    //////////////////////////////////////////////////////////////*/

    function _setPublicCapability(
        address target,
        bytes4 functionSig,
        bool enabled
    ) internal {
        isCapabilityPublic[target][functionSig] = enabled;

        emit PublicCapabilityUpdated(target, functionSig, enabled);
    }

    function _setRoleCapability(
        uint8 role,
        address target,
        bytes4 functionSig,
        bool enabled
    ) internal {
        if (enabled) {
            getRolesWithCapability[target][functionSig] |= bytes32(1 << role);
        } else {
            getRolesWithCapability[target][functionSig] &= ~bytes32(1 << role);
        }

        emit RoleCapabilityUpdated(role, target, functionSig, enabled);
    }

    function _setUserRole(address user, uint8 role, bool enabled) internal {
        if (enabled) {
            getUserRoles[user] |= bytes32(1 << role);
        } else {
            getUserRoles[user] &= ~bytes32(1 << role);
        }

        emit UserRoleUpdated(user, role, enabled);
    }
}
"
    },
    "src/Badge.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.18;
import {ERC721} from "lib/solmate/src/tokens/ERC721.sol";
import {Initializable} from "lib/openzeppelin-contracts/contracts/proxy/utils/Initializable.sol";
import {RolesAuthority} from "./auth/RolesAuthority.sol";
import {Authority, Auth} from "./auth/Auth.sol";
import {MetaTransactionContext} from "./metaTx/MetaTransactionContext.sol";
import {EIP712BaseUpgradeable} from "./eip712/EIP712BaseUpgradeable.sol";
import "lib/solmate/src/utils/MerkleProofLib.sol";
import {Multicall} from "lib/openzeppelin-contracts/contracts/utils/Multicall.sol";
import {Address} from "lib/openzeppelin-contracts/contracts/utils/Address.sol";

error INVALID_PROOF();

contract Badge is
    Initializable,
    MetaTransactionContext,
    Auth,
    RolesAuthority,
    EIP712BaseUpgradeable,
    Multicall
{
    /*//////////////////////////////////////////////////////////////
                                 EVENTS
    //////////////////////////////////////////////////////////////*/

    event Transfer(
        address indexed from,
        address indexed to,
        uint256 indexed id
    );

    event Approval(
        address indexed owner,
        address indexed spender,
        uint256 indexed id
    );

    event ApprovalForAll(
        address indexed owner,
        address indexed operator,
        bool approved
    );

    event BadgeUpdated(
        uint256 tokenId, 
        uint8 tier,
        string metadata, 
        uint256 data
    );

    event MetadataUpdate(
        uint256 _tokenId
    );
    /*//////////////////////////////////////////////////////////////
                         Auth Structs
    //////////////////////////////////////////////////////////////*/

    struct UserRole {
        uint8 role;
        bool enabled;
        address account;
    }

    struct RoleCapability {
        uint8 role;
        bytes4 functionSig;
        bool enabled;
    }

    struct PublicCapability {
        bytes4 functionSig;
        bool enabled;
    }

    struct RolesAuthorityStruct {
        UserRole[] userRoles;
        RoleCapability[] roleCapabilities;
        PublicCapability[] publicCapabilities;
    }

    /*//////////////////////////////////////////////////////////////
                         GLOBAL VARIABLES
    //////////////////////////////////////////////////////////////*/

    uint256 public MAX_TOKENS_PER_ADDRESS;

    /*//////////////////////////////////////////////////////////////
                         METADATA STORAGE/LOGIC
    //////////////////////////////////////////////////////////////*/

    string public name;

    string public symbol;

    uint256 public tokenCounter;

    mapping(uint256 => string) internal _tokenURI;
    function _baseURI() internal view virtual returns (string memory) {
        return "http://arweave.net/";
    }

    string public contractUri;

    function tokenURI(uint256 id) public view returns (string memory) {
        require(_ownerOf[id] != address(0), "NOT_MINTED");
        string memory _metadataHash = _tokenURI[id];
        string memory base = _baseURI();
        return string(abi.encodePacked(base, _metadataHash));
    }

    function setContractUri(string memory newContractUri) public requiresAuth {
        contractUri = newContractUri;
    }

    /*//////////////////////////////////////////////////////////////
                      ERC721 BALANCE/OWNER STORAGE
    //////////////////////////////////////////////////////////////*/

    mapping(uint256 => address) internal _ownerOf;

    mapping(address => uint256) internal _balanceOf;

    mapping(uint256 => uint8) internal _tierOf;

    mapping(uint256 => uint256) internal _dataOf;

    function ownerOf(uint256 id) public view virtual returns (address owner) {
        require((owner = _ownerOf[id]) != address(0), "NOT_MINTED");
    }

    function balanceOf(address owner) public view virtual returns (uint256) {
        require(owner != address(0), "ZERO_ADDRESS");
        return _balanceOf[owner];
    }

    function tierOf(uint256 id) public view virtual returns (uint8 tier) {
        require(_ownerOf[id] != address(0), "NOT_MINTED");
        tier = _tierOf[id];
    }

    function dataOf(uint256 id) public view virtual returns (uint256 data) {
        require(_ownerOf[id] != address(0), "NOT_MINTED");
        data = _dataOf[id];
    }

    function totalSupply() public view returns(uint256) {
        return tokenCounter;
    }

    /*//////////////////////////////////////////////////////////////
                            MERKLE ROOTS
    //////////////////////////////////////////////////////////////*/

    mapping(uint8 => bytes32) public rootOfTier;

    mapping(address => bool) public merkleClaimed;

    function setMerkleRoot(uint8 tier, bytes32 root) public requiresAuth {
        rootOfTier[tier] = root;
    }

    /*//////////////////////////////////////////////////////////////
                            ECDSA Vouchers
    //////////////////////////////////////////////////////////////*/

    struct Voucher {
        uint8 tier;
        address to;
        string metadata;
        uint256 data;
        uint256 nonce;
    }

    bytes32 constant Voucher_TYPEHASH =
        keccak256(
            "Voucher(uint8 tier,address to,string metadata,uint256 data,uint256 nonce)"
        );

    function _hashVoucher(
        Voucher memory voucher
    ) internal pure returns (bytes32) {
        return
            keccak256(
                abi.encode(
                    Voucher_TYPEHASH,
                    voucher.tier,
                    voucher.to,
                    voucher.metadata,
                    voucher.data,
                    voucher.nonce
                )
            );
    }

    mapping(address => uint256) public getLastNonceOf;

    /*//////////////////////////////////////////////////////////////
                         ERC721 APPROVAL STORAGE
    //////////////////////////////////////////////////////////////*/

    mapping(uint256 => address) public getApproved;

    mapping(address => mapping(address => bool)) public isApprovedForAll;

    /*//////////////////////////////////////////////////////////////
                               INITIALIZER
    //////////////////////////////////////////////////////////////*/

    function initialize(
        string memory _name,
        string memory _symbol,
        address _owner,
        address _forwarder,
        RolesAuthorityStruct memory rolesAuthorityStruct
    ) external initializer {
        name = _name;
        symbol = _symbol;
        MetaTransactionContext_init(_forwarder);
        RolesAuthority_init(_owner, Authority(address(this)));
        MAX_TOKENS_PER_ADDRESS = 1;
        _setUpAuth(rolesAuthorityStruct);
    }

    function setForwarder(address _forwarder) public requiresAuth {
        _setTrustedForwarder(_forwarder);
    }
    /*//////////////////////////////////////////////////////////////
                              AUTH LOGIC
    //////////////////////////////////////////////////////////////*/

    function _setUpAuth(
        RolesAuthorityStruct memory rolesAuthorityStruct
    ) internal {
        for (uint i = 0; i < rolesAuthorityStruct.userRoles.length; i++) {
            _setUserRole(
                rolesAuthorityStruct.userRoles[i].account,
                rolesAuthorityStruct.userRoles[i].role,
                rolesAuthorityStruct.userRoles[i].enabled
            );
        }

        for (uint i = 0; i < rolesAuthorityStruct.roleCapabilities.length; i++) {
            _setRoleCapability(
                rolesAuthorityStruct.roleCapabilities[i].role,
                address(this),
                rolesAuthorityStruct.roleCapabilities[i].functionSig,
                rolesAuthorityStruct.roleCapabilities[i].enabled
            );
        }

        for (uint i = 0; i < rolesAuthorityStruct.publicCapabilities.length; i++) {
            _setPublicCapability(
                address(this),
                rolesAuthorityStruct.publicCapabilities[i].functionSig,
                rolesAuthorityStruct.publicCapabilities[i].enabled
            );
        }
    }

    /*//////////////////////////////////////////////////////////////
                              REP3 LOGIC
    //////////////////////////////////////////////////////////////*/
    function setMaxTokensPerAddress(
        uint256 _maxTokensPerAddress
    ) public requiresAuth {
        require(
            _maxTokensPerAddress != 0,
            "Max tokens per address can not be zero"
        );
        MAX_TOKENS_PER_ADDRESS = _maxTokensPerAddress;
    }

    function mint(
        address account,
        string memory metadata,
        uint8 tier,
        uint256 data
    ) public requiresAuth {
        require(
            balanceOf(account) < MAX_TOKENS_PER_ADDRESS,
            "Exceeding max tokens per address"
        );
        uint256 tokenId = tokenCounter;
        _tierOf[tokenId] = tier;
        _tokenURI[tokenId] = metadata;
        _dataOf[tokenId] = data;
        tokenCounter++;
        _safeMint(account, tokenId);
    }

    function claimMerkle(
        uint8 tier,
        uint256 data,
        string calldata metadata,
        bytes32[] calldata proof
    ) public {
        require(!merkleClaimed[_msgSender()], "Already claimed");
        if (
            !MerkleProofLib.verify(
                proof,
                rootOfTier[tier],
                bytes32(keccak256(abi.encode(_msgSender(), data, metadata)))
            )
        ) {
            revert INVALID_PROOF();
        }
        require(
            balanceOf(_msgSender()) < MAX_TOKENS_PER_ADDRESS,
            "Exceeding max tokens per address"
        );
        uint256 tokenId = tokenCounter;
        _tierOf[tokenId] = tier;
        _tokenURI[tokenId] = metadata;
        _dataOf[tokenId] = data;
        tokenCounter++;
        merkleClaimed[_msgSender()] = true;
        _safeMint(_msgSender(), tokenId);
    }

    function claimECDSA(
        Voucher memory voucher,
        bytes calldata signature
    ) public {
        bytes32 structHash = _hashVoucher(voucher);
        address signer = recover(structHash, signature);

        canCall(
            signer,
            address(this),
            bytes4(keccak256("CAN_APPROVE_VOUCHER"))
        );

        uint256 tokenId = tokenCounter;
        _tierOf[tokenId] = voucher.tier;
        _tokenURI[tokenId] = voucher.metadata;
        _dataOf[tokenId] = voucher.data;
        tokenCounter++;
        getLastNonceOf[_msgSender()] = voucher.nonce;
        _safeMint(_msgSender(), tokenId);
    }

    function updateTier(
        uint256 tokenId,
        uint8 updatedTier,
        string memory updatedMetadata,
        uint256 updatedData
    ) public requiresAuth {
        _tierOf[tokenId] = updatedTier;
        _tokenURI[tokenId] = updatedMetadata;
        emit BadgeUpdated(tokenId, updatedTier, updatedMetadata, updatedData);
        emit MetadataUpdate(tokenId);
    }

    function burn(uint256 tokenId) public requiresAuth {
        _burn(tokenId);
    }

    /*//////////////////////////////////////////////////////////////
                              ERC721 LOGIC
    //////////////////////////////////////////////////////////////*/

    function approve(address spender, uint256 id) public virtual requiresAuth {
        address owner = _ownerOf[id];

        require(
            msg.sender == owner || isApprovedForAll[owner][msg.sender],
            "NOT_AUTHORIZED"
        );

        getApproved[id] = spender;

        emit Approval(owner, spender, id);
    }

    function setApprovalForAll(
        address operator,
        bool approved
    ) public virtual requiresAuth {
        isApprovedForAll[msg.sender][operator] = approved;

        emit ApprovalForAll(msg.sender, operator, approved);
    }

    function transferFrom(
        address from,
        address to,
        uint256 id
    ) public virtual requiresAuth {
        require(from == _ownerOf[id], "WRONG_FROM");

        require(to != address(0), "INVALID_RECIPIENT");

        require(
            msg.sender == from ||
                isApprovedForAll[from][msg.sender] ||
                msg.sender == getApproved[id],
            "NOT_AUTHORIZED"
        );

        // Underflow of the sender's balance is impossible because we check for
        // ownership above and the recipient's balance can't realistically overflow.
        unchecked {
            _balanceOf[from]--;

            _balanceOf[to]++;
        }

        _ownerOf[id] = to;

        delete getApproved[id];

        emit Transfer(from, to, id);
    }

    function safeTransferFrom(
        address from,
        address to,
        uint256 id
    ) public virtual requiresAuth {
        transferFrom(from, to, id);

        require(
            to.code.length == 0 ||
                ERC721TokenReceiver(to).onERC721Received(
                    msg.sender,
                    from,
                    id,
                    ""
                ) ==
                ERC721TokenReceiver.onERC721Received.selector,
            "UNSAFE_RECIPIENT"
        );
    }

    function safeTransferFrom(
        address from,
        address to,
        uint256 id,
        bytes calldata data
    ) public virtual requiresAuth {
        transferFrom(from, to, id);

        require(
            to.code.length == 0 ||
                ERC721TokenReceiver(to).onERC721Received(
                    msg.sender,
                    from,
                    id,
                    data
                ) ==
                ERC721TokenReceiver.onERC721Received.selector,
            "UNSAFE_RECIPIENT"
        );
    }

    /*//////////////////////////////////////////////////////////////
                              ERC165 LOGIC
    //////////////////////////////////////////////////////////////*/

    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual returns (bool) {
        return
            interfaceId == 0x01ffc9a7 || // ERC165 Interface ID for ERC165
            interfaceId == 0x80ac58cd || // ERC165 Interface ID for ERC721
            interfaceId == 0x5b5e139f || // ERC165 Interface ID for ERC721Metadata
            interfaceId == 0x49064906; // ERC4906 Interface ID for ERC4906
    }

    /*//////////////////////////////////////////////////////////////
                        INTERNAL MINT/BURN LOGIC
    //////////////////////////////////////////////////////////////*/

    function _mint(address to, uint256 id) internal virtual {
        require(to != address(0), "INVALID_RECIPIENT");

        require(_ownerOf[id] == address(0), "ALREADY_MINTED");

        // Counter overflow is incredibly unrealistic.
        unchecked {
            _balanceOf[to]++;
        }

        _ownerOf[id] = to;

        emit Transfer(address(0), to, id);
    }

    function _burn(uint256 id) internal virtual {
        address owner = _ownerOf[id];

        require(owner != address(0), "NOT_MINTED");

        // Ownership check above ensures no underflow.
        unchecked {
            _balanceOf[owner]--;
        }

        delete _ownerOf[id];

        delete getApproved[id];

        emit Transfer(owner, address(0), id);
    }

    /*//////////////////////////////////////////////////////////////
                        INTERNAL SAFE MINT LOGIC
    //////////////////////////////////////////////////////////////*/

    function _safeMint(address to, uint256 id) internal virtual {
        _mint(to, id);

        require(
            to.code.length == 0 ||
                ERC721TokenReceiver(to).onERC721Received(
                    msg.sender,
                    address(0),
                    id,
                    ""
                ) ==
                ERC721TokenReceiver.onERC721Received.selector,
            "UNSAFE_RECIPIENT"
        );
    }

    function _safeMint(
        address to,
        uint256 id,
        bytes memory data
    ) internal virtual {
        _mint(to, id);

        require(
            to.code.length == 0 ||
                ERC721TokenReceiver(to).onERC721Received(
                    msg.sender,
                    address(0),
                    id,
                    data
                ) ==
                ERC721TokenReceiver.onERC721Received.selector,
            "UNSAFE_RECIPIENT"
        );
    }

    /*//////////////////////////////////////////////////////////////
                        MULTICALL OVERRIDE
    //////////////////////////////////////////////////////////////*/

    function multicall(bytes[] calldata data) external override returns (bytes[] memory results) {
        results = new bytes[](data.length);
        for (uint256 i = 0; i < data.length; i++) {
            if (isTrustedForwarder(msg.sender)) {
                results[i] = Address.functionDelegateCall(address(this), abi.encodePacked(data[i], _msgSender()));
            }
            else {
                results[i] = Address.functionDelegateCall(address(this), data[i]);
            }
        }
        return results;
    }
}

/// @notice A generic interface for a contract which properly accepts ERC721 tokens.
/// @author Solmate (https://github.com/transmissions11/solmate/blob/main/src/tokens/ERC721.sol)
abstract contract ERC721TokenReceiver {
    function onERC721Received(
        address,
        address,
        uint256,
        bytes calldata
    ) external virtual returns (bytes4) {
        return ERC721TokenReceiver.onERC721Received.selector;
    }
}
"
    },
    "src/BadgeFactory.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.18;
import {BeaconProxy} from "lib/openzeppelin-contracts/contracts/proxy/beacon/BeaconProxy.sol";
import {Initializable} from "lib/openzeppelin-contracts/contracts/proxy/utils/Initializable.sol";
import {Badge} from "./Badge.sol";
import {OwnedUpgradeable} from "./auth/OwnedUpgradeable.sol";
import {MetaTransactionContext} from "./metaTx/MetaTransactionContext.sol";

contract BadgeFactory is
    Initializable,
    MetaTransactionContext,
    OwnedUpgradeable
{
    event BadgeDeployed(address indexed proxy);

    address public upgradeableBeacon;

    function initialize(address forwarder) public initializer {
        OwnedUpgradeable__init(_msgSender());
        MetaTransactionContext_init(forwarder);
    }

    function deployBadge(
        string memory name,
        string memory symbol,
        Badge.RolesAuthorityStruct memory rolesAuthorityStruct
    ) external returns (address) {
        bytes memory init = abi.encodeWithSignature(
            "initialize(string,string,address,address,((uint8,bool,address)[],(uint8,bytes4,bool)[],(bytes4,bool)[]))",
            name,
            symbol,
            _msgSender(),
            trustedForwarder,
            rolesAuthorityStruct
        );
        BeaconProxy proxy = new BeaconProxy(upgradeableBeacon, init);
        emit BadgeDeployed(address(proxy));
        return address(proxy);
    }

    function setUpgradeableBeacon(address _upgradeableBeacon) external onlyOwner {
        upgradeableBeacon = _upgradeableBeacon;
    }

    function setTrustedForwarder(address _forwarder) external onlyOwner {
        _setTrustedForwarder(_forwarder);
    }
}
"
    },
