    "src/metaTx/MetaTransactionContext.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.18;

import "lib/openzeppelin-contracts/contracts/utils/Context.sol";
import {Initializable} from "lib/openzeppelin-contracts/contracts/proxy/utils/Initializable.sol";

/**
 * @dev Context variant with ERC2771 support.
 */
contract MetaTransactionContext is Context, Initializable {
    /// @custom:oz-upgrades-unsafe-allow state-variable-immutable

    address public trustedForwarder;

    function MetaTransactionContext_init(address forwarder) internal onlyInitializing {
        _setTrustedForwarder(forwarder);
    }

    function _setTrustedForwarder(address forwarder) internal {
        trustedForwarder = forwarder;
    }

    function isTrustedForwarder(address forwarder) public view virtual returns (bool) {
        return forwarder == trustedForwarder;
    }

    function _msgSender() internal view virtual override returns (address sender) {
        if (isTrustedForwarder(msg.sender)) {
            // The assembly code is more direct than the Solidity version using `abi.decode`.
            /// @solidity memory-safe-assembly
            assembly {
                sender := shr(96, calldataload(sub(calldatasize(), 20)))
            }
        } else {
            return super._msgSender();
        }
    }

    function _msgData() internal view virtual override returns (bytes calldata) {
        if (isTrustedForwarder(msg.sender)) {
            return msg.data[:msg.data.length - 20];
        } else {
            return super._msgData();
        }
    }
}
"
