    "src/metaTx/Rep3Forwarder.sol": {
      "content": "// SPDX-License-Identifier: MIT
// OpenZeppelin Contracts (last updated v4.8.0) (metatx/MinimalForwarder.sol)

pragma solidity 0.8.18;

import "lib/openzeppelin-contracts/contracts/utils/cryptography/ECDSA.sol";
import "./EIP712Base.sol";
/**
 * @dev Simple minimal forwarder to be used together with an ERC2771 compatible contract. See {ERC2771Context}.
 *
 * MinimalForwarder is mainly meant for testing, as it is missing features to be a good production-ready forwarder. This
 * contract does not intend to have all the properties that are needed for a sound forwarding system. A fully
 * functioning forwarding system with good properties requires more complexity. We suggest you look at other projects
 * such as the GSN which do have the goal of building a system like that.
 */
contract Rep3Forwarder is EIP712Base {
    using ECDSA for bytes32;

    struct ForwardRequest {
        address from;
        address to;
        uint256 value;
        uint256 gas;
        uint256 batchNonce;
        uint256 batchId;
        bytes data;
    }

    mapping(address => address) public signerSenderMapping;

    bytes32 private constant _TYPEHASH =
        keccak256("ForwardRequest(address from,address to,uint256 value,uint256 gas,uint256 batchNonce,uint256 batchId,bytes data)");

    mapping(address => mapping(uint256 => uint256)) _nonces;

    constructor() EIP712Base("Rep3Forwarder", "0.0.1") {}

    function getNonce(address from, uint256 batchId) public view returns (uint256) {
        return _nonces[from][batchId];
    }

    function getDomainSeperator() public view returns (bytes32) {
        return domainSeparator;
    }

    function verify(ForwardRequest memory req, bytes calldata signature) public view returns (bool) {
        address signer = toTypedMessageHash(
            keccak256(abi.encode(_TYPEHASH, req.from, req.to, req.value, req.gas, _nonces[req.from][req.batchId], req.batchId, keccak256(req.data)))
        ).recover(signature);
        return _nonces[req.from][req.batchId] == req.batchNonce && signer == req.from;
    }

    function recoverSigner(ForwardRequest memory req, bytes calldata signature) public view returns (address) {
        address signer = toTypedMessageHash(
            keccak256(abi.encode(_TYPEHASH, req.from, req.to, req.value, req.gas, _nonces[req.from][req.batchId], req.batchId, keccak256(req.data)))
        ).recover(signature);
        return signer;
    }

    function execute(
        ForwardRequest memory req,
        bytes calldata signature)
        public
        payable
        returns (bool, bytes memory)
    {
        require(verify(req, signature), "MinimalForwarder: signature does not match request");
        _nonces[req.from][req.batchId] = _nonces[req.from][req.batchId] + 1;

        (bool success, bytes memory returndata) = req.to.call{gas: req.gas, value: req.value}(
            abi.encodePacked(req.data, req.from)
        );

        // Validate that the relayer has sent enough gas for the call.
