pragma solidity 0.8.7;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

/**
 * @dev Store amount of players' items.
 * Has functions to increase and decrease players' items when players buy items or play games.
 * Has a function to get amount of players' items.
 * Has funtions to pause and unpause other functions when malicious people attack.
 * The purpose of this function is to reduce gas fee in transfer process.
 */
contract ItemVault is Ownable, AccessControl {

    bytes32 public constant VAULT_ADMIN = keccak256("VAULT_ADMIN");

    bool public _pausedItemVault;

    mapping (address => mapping(uint256 => uint256)) public itemAmountbyAddress;

    /**
     * @dev Declare event for use emit `SetVaultBalanceAddress`,`IncreaseItem`,`DecreaseItem`
     * ,`ItemVaultPaused`, `ItemVaultUnpaused`.
     */

    event SetVaultBalanceAddress(address indexed vaultAddress);
    event IncreaseItem(address indexed userAddress, uint256 itemId , uint256 itemAmount);
    event DecreaseItem(address indexed userAddress, uint256 itemId , uint256 itemAmount);

    event ItemVaultPaused();
    event ItemVaultUnpaused();

    constructor() {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    /**
    * @dev Modifier to only allow the function to be executed when it isn't paused.
    */
    modifier whenItemVaultNotPaused() {
        require(!_pausedItemVault, "[ItemVault.whenItemVaultNotPaused] Not Paused");
        _;
    }

    /**
    * @dev Modifier to only allow the function to be executed when it is paused.
    */
    modifier whenItemVaultPaused() {
        require(_pausedItemVault, "[ItemVault.whenItemVaultPaused] Paused");
        _;
    }

     /**
     * @dev Function for increase item in vault.
     * Increase item in vault by use userAddress, itemId, itemAmount. 
     * @param _userAddress - Address of user uses to specify user in vault by Address.
     * @param _itemId - ID of item use specify item in vault.
     * @param _itemAmount - Amount of item use Increase item in vault.
     */

    function increaseItem(address _userAddress, uint256 _itemId, uint256 _itemAmount) external whenItemVaultNotPaused onlyRole(VAULT_ADMIN){
        require(itemAmountbyAddress[_userAddress][_itemId] + _itemAmount >= itemAmountbyAddress[_userAddress][_itemId], "[ItemVault.increaseItem] User has items overflow");
        itemAmountbyAddress[_userAddress][_itemId] += _itemAmount;

        emit IncreaseItem(_userAddress, _itemId, _itemAmount);
    }

    /**
     * @dev Function for decrease item in Vault.
     * Decrease item in vault by use userAddress,itemId,itemAmount. 
     * @param _userAddress - Address of user use to specify user in vault by Address.
     * @param _itemId - ID of item use specify item in vault.
     * @param _itemAmount - Amount of item use dencrease item in vault.
     */

     function decreaseItem(address _userAddress, uint256 _itemId, uint256 _itemAmount) external whenItemVaultNotPaused onlyRole(VAULT_ADMIN){
        require(_itemAmount <= itemAmountbyAddress[_userAddress][_itemId], "[ItemVault.decreaseItem] withdraw item should be less than user's item");
        require(itemAmountbyAddress[_userAddress][_itemId] - _itemAmount <= itemAmountbyAddress[_userAddress][_itemId], "[ItemVault.decreaseItem] User has items underflow");
        itemAmountbyAddress[_userAddress][_itemId] -= _itemAmount;

        emit DecreaseItem(_userAddress, _itemId, _itemAmount);
    }

    /**
    * @dev Function to get user's item amount in vault specify by item id.
    * @param _userAddress - Address of user to get item amount.
    * @param _itemId - Item id to get item amount
    * @return player's item amount stored in the vault
    */
    function getItemAmountbyId(address _userAddress, uint256 _itemId) external view returns (uint256){
        return itemAmountbyAddress[_userAddress][_itemId];
    }

    /**
    * @dev Function to pause functions in this contract.
    * can only be called by the creator of contract.
    */
    function pauseItemVault() external onlyOwner whenItemVaultNotPaused {
        _pausedItemVault = true;
        emit ItemVaultPaused();
    }

    /**
    * @dev Function to unpause functions in this contract.
    * can only be called by the creator of contract.
    */
    function unpauseItemVault() external onlyOwner whenItemVaultPaused {
        _pausedItemVault = false;
        emit ItemVaultUnpaused();
    }
}"
