    "contracts/ArrayUtils.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

library ArrayUtils {
    function search(address[] storage array, address value) internal view returns (bool) {
        for (uint i = 0; i < array.length; i++) {
            if (array[i] == value) {
                return true;
            }
        }
        return false;
    }

    function add(address[] storage array, address value) internal {
        array.push(value);
    }

    function remove(address[] storage array, address value) internal {
        for (uint i = 0; i < array.length; i++) {
            if (array[i] == value) {
                array[i] = array[array.length - 1];
                array.pop();
                break;
            }
        }
    }
}
