    "contracts/KonaDistribution.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import './interfaces/IPool.sol';
import './interfaces/IKonaGlobals.sol';
import './interfaces/IKonaStaking.sol';
import './KonaMath.sol';
import './KonaToken.sol';
import './interfaces/IOracle.sol';
import 'hardhat/console.sol';

/**
 * @title KonaDistribution
 * @dev A library for distributing funds and handling slashing and inflation in the Kona protocol.
 */
library KonaDistribution {
    event CycleTokenIssuanceForOracle(
        uint256 indexed cycle,
        address indexed pool,
        address indexed oracle,
        uint256 mintedToOracle
    );

    event CycleTokenIssuance(uint256 indexed cycle, address indexed pool, uint256 tokensToMint, uint256 totalMinted);
    event OracleResultsCalculated(
        address indexed pool,
        address indexed oracle,
        bool hasRecoveredOriginal,
        uint amountRecovered,
        bool costOfCapitalRecovered,
        uint costOfCapitalAmount
    );

    /*
     * @struct OracleResult
     * @dev Represents the result of an oracle in the distribution process.
     * @param lostCapital The amount of lost capital for the oracle.
     * @param excessAmount The excess amount of funds for the oracle.
     * @param totalVotes The total votes received by the oracle.
     */
    struct OracleResult {
        uint256 lostCapital;
        uint256 excessAmount;
        uint256 totalVotes;
    }

    /**
     * @struct OracleResults
     * @dev Represents the results for an oracle in the distribution process.
     * @param hasOriginalCapital Indicates if the oracle recovered at least the total original capital assigned.
     * @param hasCostOfCapital Indicates if the oracle recovered the total cost of capital.
     * @param initialFunds The initial funds assigned (and accepted by) the oracle to invest during the cycle.
     * @param accumulatedFunds The accumulated funds when the cycle finished by the oracle.
     * @param costOfCapitalPer The cost of capital (as a percentage) for the oracle.
     * @param costOfCapitalBrz The cost of capital (as total BRZ) for the oracle.
     * @param costOfCapitalPortionToRecoverByPool The cost of capital (total or partial) to be recovered by the pool.
     * @param standByCapitalPortionToRecoverByOracle The stand by capital (total or partial) to be recovered by the oracle.
     * @param lostCapital If positive, it means the oracle didn't recover the total initial funds and lost this amount.
     * @param oracleStandByCapital The stand-by capital committed by the oracle.
     * @param excessAmount The excess amount of funds to be distributed.
     * @param totalVotes The total votes received by the oracle.
     */
    struct OracleResults {
        bool hasOriginalCapital;
        bool hasCostOfCapital;
        uint256 initialFunds;
        uint256 accumulatedFunds;
        uint256 costOfCapitalPer;
        uint256 costOfCapitalBrz;
        uint256 costOfCapitalPortionToRecoverByPool;
        uint256 standByCapitalPortionToRecoverByOracle;
        uint256 lostCapital;
        uint256 oracleStandByCapital;
        uint256 excessAmount;
        uint256 totalVotes;
    }

    /**
     * @struct Inflation
     * @dev Represents the inflation data for distributing new tokens.
     * @param tokensToMint The total amount of tokens to mint.
     * @param firstHalf The first half of the new tokens to be minted.
     * @param totalMinted The total amount of tokens minted.
     * @param newTokens The new tokens to be minted for an oracle.
     * @param currentSupply The current supply of Kona tokens.
     * @param mintToStakers Total tokens to mint for stakers.
     */
    struct Inflation {
        uint256 tokensToMint;
        uint256 firstHalf;
        uint256 totalMinted;
        uint256 newTokens;
        uint256 currentSupply;
        uint256 mintToStakers;
    }

    /**
     * @struct Params
     * @dev Represents a set of parameters that are sent across different functions in the distribution process.
     * @param pool The address of the pool contract.
     * @param currentCycle The current cycle number.
     * @param konaGlobals The address of the KonaGlobals contract.
     * @param konaToken The KonaToken instance.
     * @param totalPools The total number of pools.
     * @param cycleDays The number of days in a cycle.
     */
    struct Params {
        address pool;
        uint256 currentCycle;
        address konaGlobals;
        KonaToken konaToken;
        uint256 totalPools;
        uint256 cycleDays;
    }

    /**
     * @dev Performs distribution for a specific pool, by checking results, returning cost of capital, distributing excess, slashing tokens and generating inflation.
     * @param _params The distribution parameters.
     */
    function distributeFunds(Params memory _params) external {
        if (!IPool(_params.pool).hasOracles()) {
            return;
        }

        uint256 totalOriginalCapital;
        uint256 totalCapitalRecovered;

        address[] memory oracles = IPool(_params.pool).getOracles();
        OracleResult[] memory oraclesResults = new OracleResult[](oracles.length);

        for (uint256 j = 0; j < oracles.length; j++) {
            // Retrieve the results for the current oracle
            (
                uint256 initialFunds,
                bool hasOriginalCapital,
                uint256 costOfCapitalPortionToRecoverByPool,
                uint256 standByCapitalPortionToRecoverByOracle,
                uint256 lostCapital,
                uint256 excessAmount,
                uint256 totalVotes
            ) = getOracleResults(_params.currentCycle, _params.pool, oracles[j]);

            // Store the oracle's results in the oraclesResults array
            oraclesResults[j] = OracleResult(lostCapital, excessAmount, totalVotes);

            // Add back funds that correspond to the pool if there are any
            if (costOfCapitalPortionToRecoverByPool > 0) {
                IPool(_params.pool).addFundsAfterDistribution(
                    _params.currentCycle,
                    oracles[j],
                    costOfCapitalPortionToRecoverByPool
                );
            }

            // Transfer funds that correspond to the oracle if there are any
            if (standByCapitalPortionToRecoverByOracle > 0) {
                IPool(_params.pool).sendStandByBackToOracle(
                    _params.currentCycle,
                    oracles[j],
                    standByCapitalPortionToRecoverByOracle
                );
            }

            // Calculate the total original capital and total capital recovered
            totalOriginalCapital += initialFunds;
            //Discount losing oracles completely by counting those that lost money as no money recovered
            if (hasOriginalCapital) {
                totalCapitalRecovered += initialFunds;
            }
        }

        uint256 alpha = totalOriginalCapital == 0 ? 0 : (totalCapitalRecovered * 100) / totalOriginalCapital;

        if (alpha == 0){
            return;
        }
        else if(alpha == 100) {
            distributeInflation(_params,alpha, oraclesResults, oracles);            
        }
        else{
            distributeSlashInflation(_params, alpha, oraclesResults, oracles);
        }

    }

    /**
     * @dev This function is responsible for distributing the slashing inflation.
     * @param _params The distribution parameters
     * @param _alpha The alpha value for the distribution
     * @param _oraclesResults The results of the oracles in the distribution process
     */
    function distributeSlashInflation(
        Params memory _params,
        uint256 _alpha,
        OracleResult[] memory _oraclesResults,
        address[] memory _oracles
    ) internal {
        uint256 s = calculateS(_params.konaGlobals, _params.konaToken);
        address[] memory oracles = IPool(_params.pool).getOracles();
        (
            address[] memory winningOracles,
            OracleResult[] memory winningOraclesResults,
            uint256 totalWinningVotes
        ) = calculateWinningOracles(_oraclesResults, _oracles);
        distributeExcess(_params, _alpha, s, oracles, _oraclesResults);
        slashAndRewardTokens(_params, _alpha, s, oracles, _oraclesResults, totalWinningVotes);
        tokenIssuance(_params, s, winningOracles, winningOraclesResults, totalWinningVotes);
    }

    /**
     * @dev This function is responsible for distributing the slashing inflation.
     * @param _params The distribution parameters
     * @param _alpha The alpha value for the distribution
     * @param _oraclesResults The results of the oracles in the distribution process
     */
    function distributeInflation(
        Params memory _params,
        uint256 _alpha,
        OracleResult[] memory _oraclesResults,
        address[] memory _oracles
    ) internal {
        uint256 s = calculateS(_params.konaGlobals, _params.konaToken);
        address[] memory oracles = IPool(_params.pool).getOracles();
        (
            address[] memory winningOracles,
            OracleResult[] memory winningOraclesResults,
            uint256 totalWinningVotes
        ) = calculateWinningOracles(_oraclesResults, _oracles);
        distributeExcess(_params, _alpha, s, oracles, _oraclesResults);
        tokenIssuance(_params, s, winningOracles, winningOraclesResults, totalWinningVotes);
    }

    /**
     * @dev This function distributes the excess funds to the winning oracles and their stakers.
     * @param _params The distribution parameters
     * @param _alpha The alpha value for the distribution
     * @param s The S parameter for the distribution
     * @param oracles The addresses of the oracles
     * @param _oraclesResults The results of the oracles in the distribution process
     */
    function distributeExcess(
        Params memory _params,
        uint256 _alpha,
        uint256 s,
        address[] memory oracles,
        OracleResult[] memory _oraclesResults
    ) internal {
        uint256 excessForStakers = KonaMath.calculateExcessForStakers(_alpha, s);

        //For each oracle we get the excess to distribute
        for (uint256 i = 0; i < _oraclesResults.length; i++) {
            if (_oraclesResults[i].excessAmount > 0) {
                if (_oraclesResults[i].totalVotes == 0 && _oraclesResults[i].excessAmount > 0) {
                    uint256 toKeep = _oraclesResults[i].excessAmount;
                    IPool(_params.pool).createRewardDeposit(oracles[i], toKeep);
                } else {
                    uint baseFee = (_oraclesResults[i].excessAmount * IOracle(oracles[i]).oracleFee()) / 1e18;
                    _oraclesResults[i].excessAmount = _oraclesResults[i].excessAmount - baseFee;
                    uint256 toDistribute = (_oraclesResults[i].excessAmount * excessForStakers) / 1e6;
                    uint256 toKeep = _oraclesResults[i].excessAmount - toDistribute + baseFee;

                    IPool(_params.pool).createRewardDeposit(oracles[i], toKeep);
                    IPool(_params.pool).createRewardDeposit(_params.pool, toDistribute);

                    IKonaStaking staking = IKonaStaking(IKonaGlobals(_params.konaGlobals).konaStaking());
                    uint bonusRate = (toDistribute * 1e32) /
                        staking.totalVotedPoolOraclePerCycle(_params.currentCycle, _params.pool, oracles[i]);
                    staking.setBrzRewardPerOracleRate(_params.currentCycle, _params.pool, oracles[i], bonusRate);
                }
            }
        }
    }

    /**
     * @dev This function slashes and distributes the tokens to the winning oracles and their stakers.
     * @param _params The distribution parameters
     * @param _alpha The alpha value for the distribution
     * @param s The S parameter for the distribution
     * @param oracles The addresses of the oracles
     * @param _oraclesResults The results of the oracles in the distribution process
     * @param totalWinningVotes The new weights for the oracles
     */
    function slashAndRewardTokens(
        Params memory _params,
        uint256 _alpha,
        uint256 s,
        address[] memory oracles,
        OracleResult[] memory _oraclesResults,
        uint totalWinningVotes
    ) internal {
        uint256 adjustment = getAdjustment(_alpha, s, _params.totalPools, _params.cycleDays);
        address konaStaking = IKonaGlobals(_params.konaGlobals).konaStaking();
        stakedTokensSlashReward(
            konaStaking,
            _params.currentCycle,
            _params.pool,
            _oraclesResults,
            oracles,
            adjustment,
            totalWinningVotes
        );
    }

    /**
     * @dev This function is responsible for the issuance of new tokens.
     * @param _params The distribution parameters
     * @param s The S parameter for the distribution
     * @param oracles The addresses of the winning oracles
     * @param oracleResults The results of the winnning oracles
     * @param winningOraclesVotes The total votes of the winning oracles
     */
    function tokenIssuance(
        Params memory _params,
        uint256 s,
        address[] memory oracles,
        OracleResult[] memory oracleResults,
        uint256 winningOraclesVotes
    ) internal {
        Inflation memory inflation;
        inflation.currentSupply = _params.konaToken.totalSupply();
        IKonaStaking konaStaking = IKonaStaking(IKonaGlobals(_params.konaGlobals).konaStaking());
        uint totalVotes = konaStaking.totalVotedPoolPerCycle(_params.currentCycle, _params.pool);
        uint winningWeight = (winningOraclesVotes * 100) / totalVotes;
        inflation.tokensToMint = KonaMath.calculateInflation(winningWeight, s, _params.currentCycle);
        inflation.tokensToMint -= inflation.currentSupply;
        uint inflationRate = ((inflation.tokensToMint / 2) * 1e18) / winningOraclesVotes;
        konaStaking.setInflationRateCycle(_params.currentCycle, inflationRate);
        for (uint i = 0; i < oracles.length; i++) {
            if (oracleResults[i].totalVotes != 0) {
                uint toMintForOracle = ((inflation.tokensToMint / 2) * oracleResults[i].totalVotes) /
                    winningOraclesVotes;
                _params.konaToken.mint(oracles[i], toMintForOracle);
                inflation.totalMinted += toMintForOracle;
                emit CycleTokenIssuanceForOracle(_params.currentCycle, _params.pool, oracles[i], toMintForOracle);
            }
        }
        _params.konaToken.mint(address(konaStaking), inflation.tokensToMint / 2);
        konaStaking.increaseNewMintedTokens(inflation.tokensToMint / 2);
        inflation.totalMinted += inflation.tokensToMint / 2;

        emit CycleTokenIssuance(_params.currentCycle, _params.pool, inflation.tokensToMint, inflation.totalMinted);
    }

    /**
     * @dev Slashes and distributes the tokens to winning oracles and their stakers.
     * @param _konaStaking The address of the KonaStaking contract.
     * @param _currentCycle The current cycle number.
     * @param _pool The address of the pool contract.
     * @param _oraclesResults The results of the oracles in the distribution process.
     * @param oracles The addresses of the oracles.
     * @param _adjustment The adjustment factor for slashing.
     * @param totalWinningVotes The toal votes that chose winning oracles this cycle.
     */
    function stakedTokensSlashReward(
        address _konaStaking,
        uint256 _currentCycle,
        address _pool,
        OracleResult[] memory _oraclesResults,
        address[] memory oracles,
        uint256 _adjustment,
        uint totalWinningVotes
    ) internal {
        uint256 totalSlashing; //Total tokens to be slashed and redistributed

        //We slash the tokens used to support the losing oracles
        for (uint256 i = 0; i < _oraclesResults.length; i++) {
            //For each Oracle...
            if (_oraclesResults[i].lostCapital > 0) {
                //... that lost
                uint256 slashing = (_oraclesResults[i].totalVotes * _adjustment) / 1e18; //I get the slashing amount (in total, for all stakers that supported it)
                // uint slashingRate
                // console.log("Oracle %s: Votes is %s, Total slashing is %s\n", i, _oraclesResults[i].totalVotes, slashing);
                IKonaStaking(_konaStaking).setRewardPerOracleRate(
                    _currentCycle,
                    _pool,
                    oracles[i],
                    (-int(_adjustment))
                );
                totalSlashing += slashing;
            }
        }
        //We calculate the winning ratio based on the total slashed tokens
        uint winningRatio = (totalSlashing * 1e18) / totalWinningVotes; //use accuracy base
        for (uint256 i = 0; i < _oraclesResults.length; i++) {
            //For each Oracle...
            //We now set all the Ratios for the oracle in the pool
            if (_oraclesResults[i].lostCapital == 0) {
                //... that won
                IKonaStaking(_konaStaking).setRewardPerOracleRate(_currentCycle, _pool, oracles[i], int(winningRatio));
            }
        }
    }

    /**
     * @dev Gets the results for an oracle in the distribution process.
     * @param _currentCycle The current cycle number.
     * @param _pool The address of the pool contract.
     * @param _oracle The address of the oracle.
     * @return initialFunds The initial funds assigned to the oracle.
     * @return hasOriginalCapital Indicates if the oracle has the original capital assigned.
     * @return costOfCapitalPortionToRecoverByPool The total amount to recover by the pool for the oracle.
     * @return standByCapitalPortionToRecoverByOracle The total amount to recover by the oracle.
     * @return lostCapital The amount of lost capital for the oracle.
     * @return excessAmount The excess amount of funds for the oracle.
     * @return totalVotes The total votes received by the oracle.
     */
    function getOracleResults(
        uint256 _currentCycle,
        address _pool,
        address _oracle
    ) internal returns (uint256, bool, uint256, uint256, uint256, uint256, uint256) {
        OracleResults memory r;

        r.initialFunds = IPool(_pool).getAcceptedCapitalAssigned(_oracle, _currentCycle);
        r.accumulatedFunds = IPool(_pool).getFundsAvailableToSpend(_oracle, _currentCycle);
        r.costOfCapitalPer = IPool(_pool).getCostOfCapital(_oracle, _currentCycle); //Percentage with 2 extra decimals
        r.costOfCapitalBrz = r.initialFunds + (r.initialFunds * r.costOfCapitalPer) / 1e4;
        r.oracleStandByCapital = IPool(_pool).getCommittedStandByCapital(_oracle, _currentCycle);

        r.hasOriginalCapital = r.accumulatedFunds >= r.initialFunds;

        r.totalVotes = IKonaStaking(IKonaGlobals(IPool(_pool).konaGlobals()).konaStaking())
            .totalVotedPoolOraclePerCycle(_currentCycle, _pool, _oracle); //Total staked

        if (r.hasOriginalCapital) {
            r.hasCostOfCapital = r.accumulatedFunds >= r.costOfCapitalBrz;
            r.standByCapitalPortionToRecoverByOracle = r.oracleStandByCapital;

            if (r.hasCostOfCapital) {
                //It has the cost of capital
                r.costOfCapitalPortionToRecoverByPool = r.costOfCapitalBrz;
                //The pool gets back the initial funds + cost of capital, the rest is excess profit
                r.excessAmount = r.accumulatedFunds - r.costOfCapitalBrz; //Excess profit
            } else {
                //No excess profit, the pool recovers the initial funds plus the excess
                r.costOfCapitalPortionToRecoverByPool = r.accumulatedFunds;
            }
        } else {
            r.lostCapital = r.initialFunds - r.accumulatedFunds;

            //I try to recover as much as possible of the initial capital from the stand by capital
            if (r.oracleStandByCapital >= r.lostCapital) {
                r.costOfCapitalPortionToRecoverByPool = r.initialFunds;
                r.standByCapitalPortionToRecoverByOracle = r.oracleStandByCapital - r.lostCapital;
            } else {
                //TODO: Off-chain, see if the Oracle requires a penalty
                r.costOfCapitalPortionToRecoverByPool = r.oracleStandByCapital + r.accumulatedFunds;
            }
        }
        if (r.hasCostOfCapital) {
            emit OracleResultsCalculated(
                _pool,
                _oracle,
                r.hasOriginalCapital,
                r.accumulatedFunds,
                r.hasCostOfCapital,
                r.costOfCapitalPortionToRecoverByPool
            );
        } else {
            emit OracleResultsCalculated(
                _pool,
                _oracle,
                r.hasOriginalCapital,
                r.accumulatedFunds,
                r.hasCostOfCapital,
                0
            );
        }
        return (
            r.initialFunds,
            r.hasOriginalCapital,
            r.costOfCapitalPortionToRecoverByPool,
            r.standByCapitalPortionToRecoverByOracle,
            r.lostCapital,
            r.excessAmount,
            r.totalVotes
        );
    }

    /**
     * @dev Calculates the S parameter used in the distribution process.
     * @param _konaGlobals The address of the KonaGlobals contract.
     * @param _konaToken The KonaToken instance.
     * @return The value of the S parameter.
     */
    function calculateS(address _konaGlobals, KonaToken _konaToken) internal view returns (uint256) {
        address staking = IKonaGlobals(_konaGlobals).konaStaking();
        uint256 totalStaked = IKonaStaking(staking).getTotalStaked();
        uint256 totalTokens = _konaToken.totalSupply();
        return (totalStaked * 1e18) / totalTokens; //1e18
    }

    /**
     * @dev Calculates the weights for the winning oracles in the distribution process.
     * @param _oraclesResults The results of the oracles in the distribution process.
     * @return The array of weights for the winning oracles and the sum of those weights.
     */
    function calculateWinningOracles(
        OracleResult[] memory _oraclesResults,
        address[] memory _oracles
    ) internal pure returns (address[] memory, OracleResult[] memory, uint256) {
        uint256 amountOfWinners = 0;
        uint256 totalVotesFromWinners;

        for (uint i = 0; i < _oraclesResults.length; i++) {
            if (_oraclesResults[i].lostCapital == 0) {
                totalVotesFromWinners += _oraclesResults[i].totalVotes;
                amountOfWinners++;
            }
        }
        address[] memory winningOracles = new address[](amountOfWinners);
        OracleResult[] memory winningOraclesResults = new OracleResult[](amountOfWinners);
        uint arrayCounter = 0;
        for (uint i = 0; i < _oraclesResults.length; i++) {
            if (_oraclesResults[i].lostCapital == 0) {
                winningOracles[arrayCounter] = _oracles[i];
                winningOraclesResults[arrayCounter] = _oraclesResults[i];
                arrayCounter++;
            }
        }

        return (winningOracles, winningOraclesResults, totalVotesFromWinners);
    }

    /**
     * @dev Calculates the adjustment factor for slashing in the distribution process.
     * @param _alpha The alpha value calculated in the distribution process.
     * @param _s The S parameter calculated in the distribution process.
     * @param _totalPools The total number of pools.
     * @param _cycleDays The number of days in a cycle.
     * @return The adjustment factor for slashing.
     */
    function getAdjustment(
        uint256 _alpha,
        uint256 _s,
        uint256 _totalPools,
        uint256 _cycleDays
    ) internal pure returns (uint256) {
        uint256 unitAtRisk = _s / (360 / _cycleDays); //We don't need to do S ** 2 anymore, tokenomics decision
        uint256 slashRate = KonaMath.calculateSlashRate(_alpha);
        uint256 adjustment = (unitAtRisk * slashRate);
        adjustment /= _totalPools;
        adjustment /= 1e6; //Slash rate decimal places
        return adjustment;
    }
}
"
    },
    "contracts/KonaMath.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import '@openzeppelin/contracts/utils/math/SafeMath.sol';

/**
 * @title KonaMath
 * @dev A library for mathematical calculations related to the Kona protocol.
 */
library KonaMath {
    using SafeMath for uint256;

    uint256 constant k = 19;
    uint256 constant c = 147221949; //0.147221949 * 1e18; //147221949; //0.147221949
    uint256 constant M = 1e27; //KonaToken total supply

    uint256 private constant SCALE = 1e18;

    /**
     * @dev Calculates the cost of capital based on the reference rate, staker weight, cycle duration, and other factors.
     * @param _referenceRate The reference rate value
     * @param _s The staker weight value
     * @param _c The constant value
     * @param _cycleDuration The duration of the cycle
     * @return The cost of capital as a percentage
     * The result is a percentage and the two last digits are decimals
     */
    function calculateCostOfCapital(
        uint256 _referenceRate,
        uint256 _s,
        uint256 _c,
        uint256 _cycleDuration
    ) public pure returns (uint256) {
        return ((_referenceRate * 100 + max(100 - _s - _c, 0) ** 2 / 2) * _cycleDuration) / 360;
    }

    /**
     * @dev Calculates the excess for stakers based on the alpha value and staker weight.
     * @param _alpha The alpha value
     * @param _s The staker weight value
     * @return The excess for stakers
     */
    function calculateExcessForStakers(uint256 _alpha, uint256 _s) public pure returns (uint256) {
        // I need to do alpha^2 * exp(1-alpha) * S / 2

        uint x = 100 - _alpha;
        uint256 exp = expApproximation(x, 100);
        uint256 calc = _alpha ** 2 * exp;
        uint256 excess = (calc * _s) / 2 / SCALE / 1e16;
        return excess;
    }

    /**
     * @dev Calculates the slash rate based on the alpha value.
     * @param _alpha The alpha value
     * @return The slash rate
     */
    function calculateSlashRate(uint256 _alpha) public pure returns (uint256) {
        if (_alpha == 100 || _alpha == 0) {
            return 0;
        }

        uint256 tan = uint256(tanh(100 - _alpha, _alpha));
        uint256 slashRate = (tan * _alpha) / (100 - _alpha);
        return slashRate;
    }

    /**
     * @dev Calculates the inflation based on the winning oracles weight, staker weight, and cycle number.
     * @param _winningOraclesWeight The weight of the winning oracles
     * @param _s The staker weight value
     * @param _cycleNumber The cycle number
     * @return The total new supply as a result of inflation
     */
    function calculateInflation(
        uint256 _winningOraclesWeight,
        uint256 _s,
        uint256 _cycleNumber
    ) public pure returns (uint256) {
        uint256 t = _winningOraclesWeight * _s * _cycleNumber;
        uint256 c_t = c * t;

        uint256 e = expApproximation(c_t, 1e29);
        uint256 er = reciprocal(e);
        uint256 div = 1e18 + k * er;
        uint256 totalNewSupply = (M * 1e18) / div;

        return totalNewSupply;
    }

    /**
     * @dev Calculates the reciprocal of a number.
     * @param _x The input number
     * @return The reciprocal of the input number
     */
    function reciprocal(uint256 _x) internal pure returns (uint256) {
        return 1e36 / _x; // Adjusted to maintain decimal precision
    }

    /**
     * @dev Finds the maximum of two numbers.
     * @param a The first number
     * @param b The second number
     * @return The maximum of the two numbers
     */
    function max(uint256 a, uint256 b) internal pure returns (uint256) {
        return a >= b ? a : b;
    }

    /**
     * @dev Calculates the hyperbolic tangent of a number.
     * @param _numerator The numerator value
     * @param _denominator The denominator value
     * @return The hyperbolic tangent of the input values
     */
    function tanh(uint256 _numerator, uint256 _denominator) internal pure returns (uint256) {
        //tanh(x) = 1 - 2 / (1 + e^2x)
        uint256 exp = expApproximation(2 * _numerator, _denominator);
        uint256 denom = SCALE + exp; //Scales by 1e18
        return 1e6 - (2 * SCALE * 1e6) / denom;
    }

    /**
     * @dev Calculates an approximation of the exponential function.
     * @param _numerator The numerator value
     * @param _denominator The denominator value
     * @return The approximation of the exponential function
     */
    function expApproximation(uint256 _numerator, uint256 _denominator) internal pure returns (uint256) {
        uint256 scaledX = (_numerator * SCALE) / _denominator;
        uint256 scaledX2 = (scaledX * scaledX) / SCALE; // Scale down x^2 by 1e18
        uint256 scaledX3 = (scaledX2 * scaledX) / SCALE; // Scale down x^3 by 1e18
        uint256 scaledX4 = (scaledX3 * scaledX) / SCALE; // Scale down x^4 by 1e18
        uint256 scaledX5 = (scaledX4 * scaledX) / SCALE; // Scale down x^5 by 1e18

        uint256 result = SCALE + scaledX + (scaledX2 / 2) + (scaledX3 / 6) + (scaledX4 / 24) + (scaledX5 / 120);

        return result;
    }
}
"
    },
    "contracts/KonaPools.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import '@openzeppelin/contracts/access/AccessControl.sol';
import '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import './interfaces/IKonaPools.sol';
import './interfaces/IPool.sol';
import './interfaces/IKonaGlobals.sol';
import './interfaces/IKonaStaking.sol';
import './KonaToken.sol';
import './KonaDistribution.sol';
import './ArrayUtils.sol';
import './KonaMath.sol';

contract KonaPools is AccessControl, IKonaPools {
    using ArrayUtils for address[];

    bytes32 public constant MANAGER_ROLE = keccak256('MANAGER_ROLE');
    bytes32 public constant EMERGENCY_ROLE = keccak256('EMERGENCY_ROLE');

    uint256 public constant ASSIGN_CAPITAL_DURATION = 1 days;
    uint256 public constant VOTING_DURATION = 1 days;
    uint256 public constant JOIN_LEAVE_DURATION = 1 days;
    uint256 public constant CYCLE_DAYS = 90;
    uint256 public constant CYCLE_DURATION = CYCLE_DAYS * 1 days;

    bool public testMode;
    bool public emergencyMode;
    address public override konaGlobals;

    address[] internal pools;

    Status status;

    uint256 public currentCycle = 1;
    uint256 public currentStatusStartedOn;

    //Cycle x Pool x Bool
    mapping(uint256 => mapping(address => bool)) private fundsDistributed;

    IERC20 public brzToken;
    KonaToken public konaToken;

    constructor(IERC20 _brzToken, KonaToken _konaToken, address _konaGlobals) {
        brzToken = _brzToken;
        konaToken = _konaToken;
        konaGlobals = _konaGlobals;

        status = Status.JOIN_LEAVE;

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(MANAGER_ROLE, msg.sender);
        _grantRole(EMERGENCY_ROLE, msg.sender);
    }

    function setTestMode(bool toSet) external onlyRole(DEFAULT_ADMIN_ROLE) {
        testMode = toSet;
    }

    function setEmergencyMode(bool toSet) external onlyRole(EMERGENCY_ROLE) {
        emergencyMode = toSet;
    }

    function isValidPool(address pool) public view returns (bool) {
        address konaStaking = IKonaGlobals(konaGlobals).konaStaking();
        if (status == Status.VOTING) {
            return IPool(pool).totalCapitalSupplyPerCycle(currentCycle) > 0 ? true : false;
        } else {
            return
                IKonaStaking(konaStaking).totalVotedPoolPerCycle(currentCycle, pool) > 0 ||
                IPool(pool).totalCapitalsAcceptedManual(currentCycle) > 0 ||
                IPool(pool).totalCapitalsAccepted(currentCycle) > 0;
        }
    }

    /**
     * @dev Changes the status of a pool.
     * @param _pool The address of the pool
     * @param _status The new status of the pool
     */
    function changePoolStatus(address _pool, bool _status) public onlyRole(MANAGER_ROLE) {
        require(!pools.search(_pool) || !_status, 'Cannot set the same status');

        if (_status) {
            pools.add(_pool);
        } else {
            pools.remove(_pool);
        }

        emit PoolStatusChanged(_pool, _status);
    }

    /**
     * @dev Checks if the distribution of funds can be started.
     * @return A boolean indicating if the distribution can be started
     */
    function canStartDistribution() public view returns (bool) {
        return
            status == Status.CYCLE &&
            (block.timestamp >= currentStatusStartedOn + CYCLE_DURATION || testMode) &&
            !emergencyMode;
    }

    /**
     * @dev Checks if the join/leave period can be started.
     * @return A boolean indicating if the join/leave period can be started
     */
    function canStartJoinLeave() public view returns (bool) {
        for (uint i = 0; i < pools.length; i++) {
            if (!fundsDistributed[currentCycle][pools[i]]) {
                if (isValidPool(pools[i])) return false;
            }
        }

        return status == Status.DISTRIBUTION;
    }

    /**
     * @dev Checks if the voting period can be started.
     * @return A boolean indicating if the voting period can be started
     */
    function canStartVoting() public view returns (bool) {
        //verify there is capital in at least one pool
        bool hasCapital;
        for (uint i = 0; i < pools.length; i++) {
            if (IPool(pools[i]).totalCapitalSupplyPerCycle(currentCycle) > 0) {
                hasCapital = true;
                break;
            }
        }
        return
            status == Status.JOIN_LEAVE &&
            hasCapital &&
            (block.timestamp >= currentStatusStartedOn + JOIN_LEAVE_DURATION || testMode) &&
            !emergencyMode;
    }

    /**
     * @dev Checks if the assign capital period can be started.
     * @return A boolean indicating if the assign capital period can be started
     */
    function canStartAssignCapital() public view returns (bool) {
        bool hasVotes = IKonaStaking(IKonaGlobals(konaGlobals).konaStaking()).totalVotesPerCycle(currentCycle) > 1;
        return
            status == Status.VOTING &&
            hasVotes &&
            (block.timestamp >= currentStatusStartedOn + VOTING_DURATION || testMode) &&
            !emergencyMode;
    }

    /**
     * @dev Checks if a new cycle can be started.
     * @return A boolean indicating if a new cycle can be started
     */
    function canStartNewCycle() public view returns (bool) {
        bool validForCycle = false;
        for (uint i = 0; i < pools.length; i++) {
            if (isValidPool(pools[i]) && IPool(pools[i]).atLeastOneOraclesHasCapital(currentCycle)) {
                validForCycle = true;
            }
        }


        return
            ((status == Status.ASSIGN_CAPITAL && validForCycle) ||
                (status == Status.ASSIGN_CAPITAL &&
                    (block.timestamp >= currentStatusStartedOn + ASSIGN_CAPITAL_DURATION || testMode))) &&
            !emergencyMode;
    }

    /**
     * @dev Starts a new cycle.
     */
    function startNewCycle() external {
        require(canStartNewCycle(), 'Action not allowed yet');

        currentStatusStartedOn = block.timestamp;

        status = Status.CYCLE;
    }

    /**
     * @dev Starts the distribution of funds.
     */
    function startDistribution() external {
        require(canStartDistribution(), 'Action not allowed yet');

        currentStatusStartedOn = block.timestamp;

        status = Status.DISTRIBUTION;
    }

    /**
     * @dev Starts the join/leave period.
     */
    function startJoinLeave() external {
        require(canStartJoinLeave(), 'Action not allowed yet');
        uint poolLength = pools.length;
        for (uint pool = 0; pool < poolLength; pool++) {
            //Rollover the balances
            IPool(pools[pool]).onCycleRollOver(currentCycle);
        }
        currentStatusStartedOn = block.timestamp;

        status = Status.JOIN_LEAVE;

        currentCycle += 1;
    }

    /**
     * @dev Starts the voting period.
     */
    function startVoting() external {
        require(canStartVoting(), 'Action not allowed yet');

        currentStatusStartedOn = block.timestamp;

        status = Status.VOTING;
    }

    /**
     * @dev Starts the assign capital period.
     */
    function startAssignCapital() external {
        require(canStartAssignCapital(), 'Action not allowed yet');

        currentStatusStartedOn = block.timestamp;

        status = Status.ASSIGN_CAPITAL;
    }

    /**
     * @dev Checks if a maturity date is within the current cycle.
     * @param _maturity The maturity date to check
     * @return A boolean indicating if the maturity date is within the current cycle
     */
    function isDateInsideCurrentCycle(uint256 _maturity) public view returns (bool) {
        if (status != Status.CYCLE) {
            return false;
        }

        return _maturity < currentStatusStartedOn + CYCLE_DURATION;
    }

    /**
     * @dev Gets the current cycle status.
     * @return The current cycle, the timestamp when the current status started, and the status itself
     */
    function getCycleStatus() public view returns (uint256, uint256, Status) {
        return (currentCycle, currentStatusStartedOn, status);
    }

    /**
     * @dev Distributes funds to a pool.
     * @param _pool The address of the pool
     */
    function distributeFunds(address _pool) public {
        require(pools.search(_pool), 'Invalid pool');
        require(status == Status.DISTRIBUTION, 'Not allowed yet');
        require(!fundsDistributed[currentCycle][_pool], 'Already distributed');
        require(isValidPool(_pool), 'Pool not valid');
        fundsDistributed[currentCycle][_pool] = true;

        KonaDistribution.Params memory params = KonaDistribution.Params(
            _pool,
            currentCycle,
            konaGlobals,
            konaToken,
            pools.length,
            CYCLE_DAYS
        );

        KonaDistribution.distributeFunds(params);
    }

    /**
     * @dev Gets the list of pools.
     * @return The array of pool addresses
     */
    function getPools() external view returns (address[] memory) {
        return pools;
    }

    /**
     * @dev Gets the length of a cycle in days.
     * @return The total number of days in a cycle
     */
    function getCycleDays() external pure returns (uint256) {
        return CYCLE_DAYS;
    }
}
"
    },
    "contracts/KonaToken.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import '@openzeppelin/contracts/token/ERC20/ERC20.sol';
import '@openzeppelin/contracts/access/AccessControl.sol';

contract KonaToken is ERC20, AccessControl {
    bytes32 public constant MINTER_ROLE = keccak256('MINTER_ROLE');

    uint256 public constant MAX_SUPPLY = 1 * 10 ** 9 * 10 ** 18; // 1 billion tokens

    /**
     * @dev Contract constructor
     */
    constructor() ERC20('Kona Token', 'KONA') {
        // Grant DEFAULT_ADMIN_ROLE and MINTER_ROLE to the contract deployer
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(MINTER_ROLE, msg.sender);

        // Mint 50 million tokens to the contract deployer
        _mint(msg.sender, 50 * 10 ** 6 * 10 ** 18); // 50 million tokens
    }

    /**
     * @dev (potentially only for testing) Function to give KonaPools minting rights
     * @param _konaPools - the Kona Pools contract
     */
    function addKonaPoolsMinter(address _konaPools) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _grantRole(MINTER_ROLE, _konaPools);
    }

    /**
     * @dev Mints new tokens and assigns them to the specified address.
     * @param _to The address to mint tokens to
     * @param _amount The amount of tokens to mint
     */
    function mint(address _to, uint256 _amount) external onlyRole(MINTER_ROLE) {
        require(totalSupply() + _amount <= MAX_SUPPLY, 'Max supply exceeded');
        _mint(_to, _amount);
    }
}
"
    },
    "hardhat/console.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity >= 0.4.22 <0.9.0;

library console {
	address constant CONSOLE_ADDRESS = address(0x000000000000000000636F6e736F6c652e6c6f67);

	function _sendLogPayload(bytes memory payload) private view {
		uint256 payloadLength = payload.length;
		address consoleAddress = CONSOLE_ADDRESS;
		assembly {
			let payloadStart := add(payload, 32)
			let r := staticcall(gas(), consoleAddress, payloadStart, payloadLength, 0, 0)
		}
	}

	function log() internal view {
		_sendLogPayload(abi.encodeWithSignature("log()"));
	}

	function logInt(int256 p0) internal view {
		_sendLogPayload(abi.encodeWithSignature("log(int256)", p0));
	}

	function logUint(uint256 p0) internal view {
		_sendLogPayload(abi.encodeWithSignature("log(uint256)", p0));
	}

	function logString(string memory p0) internal view {
		_sendLogPayload(abi.encodeWithSignature("log(string)", p0));
	}

	function logBool(bool p0) internal view {
		_sendLogPayload(abi.encodeWithSignature("log(bool)", p0));
	}

	function logAddress(address p0) internal view {
		_sendLogPayload(abi.encodeWithSignature("log(address)", p0));
	}

	function logBytes(bytes memory p0) internal view {
		_sendLogPayload(abi.encodeWithSignature("log(bytes)", p0));
	}

	function logBytes1(bytes1 p0) internal view {
		_sendLogPayload(abi.encodeWithSignature("log(bytes1)", p0));
	}

	function logBytes2(bytes2 p0) internal view {
		_sendLogPayload(abi.encodeWithSignature("log(bytes2)", p0));
	}

	function logBytes3(bytes3 p0) internal view {
		_sendLogPayload(abi.encodeWithSignature("log(bytes3)", p0));
	}

	function logBytes4(bytes4 p0) internal view {
		_sendLogPayload(abi.encodeWithSignature("log(bytes4)", p0));
	}

	function logBytes5(bytes5 p0) internal view {
		_sendLogPayload(abi.encodeWithSignature("log(bytes5)", p0));
	}

	function logBytes6(bytes6 p0) internal view {
		_sendLogPayload(abi.encodeWithSignature("log(bytes6)", p0));
	}

	function logBytes7(bytes7 p0) internal view {
		_sendLogPayload(abi.encodeWithSignature("log(bytes7)", p0));
	}

	function logBytes8(bytes8 p0) internal view {
		_sendLogPayload(abi.encodeWithSignature("log(bytes8)", p0));
	}

	function logBytes9(bytes9 p0) internal view {
		_sendLogPayload(abi.encodeWithSignature("log(bytes9)", p0));
	}

	function logBytes10(bytes10 p0) internal view {
		_sendLogPayload(abi.encodeWithSignature("log(bytes10)", p0));
	}

	function logBytes11(bytes11 p0) internal view {
		_sendLogPayload(abi.encodeWithSignature("log(bytes11)", p0));
	}

	function logBytes12(bytes12 p0) internal view {
		_sendLogPayload(abi.encodeWithSignature("log(bytes12)", p0));
	}

	function logBytes13(bytes13 p0) internal view {
		_sendLogPayload(abi.encodeWithSignature("log(bytes13)", p0));
