    "contracts/NFTStakingVault.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.7;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "hardhat/console.sol";

contract NFTStakingVault is Ownable, ReentrancyGuard, Pausable {

    uint256 private constant MONTH = 30 * 86400;
    uint256 private _fee = 0.01 ether;
    uint256 private _reward = 1e18;

    IERC721 public nft;
    IERC20 public token;

    struct Stake {
        address owner;
        uint256 stakedAt;
        uint256 claimedAt;
        bool stake;
    }

    mapping(uint256 => Stake) vault;

    event ItemStaked(uint256 tokenId, address owner, uint256 timestamp);
    event Claimed(address owner, uint256 reward);
    event Received(address from, uint256 amount);

    constructor(address _nftAddress, address _tokenAddress) {
        nft = IERC721(_nftAddress);
        token = IERC20(_tokenAddress);
    }

    receive() external payable {
        emit Received(msg.sender, msg.value);
    }

    function stake(uint256[] memory tokenIds_) external nonReentrant payable {
        require(msg.value == _fee, "Payment is not enough");
        for (uint i = 0; i < tokenIds_.length; i++) {
            require(nft.ownerOf(tokenIds_[i]) == msg.sender, "Not Owner");
            require(vault[tokenIds_[i]].stake != true, "Already Staked");
            vault[tokenIds_[i]] = Stake(msg.sender, block.timestamp, block.timestamp, true);
            emit ItemStaked(tokenIds_[i], msg.sender, block.timestamp);
        }
    }

    function unstake(uint256[] memory tokenIds_) external nonReentrant {
        uint calculatedReward = 0;
        for (uint i = 0; i < tokenIds_.length; i++) {
            require(vault[tokenIds_[i]].owner == msg.sender, "Not Owner");
            require(vault[tokenIds_[i]].stake == true, "Not staked");
            uint256 _stakedAt = vault[tokenIds_[i]].stakedAt;
            uint256 _claimedAt = vault[tokenIds_[i]].claimedAt;
            uint256 stakingPeriod = block.timestamp - _stakedAt;
            uint256 _dailyReward = _calculateReward(stakingPeriod);
            uint256 claimPeriod = block.timestamp - _claimedAt;
            calculatedReward += _dailyReward * claimPeriod / 86400;
        }
        require(token.transfer(msg.sender, calculatedReward), "Token transfer failed");
        for (uint j = 0; j < tokenIds_.length; j++) {
            vault[tokenIds_[j]].stake = false;
        }
        emit Claimed(msg.sender, calculatedReward);
    }

    function claimAll(uint256[] memory tokenIds_) external nonReentrant {
        uint calculatedReward = 0;
        for (uint i = 0; i < tokenIds_.length; i++) {
            require(vault[tokenIds_[i]].owner == msg.sender, "Not Owner");
            require(vault[tokenIds_[i]].stake == true, "Not staked");
            uint256 _stakedAt = vault[tokenIds_[i]].stakedAt;
            uint256 _claimedAt = vault[tokenIds_[i]].claimedAt;
            uint256 stakingPeriod = block.timestamp - _stakedAt;
            uint256 _dailyReward = _calculateReward(stakingPeriod);
            uint256 claimPeriod = block.timestamp - _claimedAt;
            calculatedReward += _dailyReward * claimPeriod / 86400;
        }
        require(token.transfer(msg.sender, calculatedReward), "Token transfer failed");
        for (uint j = 0; j < tokenIds_.length; j++) {
            vault[tokenIds_[j]].claimedAt = block.timestamp;
        }
        emit Claimed(msg.sender, calculatedReward);
    }

    function rewardAmount(uint256[] memory tokenIds_) external view returns (uint256) {
        uint256 calculatedReward = 0;
        uint256 dailyReward;
        for (uint i = 0; i < tokenIds_.length; i++) {
            if(vault[tokenIds_[i]].stake == true) {
                uint256 _stakedAt = vault[tokenIds_[i]].stakedAt;
                uint256 _claimedAt = vault[tokenIds_[i]].claimedAt;
                uint256 stakingPeriod = block.timestamp - _stakedAt;
                uint256 claimPeriod = block.timestamp - _claimedAt;
                if (stakingPeriod <= MONTH) {
                    dailyReward = _reward;
                } else if (stakingPeriod < 3 * MONTH) {
                    dailyReward = _reward * 2;
                } else if (stakingPeriod < 6 * MONTH) {
                    dailyReward = _reward * 3;
                } else if (stakingPeriod >= 6 * MONTH) {
                    dailyReward = _reward * 4;
                }
                calculatedReward += dailyReward * claimPeriod / 86400;
            }
        }
        return calculatedReward;
    }

    function _calculateReward(uint256 stakingPeriod)
        internal
        returns (uint256)
    {
        uint256 dailyReward;
        if (stakingPeriod <= MONTH) {
            dailyReward = _reward;
        } else if (stakingPeriod < 3 * MONTH) {
            dailyReward = _reward * 2;
        } else if (stakingPeriod < 6 * MONTH) {
            dailyReward = _reward * 3;
        } else if (stakingPeriod >= 6 * MONTH) {
            dailyReward = _reward * 4;
        }
        return dailyReward;
    }

    function getDailyReward(uint256 tokenId_) external view returns (uint256 dailyReward) {
        require(vault[tokenId_].stake == true, "Not staked");
        uint256 _claimedAt = vault[tokenId_].claimedAt;
        uint256 stakingPeriod = block.timestamp - _claimedAt;
        if (stakingPeriod <= MONTH) {
            dailyReward = _reward;
        } else if (stakingPeriod < 3 * MONTH) {
            dailyReward = _reward * 2;
        } else if (stakingPeriod < 6 * MONTH) {
            dailyReward = _reward * 3;
        } else if (stakingPeriod >= 6 * MONTH) {
            dailyReward = _reward * 4;
        }
    }

    function readStake(uint256 tokenId_) external view returns (bool) {
        return vault[tokenId_].stake;
    }

    function fee() external view returns (uint) {
        return _fee;
    }

    function setFee(uint fee_) external onlyOwner {
        _fee = fee_;
    }

    function reward() external view returns (uint) {
        return _reward;
    }

    function setReward(uint reward_) external onlyOwner {
        _reward = reward_;
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function withdraw() external onlyOwner {
        require(token.transfer(msg.sender, token.balanceOf(address(this))), "Token transfer failed");
        require(payable(_msgSender()).send(address(this).balance));
    }
}
"
    },
