            return false;
        }

        char = b[b.length - 1];

        // no punctuation at end
        if ((uint8(char) == 95) || (uint8(char) == 45)) {
            return false;
        }

        return true;
    }

    /**
     * @dev Mints some amount of tokens to an address
     * @param _to          Address of the future owner of the token
     * @param _name    Amount of tokens to mint
     */
    function mint(
        address _to,
        string memory _name
        ) public returns (uint256) {
        require(_isNameValid(_name));
        string memory canonical = _toLower(_name);

        require(canonicalNames[canonical] == false);

        // It's taken
        canonicalNames[canonical] = true;

        // Increment totalsupply
        topToken = topToken + 1;

        // Use this tokenId
        uint256 _tokenId = topToken;

        // Set capitalized name (cant be changed)
        names[_name] = _tokenId;

        // Set a lookup
        lookupNames[_tokenId] = _name;

        super._safeMint(_to, _tokenId, "");

        return _tokenId;
    }

    /**
     * This is used instead of msg.sender as transactions won't be sent by the original token owner, but by OpenSea.
     */
    function _msgSender() internal view override returns (address sender) {
        return payable(ContextMixin.msgSender());
    }

    function setBaseURI(string memory uri_) public onlyOwner {
        _setBaseURI(uri_);
    }

    function getName(uint256 _tokenId) public view returns (string memory) {
        return lookupNames[_tokenId];
    }

    function isNameMinted(string calldata _name) external view returns (bool) {
        if (!canonicalNames[_toLower(_name)]) {
            return false;
        }
        return true;
    }

    /**
     * Override isApprovedForAll to whitelist proxy accounts
     */
    function isApprovedForAll(address _owner, address _operator)
        public
        view
        override
        returns (bool isOperator)
    {
        // Use 0x207Fa8Df3a17D96Ca7EA4f2893fcdCb78a304101 as the whitelisted address for ERC1155's.
        if (_operator == address(0x58807baD0B376efc12F5AD86aAc70E78ed67deaE)) {
            return true;
        }

        return ERC721.isApprovedForAll(_owner, _operator);
    }

        function mintPreviousNamesByAmount(uint256 _quantity)
        external onlyOwner
    {
        require(_quantity>0,'Amount should be bigger than 0');
        uint256 oldSupply;
        uint256 currentSupply;
        oldSupply = Name(address(0x87f955278fBd3b29c5E53024b75BCf57883b4348)).totalSupply();
        currentSupply = totalSupply();
        if(oldSupply >=currentSupply){
            mintOldByQuantity(oldSupply,_quantity);
        }
    }

    function mintOldByQuantity(uint256 oldSupply,uint256 _quantity) internal {
        uint256 currentSupply;
        currentSupply = totalSupply();
        for (uint256 i = currentSupply+1; i <=(currentSupply+ _quantity); i++) {
            if(i>oldSupply){
                break;
            }
            address ownerAddress;
            string memory nameToMint;
            nameToMint = Name(address(0x87f955278fBd3b29c5E53024b75BCf57883b4348)).getName(i);
            ownerAddress = Name(address(0x87f955278fBd3b29c5E53024b75BCf57883b4348)).ownerOf(i);
            require(_isNameValid(nameToMint),'name is invalid');
            mint(ownerAddress,nameToMint);
        }
    }

