    "contracts/ITicketMeGoods.sol": {
      "content": "// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.9;

import "@openzeppelin/contracts-upgradeable/interfaces/IERC721Upgradeable.sol";

/**
 * @title TicketMe Goods NFT interface
 * @author TicketMe
 */
interface ITicketMeGoods is IERC721Upgradeable {
    struct Goods {
        string baseTokenURI;
        string goodsId;
        uint256 goodsJpyPrice;
        uint256 maxSupply;
        uint256 saleStartTime;
        uint256 saleEndTime;
        uint256 claimStartTime;
        uint256 claimEndTime;
        address royaltyReceiver;
        uint96 royaltyFeeRate;
    }

    enum RestrictType {
        NONE,
        ANY,
        ALL
    }

    struct Holder {
        address contractAddress;
        uint256 balance;
    }

    struct HolderRestriction {
        Holder[] restrictions;
        RestrictType restrictType;
    }

    function initialize(Goods memory _goods, address _priceFeederAddress, HolderRestriction memory _holderRestriction) external;

    /**
     * @dev Setter of Goods data.
     * @param _goods Goods data.
     */
    function setGoods(Goods memory _goods) external;

    /**
     * @dev Setter of HolderRestriction data.
     * @param _holderRestriction HolderRestriction data.
     */
    function setHolderRestriction(HolderRestriction memory _holderRestriction) external;

    /**
     * @dev Setter of PriceFeeder contract address.
     * @param _priceFeederAddress PriceFeeder contract address.
     */
    function setPriceFeeder(address _priceFeederAddress) external;

    /**
     * @dev Setter of isClaimed flag.
     * @param _tokenId tokenId
     * @param _isClaimed isClaimed
     */
    function setIsClaimed(uint256 _tokenId, bool _isClaimed) external;

    /**
     * @dev Getter of current total mint count.
     */
    function totalMint() external returns (uint256);

    /**
     * @dev Returns goods mint price.
     */
    function getMintPrice() external returns (uint256);

    /**
     * @dev Mint ticket NFT. Transfer it to the specified address.
     */
    function mint(address _to, uint256 _mintNumber) external payable;

    /**
     * @dev Claim goods.
     * @param _tokenId tokenId
     */
    function claim(uint256 _tokenId) external;

    /**
     * @dev Withdraw all. Transfer it to the sender address.
     */
    function withdrawAll() external;

    /**
     * @dev transferOwnership.
     */
    function transferOwnership(address newOwner) external;
}
