    "contracts/TicketMeGoods.sol": {
      "content": "// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.9;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/utils/CountersUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/ERC721Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/common/ERC2981Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/cryptography/draft-EIP712Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/cryptography/ECDSAUpgradeable.sol";
import "operator-filter-registry/src/upgradeable/RevokableDefaultOperatorFiltererUpgradeable.sol";
import "./ITicketMeGoods.sol";
import "./IPriceFeeder.sol";
import "./IERC721PermitForAllUpgradeable.sol";

interface Token {
    function balanceOf(address _owner) external view returns (uint256);
}

/**
 * @title TicketMe Goods NFT
 * @author TicketMe
 */
contract TicketMeGoods is
    Initializable,
    ITicketMeGoods,
    ERC721Upgradeable,
    EIP712Upgradeable,
    ERC2981Upgradeable,
    RevokableDefaultOperatorFiltererUpgradeable,
    OwnableUpgradeable,
    IERC721PermitForAllUpgradeable
{
    using CountersUpgradeable for CountersUpgradeable.Counter;
    using StringsUpgradeable for uint256;

    event Minted(address indexed _minter, address indexed _to, uint256 _mintNumber);
    event Claimed(address indexed _claimer, uint256 _tokenId);
    event Permitted(address indexed _owner, address indexed _operator);

    Goods public goods;
    RestrictType public restrictType;
    Holder[] public restrictions;
    IPriceFeeder public priceFeederContract;
    CountersUpgradeable.Counter private _tokenIds;
    mapping(uint256 => bool) public isClaimed;
    mapping(address => mapping(bytes32 => bool)) private _permitNonces;

    bytes32 private immutable _PERMIT_ALL_TYPEHASH = keccak256("PermitForAll(address signer,address operator,uint256 deadline,bytes32 nonce)");

    /**
     * @dev Sale period validation.
     */
    modifier inSalePeriod() {
        require(block.timestamp >= goods.saleStartTime && block.timestamp <= goods.saleEndTime, "Out of sale period.");
        _;
    }

    /**
     * @dev Claim period validation.
     */
    modifier inClaimPeriod() {
        require(block.timestamp >= goods.claimStartTime && block.timestamp <= goods.claimEndTime, "Out of claim period.");
        _;
    }

    /**
     * @dev Initialize.
     */
    function initialize(Goods memory _goods, address _priceFeederAddress, HolderRestriction memory _holderRestriction) public initializer {
        __Ownable_init();
        __ERC721_init("TicketMeGoods", "TicketMeGoods");
        __EIP712_init("TicketMeGoods", "3");
        __ERC2981_init();
        __RevokableDefaultOperatorFilterer_init();

        goods = _goods;
        setHolderRestriction(_holderRestriction);
        _setDefaultRoyalty(_goods.royaltyReceiver, _goods.royaltyFeeRate);
        priceFeederContract = IPriceFeeder(_priceFeederAddress);
    }

    /**
     * @dev Setter of Goods data.
     * @param _goods Goods data.
     */
    function setGoods(Goods memory _goods) public onlyOwner {
        goods = _goods;
        _setDefaultRoyalty(_goods.royaltyReceiver, _goods.royaltyFeeRate);
    }

    /**
     * @dev Setter of HolderRestriction data.
     * @param _holderRestriction HolderRestriction data.
     */
    function setHolderRestriction(HolderRestriction memory _holderRestriction) public onlyOwner {
        restrictType = _holderRestriction.restrictType;

        delete restrictions;
        for (uint256 i = 0; i < _holderRestriction.restrictions.length; i++) {
            restrictions.push(_holderRestriction.restrictions[i]);
        }
    }

    /**
     * @dev Setter of PriceFeeder contract address.
     * @param _priceFeederAddress PriceFeeder contract address.
     */
    function setPriceFeeder(address _priceFeederAddress) public onlyOwner {
        priceFeederContract = IPriceFeeder(_priceFeederAddress);
    }

    /**
     * @dev Setter of isClaimed flag.
     * @param _tokenId tokenId
     * @param _isClaimed isClaimed
     */
    function setIsClaimed(uint256 _tokenId, bool _isClaimed) public onlyOwner {
        require(_tokenId <= totalMint(), "Token not yet minted.");
        isClaimed[_tokenId] = _isClaimed;
    }

    /**
     * @dev Getter of base token URI. Override ERC721.
     */
    function _baseURI() internal view override returns (string memory) {
        return goods.baseTokenURI;
    }

    /**
     * @dev Getter of tokenURI. Override ERC721.
     */
    function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {
        return string(abi.encodePacked(_baseURI(), tokenId.toString(), ".json"));
    }

    /**
     * @dev Getter of current total mint count.
     */
    function totalMint() public view returns (uint256) {
        return _tokenIds.current();
    }

    /**
     * @dev Returns goods mint price.
     */
    function getMintPrice() public view returns (uint256) {
        return goods.goodsJpyPrice * priceFeederContract.getJpyMaticPrice(18);
    }

    /**
     * @dev Mint ticket NFT. Transfer it to the specified address.
     */
    function mint(address _to, uint256 _mintNumber) public payable inSalePeriod onlyOwner {
        require(_mintNumber > 0, "Mint number must be at least 1.");
        require(totalMint() + _mintNumber <= goods.maxSupply, "This goods is sold out.");
        require(msg.value >= getMintPrice() * _mintNumber, "Below mint price.");
        require(checkHolder(_to), "This address is not allowed to mint.");

        for (uint256 i = 0; i < _mintNumber; i++) {
            _tokenIds.increment();
            uint256 nextTokenId = _tokenIds.current();
            _safeMint(_to, nextTokenId);
        }

        withdraw(msg.value);

        emit Minted(msg.sender, _to, _mintNumber);
    }

    /**
     * @dev Claim goods.
     * @param _tokenId tokenId
     */
    function claim(uint256 _tokenId) public onlyOwner inClaimPeriod {
        require(_tokenId <= totalMint(), "Token not yet minted.");
        isClaimed[_tokenId] = true;

        emit Claimed(ownerOf(_tokenId), _tokenId);
    }

    /**
     * @dev Withdraw to owner address.
     */
    function withdraw(uint256 balance) private {
        if (balance > 0) {
            (bool success, ) = (owner()).call{value: balance}("");
            require(success, "Transfer failed.");
        }
    }

    /**
     * @dev Withdraw all. Transfer it to the sender address.
     */
    function withdrawAll() public onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "Balance is zero.");

        (bool success, ) = (msg.sender).call{value: balance}("");
        require(success, "Transfer failed.");
    }

    /**
     * @dev EIP712 DOMAIN_SEPARATOR
     */
    function DOMAIN_SEPARATOR() external view override returns (bytes32) {
        return _domainSeparatorV4();
    }

    /**
     * @dev Permit All for ERC721
     * @param signer address to approve
     * @param operator address to operator
     * @param deadline deadline for permit
     * @param nonce random nonce for permit
     * @param signature signature for permit
     */
    function permitForAll(address signer, address operator, uint256 deadline, bytes32 nonce, bytes memory signature) external override {
        require(block.timestamp <= deadline, "Expired deadline");
        require(!_permitNonces[signer][nonce], "Permit nonce is used");
        require(!isApprovedForAll(signer, operator), "Already approved");

        bytes32 structHash = keccak256(abi.encode(_PERMIT_ALL_TYPEHASH, signer, operator, deadline, nonce));
        bytes32 hash = _hashTypedDataV4(structHash);

        (address _signer, ) = ECDSAUpgradeable.tryRecover(hash, signature);
        require(_signer != address(0) && _signer == signer, "Invalid signature");

        _setApprovalForAll(_signer, operator, true);

        _permitNonces[_signer][nonce] = true;
        emit Permitted(_signer, operator);
    }

    /**
     * @dev Check token holder restriction.
     * @param _holder Address to check.
     */
    function checkHolder(address _holder) public view returns (bool) {
        if (restrictType == RestrictType.NONE) {
            return true;
        } else if (restrictType == RestrictType.ANY) {
            for (uint256 i = 0; i < restrictions.length; i++) {
                Token token = Token(restrictions[i].contractAddress);
                if (token.balanceOf(_holder) >= restrictions[i].balance) {
                    return true;
                }
            }
            return false;
        } else if (restrictType == RestrictType.ALL) {
            for (uint256 i = 0; i < restrictions.length; i++) {
                Token token = Token(restrictions[i].contractAddress);
                if (token.balanceOf(_holder) < restrictions[i].balance) {
                    return false;
                }
            }
            return true;
        }
        return true;
    }

    /**
     * @notice Override ERC721.
     * @dev Cannot transfer when already claimed.
     */
    function _beforeTokenTransfer(address _from, address _to, uint256 _tokenId, uint256 _batchSize) internal virtual override {
        if (isClaimed[_tokenId]) {
            require(_from == address(0), "Transfer is not allowed.");
        }
        super._beforeTokenTransfer(_from, _to, _tokenId, _batchSize);
    }

    /**
     * @dev override owner.
     */
    function owner() public view virtual override(OwnableUpgradeable, RevokableOperatorFiltererUpgradeable) returns (address) {
        return OwnableUpgradeable.owner();
    }

    /**
     * @dev override supportsInterface.
     */
    function supportsInterface(bytes4 interfaceId) public view virtual override(ERC721Upgradeable, ERC2981Upgradeable, IERC165Upgradeable) returns (bool) {
        return interfaceId == type(IERC721PermitForAllUpgradeable).interfaceId || super.supportsInterface(interfaceId);
    }

    /**
     * @dev override transferOwnership.
     */
    function transferOwnership(address newOwner) public virtual override(ITicketMeGoods, OwnableUpgradeable) {
        OwnableUpgradeable.transferOwnership(newOwner);
    }
}
"
    },
    "operator-filter-registry/src/IOperatorFilterRegistry.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

interface IOperatorFilterRegistry {
    function isOperatorAllowed(address registrant, address operator) external view returns (bool);
    function register(address registrant) external;
    function registerAndSubscribe(address registrant, address subscription) external;
    function registerAndCopyEntries(address registrant, address registrantToCopy) external;
    function unregister(address addr) external;
    function updateOperator(address registrant, address operator, bool filtered) external;
    function updateOperators(address registrant, address[] calldata operators, bool filtered) external;
    function updateCodeHash(address registrant, bytes32 codehash, bool filtered) external;
    function updateCodeHashes(address registrant, bytes32[] calldata codeHashes, bool filtered) external;
    function subscribe(address registrant, address registrantToSubscribe) external;
    function unsubscribe(address registrant, bool copyExistingEntries) external;
    function subscriptionOf(address addr) external returns (address registrant);
    function subscribers(address registrant) external returns (address[] memory);
    function subscriberAt(address registrant, uint256 index) external returns (address);
    function copyEntriesOf(address registrant, address registrantToCopy) external;
    function isOperatorFiltered(address registrant, address operator) external returns (bool);
    function isCodeHashOfFiltered(address registrant, address operatorWithCode) external returns (bool);
    function isCodeHashFiltered(address registrant, bytes32 codeHash) external returns (bool);
    function filteredOperators(address addr) external returns (address[] memory);
    function filteredCodeHashes(address addr) external returns (bytes32[] memory);
    function filteredOperatorAt(address registrant, uint256 index) external returns (address);
    function filteredCodeHashAt(address registrant, uint256 index) external returns (bytes32);
    function isRegistered(address addr) external returns (bool);
    function codeHashOf(address addr) external returns (bytes32);
}
"
