pragma solidity >=0.8.17;

// Dependencies
import {ICygnusX1Vault} from "./interfaces/ICygnusX1Vault.sol";

// Libraries
import {SafeTransferLib} from "./libraries/SafeTransferLib.sol";
import {FixedPointMathLib} from "./libraries/FixedPointMathLib.sol";

// Interfaces
import {IHangar18} from "./interfaces/IHangar18.sol";

/**
 *  @notice This is a minimally modified verison of TraderJoe's StableJoeStaking:
 *          https://github.com/traderjoe-xyz/joe-core/blob/main/contracts/StableJoeStaking.sol
 *  @title CygnusX1Vault Contract to stake CYG and receive rewards in different tokens
 */
contract CygnusX1Vault is ICygnusX1Vault {
    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
            1. LIBRARIES
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /**
     *  @custom:library SafeTransferLib For safe transfers of Erc20 tokens
     */
    using SafeTransferLib for address;

    /**
     *  @custom:library FixedPointMathLib Arithmetic library with operations for fixed-point numbers
     */
    using FixedPointMathLib for uint256;

    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
            2. STORAGE
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /*  ────────────────────────────────────────────── Private ────────────────────────────────────────────────  */

    /**
     *  @custom:struct UserInfo Info of each user
     *  @custom:member amount The current amount staked of `cygToken` for user
     *  @custom:member rewardDebt The current reward debt of a rewardToken for user
     *  @custom:member lastClaim The timestamp of the last vault claim
     */
    struct UserInfo {
        uint256 amount;
        mapping(address => uint256) rewardDebt;
        uint256 lastClaim;
    }

    /**
     *  @notice Info of each user that is currently staking in the vault
     */
    mapping(address => UserInfo) private userInfo;

    /*  ─────────────────────────────────────────────── Public ────────────────────────────────────────────────  */

    /**
     *  @inheritdoc ICygnusX1Vault
     */
    mapping(address => uint256) public override accRewardPerShare;

    /**
     *  @inheritdoc ICygnusX1Vault
     */
    mapping(address => bool) public override isRewardToken;

    /**
     *  @inheritdoc ICygnusX1Vault
     */
    mapping(address => uint256) public override lastRewardBalance;

    /**
     *  @inheritdoc ICygnusX1Vault
     */
    address[] public override allRewardTokens;

    /**
     *  @inheritdoc ICygnusX1Vault
     */
    string public override name = "CygnusDAO: X1 Vault";

    /**
     *  @inheritdoc ICygnusX1Vault
     */
    string public override version = "1.0.0";

    /**
     *  @inheritdoc ICygnusX1Vault
     */
    uint256 public constant override ACC_REWARD_PER_SHARE_PRECISION = 1e24;

    /**
     *  @inheritdoc ICygnusX1Vault
     */
    uint256 public constant override MAX_REWARD_TOKENS = 25;

    /**
     *  @inheritdoc ICygnusX1Vault
     */
    uint256 public constant override MAX_DEPOSIT_FEE = 0.05e18;

    /**
     *  @inheritdoc ICygnusX1Vault
     */
    IHangar18 public immutable override hangar18;

    /**
     *  @inheritdoc ICygnusX1Vault
     */
    address public immutable override cygToken;

    /**
     *  @inheritdoc ICygnusX1Vault
     */
    uint256 public cygStakedBalance;

    /**
     *  @inheritdoc ICygnusX1Vault
     */
    uint256 public x1DepositFee;

    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
            3. CONSTRUCTOR
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /**
     * @notice Initialize a new StableJoeStaking contract
     * @dev This contract needs to receive an ERC20 `_rewardToken` in order to distribute them
     * (with MoneyMaker in our case)
     * @param _cygToken The address of the CYG Token
     */
    constructor(IHangar18 _hangar18, address _cygToken) {
        // Assign hangar18
        hangar18 = _hangar18;

        // Assign staking token
        cygToken = _cygToken;

        // Initial deposit fee of 0.5%
        x1DepositFee = 0.005e18;
    }

    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
            4. MODIFIERS
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /**
     *  @custom:modifier cygnusAdmin Controls important parameters in both Collateral and Borrow contracts 👽
     */
    modifier cygnusAdmin() {
        checkAdmin();
        _;
    }

    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
            5. CONSTANT FUNCTIONS
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /*  ────────────────────────────────────────────── Private ────────────────────────────────────────────────  */

    /**
     *  @notice Internal check for msg.sender admin, checks factory's current admin 👽
     */
    function checkAdmin() private view {
        // Current admin from the factory
        address admin = hangar18.admin();

        /// @custom:error MsgSenderNotAdmin Avoid unless caller is Cygnus Admin
        if (msg.sender != admin) revert CygnusX1Vault__MsgSenderNotAdmin();
    }

    /**
     *  @notice Checks the `token` balance of this contract
     *  @param token The token to view balance of
     *  @return amount This contract's `token` balance
     */
    function _checkBalance(address token) private view returns (uint256) {
        // Our balance of `token`
        return token.balanceOf(address(this));
    }

    /*  ────────────────────────────────────────────── External ───────────────────────────────────────────────  */

    /**
     * @notice Get the number of reward tokens
     * @return The length of the array
     */
    function rewardTokensLength() external view returns (uint256) {
        return allRewardTokens.length;
    }

    /**
     * @notice Get user info
     * @param user The address of the user
     * @param token The address of the reward token
     * @return The amount of CYG user has deposited
     * @return The reward debt for the chosen token
     */
    function getUserInfo(address user, address token) external view returns (uint256, uint256) {
        // Load user
        UserInfo storage _user = userInfo[user];

        // Return staked amount and the reward debt for rewardToken
        return (_user.amount, _user.rewardDebt[token]);
    }

    /**
     *  @inheritdoc ICygnusX1Vault
     */
    function pendingReward(address user, address token) external view returns (uint256) {
        // Load user
        UserInfo storage _user = userInfo[user];

        // Total amount of CYG currently staked
        uint256 _totalCyg = cygStakedBalance;

        // Gas savings
        uint256 _accRewardTokenPerShare = accRewardPerShare[token];

        // Get vault balance of token
        uint256 balance = _checkBalance(token);

        if (balance != lastRewardBalance[token] && _totalCyg != 0) {
            // New reward balance
            uint256 newReward = balance - lastRewardBalance[token];

            // Reward token per share to get new pending reward
            _accRewardTokenPerShare = _accRewardTokenPerShare + newReward.fullMulDiv(ACC_REWARD_PER_SHARE_PRECISION, _totalCyg);
        }

        // Return pending amount of `token` claimable by `user`
        return _user.amount.fullMulDiv(_accRewardTokenPerShare, ACC_REWARD_PER_SHARE_PRECISION) - _user.rewardDebt[token];
    }

    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
            6. NON-CONSTANT FUNCTIONS
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /*  ────────────────────────────────────────────── Private ────────────────────────────────────────────────  */

    /**
     * @notice Safe token transfer function, just in case if rounding error causes pool to not have enough reward tokens
     * @param token The address of the token to transfer
     * @param to The address that will receive `amount` of `rewardToken`
     * @param amount The amount to send to `to`
     */
    function safeTokenTransfer(address token, address to, uint256 amount) private {
        // Balance of token
        uint256 balance = _checkBalance(token);

        // If higher then adjust
        if (amount > balance) {
            lastRewardBalance[token] = lastRewardBalance[token] - balance;

            token.safeTransfer(to, balance);
        } else {
            // Send difference
            lastRewardBalance[token] = lastRewardBalance[token] - amount;

            token.safeTransfer(to, amount);
        }
    }

    /*  ─────────────────────────────────────────────── Public ────────────────────────────────────────────────  */

    /**
     *  @inheritdoc ICygnusX1Vault
     */
    function updateReward(address token) public override {
        /// @custom:error InvalidRewardToken
        if (!isRewardToken[token]) revert CygnusX1Vault__InvalidRewardToken();

        // Get total CYG balance currently staked
        uint256 totalCyg = cygStakedBalance;

        // Get token balance
        uint256 balance = _checkBalance(token);

        // If no new tokens received then return without updating
        if (balance == lastRewardBalance[token] || totalCyg == 0) return;

        // New reward balance
        uint256 newReward = balance - lastRewardBalance[token];

        // Update token reward per share
        accRewardPerShare[token] += newReward.fullMulDiv(ACC_REWARD_PER_SHARE_PRECISION, totalCyg);

        // Update reward balance
        lastRewardBalance[token] = balance;
    }

    /**
     *  @inheritdoc ICygnusX1Vault
     */
    function updateRewardsAll() public override {
        // Get total CYG balance currently staked
        uint256 totalCyg = cygStakedBalance;

        // Gas savings
        address[] memory _tokens = allRewardTokens;

        // Loop through each and update
        for (uint256 i = 0; i < _tokens.length; i++) {
            // Get token balance
            uint256 balance = _checkBalance(_tokens[i]);

            // If no new tokens received then return without updating
            if (balance == lastRewardBalance[_tokens[i]] || totalCyg == 0) continue;

            // New reward balance
            uint256 newReward = balance - lastRewardBalance[_tokens[i]];

            // Update token reward per share
            accRewardPerShare[_tokens[i]] += newReward.fullMulDiv(ACC_REWARD_PER_SHARE_PRECISION, totalCyg);

            // Update reward balance
            lastRewardBalance[_tokens[i]] = balance;
        }
    }

    /*  ────────────────────────────────────────────── External ───────────────────────────────────────────────  */

    /**
     *  @inheritdoc ICygnusX1Vault
     */
    function deposit(uint256 cygAmount) external override {
        // Load user to storage
        UserInfo storage user = userInfo[msg.sender];

        // Calculate the fee based on the deposit fee percentage
        uint256 depositFee = cygAmount.mulWad(x1DepositFee);

        // Calculate the amount to be deposited after deducting the fee
        uint256 finalAmount = cygAmount - depositFee;

        // Keep track of the previous amount deposited by the user
        uint256 previousAmount = user.amount;

        // Calculate the new amount by adding the current deposit amount minus the fee
        uint256 newAmount = previousAmount + finalAmount;

        // Update the user's deposited amount to the new amount
        user.amount = newAmount;

        // Gas savings
        address[] memory _allRewardTokens = allRewardTokens;

        // Loop through each reward token and update the user's reward debt
        for (uint256 i = 0; i < _allRewardTokens.length; i++) {
            // Get reward token
            address _token = _allRewardTokens[i];

            // Update the reward for the token
            updateReward(_token);

            // Keep track of the user's previous reward debt for the token
            uint256 _previousRewardDebt = user.rewardDebt[_token];

            // Calculate the new reward debt based on the new deposited amount
            user.rewardDebt[_token] = newAmount.fullMulDiv(accRewardPerShare[_token], ACC_REWARD_PER_SHARE_PRECISION);

            // If the user had a previous deposit, calculate and transfer the pending rewards
            if (previousAmount != 0) {
                // Calculate the pending rewards for the token
                uint256 _pending = previousAmount.fullMulDiv(accRewardPerShare[_token], ACC_REWARD_PER_SHARE_PRECISION) -
                    _previousRewardDebt;

                // If there are pending rewards, transfer them to the user
                if (_pending != 0) {
                    // Transfer reward to user
                    safeTokenTransfer(_token, msg.sender, _pending);

                    /// @custom:event ClaimReward
                    emit ClaimReward(msg.sender, _token, _pending);
                }
            }
        }

        // Increase the internal balance of CYG tokens held by the contract
        cygStakedBalance += finalAmount;

        // Transfer the fee amount to the fee collector
        if (depositFee != 0) cygToken.safeTransferFrom(msg.sender, hangar18.daoReserves(), depositFee);

        // Transfer the deposited amount (minus the fee) to the contract
        if (finalAmount != 0) cygToken.safeTransferFrom(msg.sender, address(this), finalAmount);

        // Store last claim
        user.lastClaim = block.timestamp;

        // Emit the Deposit event to notify external listeners
        emit Deposit(msg.sender, finalAmount, depositFee);
    }

    /**
     *  @inheritdoc ICygnusX1Vault
     */
    function withdraw(uint256 _amount) external override {
        // Load user to storage
        UserInfo storage user = userInfo[msg.sender];

        // Keep track of the previous amount deposited by the user
        uint256 stakedAmount = user.amount;

        /// @custom:error AmountExceedsBalance
        if (_amount > stakedAmount) revert CygnusX1Vault__AmountExceedsBalance();

        // Calculate the new amount after the withdrawal
        uint256 _newAmount = stakedAmount - _amount;

        // Update the user's deposited amount to the new amount
        user.amount = _newAmount;

        // Gas savings
        address[] memory _allRewardTokens = allRewardTokens;

        // Get total amount of reward tokens
        uint256 totalRewards = _allRewardTokens.length;

        // If the user had a previous deposit and there are reward tokens
        if (stakedAmount != 0 && totalRewards != 0) {
            // Loop through each reward token and update the user's reward debt
            for (uint256 i = 0; i < totalRewards; i++) {
                // Get token at index `i`
                address _token = _allRewardTokens[i];

                // Update the reward for the token
                updateReward(_token);

                // Calculate the pending rewards for the token
                uint256 _pending = stakedAmount.fullMulDiv(accRewardPerShare[_token], ACC_REWARD_PER_SHARE_PRECISION) -
                    user.rewardDebt[_token];

                // Calculate the new reward debt based on the new deposited amount
                user.rewardDebt[_token] = _newAmount.fullMulDiv(accRewardPerShare[_token], ACC_REWARD_PER_SHARE_PRECISION);

                // If there are pending rewards, transfer them to the user
                if (_pending != 0) {
                    // Transfer to msg.sender
                    safeTokenTransfer(_token, msg.sender, _pending);

                    /// @custom:event ClaimReward
                    emit ClaimReward(msg.sender, _token, _pending);
                }
            }
        }

        // Decrease the internal balance of CYG tokens held by the contract
        cygStakedBalance -= _amount;

        // Transfer the withdrawn CYG tokens to the user
        cygToken.safeTransfer(msg.sender, _amount);

        // Store last claim
        user.lastClaim = block.timestamp;

        /// @custom:event Withdraw
        emit Withdraw(msg.sender, _amount);
    }

    /**
     *  @inheritdoc ICygnusX1Vault
     */
    function emergencyWithdraw() external override {
        // Load to storage
        UserInfo storage user = userInfo[msg.sender];

        // Get amount of CYG staked
        uint256 stakedAmount = user.amount;

        // Update to 0
        user.amount = 0;

        // Loop through each reward token
        for (uint256 i; i < allRewardTokens.length; i++) {
            // get Token at index `i`
            address token = allRewardTokens[i];

            // Set reward debt to 0
            user.rewardDebt[token] = 0;
        }
        // Update CYG token balance held by this contract
        cygStakedBalance = cygStakedBalance - stakedAmount;

        // Transfer CYG to msg.sender
        cygToken.safeTransfer(msg.sender, stakedAmount);

        // Store last claim
        user.lastClaim = block.timestamp;

        /// @custom:event EmergencyWithdraw
        emit EmergencyWithdraw(msg.sender, stakedAmount);
    }

    //  Admin Only functions  //

    /**
     *  @inheritdoc ICygnusX1Vault
     *  @custom:security only-admin 👽
     */
    function addRewardToken(address token) external override cygnusAdmin {
        /// @custom:error TokenAlreadyAdded
        if (isRewardToken[token]) revert CygnusX1Vault__TokenAlreadyAdded();

        /// @custom:error InvalidRewardToken
        if (token == address(0)) revert CygnusX1Vault__InvalidRewardToken();

        /// @custom:error VaultIsFull
        if (allRewardTokens.length > MAX_REWARD_TOKENS) revert CygnusX1Vault__VaultIsFull();

        // Add token to array
        allRewardTokens.push(token);

        // Mark as true
        isRewardToken[token] = true;

        // Update token reward
        updateReward(token);

        /// @custom:error NewRewardToken
        emit NewRewardToken(msg.sender, allRewardTokens.length, token);
    }

    /**
     *  @inheritdoc ICygnusX1Vault
     *  @custom:security only-admin 👽
     */
    function removeRewardToken(address token) external override cygnusAdmin {
        /// @custom:error InvalidRewardToken
        if (!isRewardToken[token]) revert CygnusX1Vault__InvalidRewardToken();

        // Update reward first
        updateReward(token);

        // Mark as false
        isRewardToken[token] = false;

        // Gas savings
        uint256 length = allRewardTokens.length;

        // Loop through each reward token
        for (uint256 i; i < length; i++) {
            // If token is found then we remove it
            if (allRewardTokens[i] == token) {
                // Move token to the last index of the array
                allRewardTokens[i] = allRewardTokens[length - 1];

                // Pop last index
                allRewardTokens.pop();

                // Escape
                break;
            }
        }

        /// @custom:event RewardTokenRemoved
        emit RewardTokenRemoved(token);
    }

    /**
     *  @inheritdoc ICygnusX1Vault
     *  @custom:security only-admin 👽
     */
    function setNewDepositFee(uint256 fee) external override cygnusAdmin {
        /// @custom:error DepositFeeTooHigh
        if (fee > MAX_DEPOSIT_FEE) revert CygnusX1Vault__DepositFeeTooHigh();

        // Fee up to this point
        uint256 oldFee = x1DepositFee;

        // Set new fee
        x1DepositFee = fee;

        /// @custom:event NewDepositFee
        emit NewDepositFee(fee, oldFee);
    }
}
"
    },
    "contracts/cygnus-vault/interfaces/ICygnusX1Vault.sol": {
      "content": "// SPDX-License-Identifier: Unlicense
pragma solidity >=0.8.17;

import {IHangar18} from "./IHangar18.sol";

// Vault
interface ICygnusX1Vault {
    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
            1. CUSTOM ERRORS
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /**
     *  @dev Reverts when withdrawing more than user balance
     *  @custom:error AmountExceedsBalance
     */
    error CygnusX1Vault__AmountExceedsBalance();

    /**
     *  @dev Reverts when msg.sender is not admin
     *  @custom:error MsgSenderNotAdmin
     */
    error CygnusX1Vault__MsgSenderNotAdmin();

    /**
     *  @dev Reverts when attempting to change state of a non-existing token 
     *  @custom:error InvalidRewardToken
     */
    error CygnusX1Vault__InvalidRewardToken();

    /**
     *  @dev Reverts when adding an already-added token
     *  @custom:error TokenAlreadyAdded
     */
    error CygnusX1Vault__TokenAlreadyAdded();

    /**
     *  @dev Reverts when the deposit fee set is above max
     *  @custom:error DepositFeeTooHigh
     */
    error CygnusX1Vault__DepositFeeTooHigh();

    /**
     *  @dev Reverts when adding more tokens than max allowed vault size
     *  @custom:error VaultIsFull
     */
    error CygnusX1Vault__VaultIsFull();

    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
            2. CUSTOM EVENTS
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /**
     *  @notice Emitted when a user deposits CYG
     *  @param user The address of the user that deposited
     *  @param amount The amount of CYG deposited
     *  @param fee The fee taken from the deposit
     */
    event Deposit(address indexed user, uint256 amount, uint256 fee);

    /**
     *  @notice Emitted when owner changes the deposit fee percentage
     *  @param newFee The new deposit fee percentage
     *  @param oldFee The old deposit fee percentage
     */
    event NewDepositFee(uint256 newFee, uint256 oldFee);

    /**
     *  @notice Emitted when a user withdraws CYG
     *  @param user The address of the withdrawing user
     *  @param amount The amount of CYG withdrawn
     */
    event Withdraw(address indexed user, uint256 amount);

    /**
     *  @notice Emitted when a user claims reward
     *  @param user The address of the user claiming rewards
     *  @param rewardToken The address of the reward token claimed
     *  @param amount The amount of the reward token claimed
     */
    event ClaimReward(address indexed user, address indexed rewardToken, uint256 amount);

    /**
     *  @notice Emitted when a user emergency withdraws from the vault
     *  @param user The address of the user performing the emergency withdraw
     *  @param amount The amount of CYG emergency withdrawn
     */
    event EmergencyWithdraw(address indexed user, uint256 amount);

    /**
     *  @notice Emitted when owner adds a token to the reward tokens list
     *  @param sender msg.sender
     *  @param vaultSize The vault size after this token was added
     *  @param token The address of the token added to the reward tokens list
     */
    event NewRewardToken(address sender, uint256 vaultSize, address token);

    /**
     *  @notice Emitted when owner removes a token from the reward tokens list
     *  @param token The address of the token removed from the reward tokens list
     */
    event RewardTokenRemoved(address token);

    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
            3. CONSTANT FUNCTIONS
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /** 
     *  @notice The name of the x1 vault
     */
    function name() external view returns (string memory);

    /**
     *  @notice Version of the vault
     */
    function version() external view returns (string memory);

    /**
     *  @notice Address of the hangar18 on this chain
     *  @return The hangar18
     */
    function hangar18() external view returns (IHangar18);

    /// @notice Accumulated `token` rewards per share, scaled to `ACC_REWARD_PER_SHARE_PRECISION`
    /// @param token The reward token address
    /// @return The reward per share for the given token
    function accRewardPerShare(address token) external view returns (uint256);

    /**
     *  @notice Indicates if a token is an active reward token
     *  @param token The reward token address
     *  @return True if token is an active reward token, false otherwise
     */
    function isRewardToken(address token) external view returns (bool);

    /**
     *  @notice Last reward balance of `token`
     *  @param token The reward token address
     *  @return The balance of the reward token
     */
    function lastRewardBalance(address token) external view returns (uint256);

    /**
     *  @notice The list of all active reward tokens
     *  @return The address array of all active reward tokens
     */
    function allRewardTokens(uint256) external view returns (address);

    /**
     *  @return The precision used to calculate rewards per share
     */
    function ACC_REWARD_PER_SHARE_PRECISION() external pure returns (uint256);

    /**
     *  @return The maximum number of reward tokens allowed
     */
    function MAX_REWARD_TOKENS() external pure returns (uint256);

    /**
     *  @return The maximum deposit fee percentage
     */
    function MAX_DEPOSIT_FEE() external pure returns (uint256);

    /**
     *  @return The address of the staking token (CYG)
     */
    function cygToken() external view returns (address);

    /**
     *  @return The current balance of staked CYG
     */
    function cygStakedBalance() external view returns (uint256);

    /**
     *  @return The deposit fee (if any)
     */
    function x1DepositFee() external view returns (uint256);

    /**
     *  @notice View function to see pending reward token
     *  @param user The address of the user
     *  @param token The address of the token
     *  @return Pending amount of `token` to be collected by `user`
     */
    function pendingReward(address user, address token) external view returns (uint256);

    /*  ═══════════════════════════════════════════════════════════════════════════════════════════════════════ 
            4. NON-CONSTANT FUNCTIONS
        ═══════════════════════════════════════════════════════════════════════════════════════════════════════  */

    /*  ─────────────────────────────────────────────── Public ────────────────────────────────────────────────  */

    /**
     *  @notice Update reward variables
     *  @param token The address of the reward token
     *  @dev Needs to be called before any deposit or withdrawal
     */
    function updateReward(address token) external;

    /**
     *  @notice Update all rewards
     */
    function updateRewardsAll() external;

    /*  ────────────────────────────────────────────── External ───────────────────────────────────────────────  */

    /**
     *  @notice Deposit CYG for reward token allocation
     *  @param cygAmount The amount of CYG to deposit
     */
    function deposit(uint256 cygAmount) external;

    /**
     *  @notice Withdraw CYG and harvest the rewards
     *  @param _amount The amount of CYG to withdraw
     */
    function withdraw(uint256 _amount) external;

    /**
     *  @notice Add a reward token
     *  @param token The address of the reward token
     *  @custom:security only-admin
     */
    function addRewardToken(address token) external;

    /**
     *  @notice Remove a reward token
     *  @param token The address of the reward token
     *  @custom:security only-admin
     */
    function removeRewardToken(address token) external;

    /**
     *  @notice Set the deposit fee percent
     *  @param fee The new deposit fee percent
     *  @custom:security only-admin
     */
    function setNewDepositFee(uint256 fee) external;

    /**
     *  @notice Withdraws CYG without caring about rewards
     */
    function emergencyWithdraw() external;
}
"
    },
