// File contracts/Vesting.sol


pragma solidity ^0.8.4;





contract Vesting is AccessControl {
  using SafeERC20 for IERC20;
  using Counters for Counters.Counter;

  bytes32 public constant API_ROLE = keccak256("API_ROLE");
  uint256 public constant PAGE_LIMIT = 100;

  IERC20 public immutable token;
  address public tokenSpender;

  bool isStopped = false;

  struct Vault {
    uint256 initAmount;
    uint256 unclaimedAmount;
    uint256 releaseFrom;
    uint256 releaseInterval;
    uint256 tokensAmountPerInterval;
    uint256 lastRelease;
    uint256 initTime;
    uint256 index;
  }

  mapping(address => Vault[]) public vaults;

  // Holders array
  mapping(address => bool) public isHolder;
  mapping(uint256 => address) public holders;
  Counters.Counter private _holdersCount;
  Counters.Counter private _vaultCount;

  // Events
  event VaultDeposit(
    address indexed receiver,
    uint256 amount,
    uint256 releaseFrom,
    uint256 releaseInterval,
    uint256 tokensAmountPerInterval,
    uint256 indexed vaultIndex
  );
  event VaultWithdraw(address indexed owner, uint256 amount, uint256 indexed vaultIndex);
  event ChangeTokenSpender(address indexed caller, address indexed newTokenSpender);

  constructor(address _tokenAddress, address _tokenSpender) {
    _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    require(_tokenAddress != address(0), "Invalid token address");
    token = IERC20(_tokenAddress);
    require(_tokenSpender != address(0), "Invalid spender address");
    tokenSpender = _tokenSpender;
  }

  modifier stoppedInEmergency() {
    require(!isStopped);
    _;
  }

  modifier onlyWhenStopped() {
    require(isStopped);
    _;
  }

  function changeTokenSpender(address newTokenSpender) external onlyRole(DEFAULT_ADMIN_ROLE) {
    tokenSpender = newTokenSpender;
    emit ChangeTokenSpender(msg.sender, newTokenSpender);
  }

  function vaultsLength(address user) external view returns (uint256 length) {
    return vaults[user].length;
  }

  function vaultsPages(address user) external view returns (uint256 length) {
    length = vaults[user].length / PAGE_LIMIT;
    if (vaults[user].length % PAGE_LIMIT > 0) {
      length += 1;
    }
  }

  function holdersCount() external view returns (uint256) {
    return _holdersCount.current();
  }

  function _addHolder(address holderAddress) internal {
    if (!isHolder[holderAddress]) {
      isHolder[holderAddress] = true;
      holders[_holdersCount.current()] = holderAddress;
      _holdersCount.increment();
    }
  }

  function createVault(
    address to,
    uint256 amount,
    uint256 releaseFrom,
    uint256 releaseInterval,
    uint256 tokensAmountPerInterval
  ) external onlyRole(API_ROLE) stoppedInEmergency {
    uint256 allowance = token.allowance(tokenSpender, address(this));
    uint256 balance = token.balanceOf(tokenSpender);
    require(allowance >= amount, "Invalid allowance");
    require(balance >= amount, "Invalid balance");

    require(tokensAmountPerInterval > 0, "tokensAmountPerInterval must be greater than 0");
    require(to != address(0), "to parameter cannot be a address zero");
    require(releaseInterval > 0, "releaseInterval must be greater than 0");

    _addHolder(to);

    // Add vault
    Vault memory newVault = Vault(
      amount,
      amount,
      releaseFrom,
      releaseInterval,
      tokensAmountPerInterval,
      block.timestamp,
      block.timestamp,
      _vaultCount.current()
    );
    vaults[to].push(newVault);

    emit VaultDeposit(
      to,
      amount,
      releaseFrom,
      releaseInterval,
      tokensAmountPerInterval,
      _vaultCount.current()
    );
    _vaultCount.increment();

    // Transfer tokens from spender to contract
    token.safeTransferFrom(tokenSpender, address(this), amount);
  }

  function _vaultInfo(address userAddress, uint256 index)
    internal
    view
    returns (
      uint256 totalTokens,
      uint256 lockedTokens,
      uint256 unlockedTokens,
      uint256 nextRelease,
      uint256 releaseAmount
    )
  {
    Vault memory userVault = vaults[userAddress][index];
    totalTokens = userVault.initAmount;

    uint256 releasedIntervals = (userVault.initAmount - userVault.unclaimedAmount) /
      userVault.tokensAmountPerInterval;
    uint256 spendableIntervals = 0;
    uint256 intervalsToWithdraw = 0;

    nextRelease = userVault.releaseFrom;
    releaseAmount = userVault.tokensAmountPerInterval;

    if (block.timestamp >= userVault.releaseFrom) {
      spendableIntervals =
        (block.timestamp - userVault.releaseFrom + userVault.releaseInterval) /
        userVault.releaseInterval;
      intervalsToWithdraw = spendableIntervals - releasedIntervals;
      if (userVault.unclaimedAmount == 0) {
        nextRelease = 0;
        releaseAmount = 0;
      } else {
        nextRelease = spendableIntervals * userVault.releaseInterval + userVault.releaseFrom;
      }
    }
    unlockedTokens = intervalsToWithdraw * userVault.tokensAmountPerInterval;

    if (unlockedTokens > userVault.unclaimedAmount) {
      unlockedTokens = userVault.unclaimedAmount;
    }

    lockedTokens = userVault.unclaimedAmount - unlockedTokens;
  }

  function walletInfo(address userAddress, uint256 page)
    external
    view
    returns (
      uint256 totalTokens,
      uint256 lockedTokens,
      uint256 unlockedTokens,
      uint256 nextRelease,
      uint256 nextReleaseAmount
    )
  {
    uint256 from = page * PAGE_LIMIT;
    uint256 to = vaults[userAddress].length;

    require(from < to, "Page does not exist");

    if ((page + 1) * PAGE_LIMIT <= to) {
      to = (page + 1) * PAGE_LIMIT;
    }

    totalTokens = 0;
    lockedTokens = 0;
    unlockedTokens = 0;
    nextRelease = 0;
    nextReleaseAmount = 0;

    for (uint256 index = from; index < to; index++) {
      (
        uint256 vault_totalTokens,
        uint256 vault_lockedTokens,
        uint256 vault_unlockedTokens,
        uint256 vault_nextRelease,
        uint256 vault_releaseAmount
      ) = _vaultInfo(userAddress, index);
      totalTokens += vault_totalTokens;
      unlockedTokens += vault_unlockedTokens;
      lockedTokens += vault_lockedTokens;

      if (vault_lockedTokens > 0) {
        if (nextRelease == 0 || nextRelease > vault_nextRelease) {
          nextRelease = vault_nextRelease;
          nextReleaseAmount = vault_releaseAmount;
        } else if (nextRelease == vault_nextRelease) {
          nextReleaseAmount += vault_releaseAmount;
        }
      }
    }
  }

  function withdrawByVaultIndex(uint256 vaultIndex) external stoppedInEmergency {
    (, , uint256 unlockedTokens, , ) = _vaultInfo(msg.sender, vaultIndex);

    require(unlockedTokens > 0, "Nothing to withdraw");

    Vault storage vault = vaults[msg.sender][vaultIndex];
    vault.lastRelease = block.timestamp;
    vault.unclaimedAmount = vault.unclaimedAmount - unlockedTokens;

    emit VaultWithdraw(msg.sender, unlockedTokens, vault.index);
    token.safeTransfer(msg.sender, unlockedTokens);
  }

  function withdraw(uint256 page) external stoppedInEmergency {
    uint256 totalAmount = 0;
    uint256 nowTimestamp = block.timestamp;
    uint256 from = page * PAGE_LIMIT;
    uint256 to = vaults[msg.sender].length;

    require(from < to, "Page does not exist");

    if ((page + 1) * PAGE_LIMIT <= to) {
      to = (page + 1) * PAGE_LIMIT;
    }

    for (uint256 index = from; index < to; index++) {
      (, , uint256 unlockedTokens, , ) = _vaultInfo(msg.sender, index);
      if (unlockedTokens > 0) {
        vaults[msg.sender][index].lastRelease = nowTimestamp;
        vaults[msg.sender][index].unclaimedAmount =
          vaults[msg.sender][index].unclaimedAmount -
          unlockedTokens;

        emit VaultWithdraw(msg.sender, unlockedTokens, vaults[msg.sender][index].index);
        totalAmount = totalAmount + unlockedTokens;
      }
    }

    require(totalAmount > 0, "Nothing to withdraw");

    token.safeTransfer(msg.sender, totalAmount);
  }

  function resumeContract() external onlyRole(DEFAULT_ADMIN_ROLE) {
    isStopped = false;
  }

  function emergencyWithdraw() external onlyRole(DEFAULT_ADMIN_ROLE) {
    uint256 balanceOnVesting = token.balanceOf(address(this));
    token.safeTransfer(tokenSpender, balanceOnVesting);
    isStopped = true;
  }
}
