pragma solidity ^0.8.2;


contract Pizza is ERC20, AccessControl, ERC721Holder {
    bytes32 public constant CONTRACT_ADMIN_ROLE = keccak256("CONTRACT_ADMIN_ROLE");
    bytes32 public constant BURNER_ROLE = keccak256("BURNER_ROLE");
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

    address public collectionAddressBird;
    address public collectionAddressCape;

    uint256 public numberOfBlocksPerRewardUnit;

    uint256 public coinAmountPerRewardUnitBird;
    uint256 public coinAmountPerRewardUnitCape;

    uint256 public welcomeBonusAmountBird;
    uint256 public welcomeBonusAmountCape;

    uint256 public amountOfStakersBird;
    uint256 public amountOfStakersCape;

    uint256 public tokensStakedBird;
    uint256 public tokensStakedCape;

    uint256 immutable public contractCreationBlock;

    struct StakeInfo {
        uint256 stakedAtBlock;
        uint256 lastHarvestBlock;
        bool currentlyStaked;
    }
    /// owner => tokenId => StakeInfo
    mapping (address => mapping(uint256 => StakeInfo)) public stakeLogBird;

        /// owner => tokenId => StakeInfo
    mapping (address => mapping(uint256 => StakeInfo)) public stakeLogCape;

    /// owner => #NFTsStaked
    mapping (address => uint256) public tokensStakedByUserBird;

        /// owner => #NFTsStaked
    mapping (address => uint256) public tokensStakedByUserCape;

    /// tokenId => true/false (true if welcome bonus has been collected for a specific tokenId)
    mapping (uint256 => bool) public welcomeBonusCollectedBird;

        /// tokenId => true/false (true if welcome bonus has been collected for a specific tokenId)
    mapping (uint256 => bool) public welcomeBonusCollectedCape;

    /// owner => list of all tokenIds that the user has staked
    mapping (address => uint256[]) public stakePortfolioByUserBird;

        /// owner => list of all tokenIds that the user has staked
    mapping (address => uint256[]) public stakePortfolioByUserCape;

    /// tokenId => indexInStakePortfolio
    mapping(uint256 => uint256) public indexOfTokenIdInStakePortfolioBird;

        /// tokenId => indexInStakePortfolio
    mapping(uint256 => uint256) public indexOfTokenIdInStakePortfolioCape;

    event RewardsHarvestedBird (address owner, uint256 amount);
    event NFTStakedBird (address owner, uint256 tokenId);
    event NFTUnstakedBird (address owner, uint256 tokenId);

    event RewardsHarvestedCape (address owner, uint256 amount);
    event NFTStakedCape (address owner, uint256 tokenId);
    event NFTUnstakedCape (address owner, uint256 tokenId);

    constructor(address owner, address _collectionAddressBird, address _collectionAddressCape) ERC20("Pizza", "PIZZA") AccessControl(){
        _mint(owner, 25185000 * 10 ** 18);
        _setupRole(DEFAULT_ADMIN_ROLE, owner);
        _setupRole(CONTRACT_ADMIN_ROLE, owner);
        _setupRole(BURNER_ROLE, owner);
        _setupRole(MINTER_ROLE, owner);
        collectionAddressBird = _collectionAddressBird;
        collectionAddressCape = _collectionAddressCape;
        contractCreationBlock = block.number;
        coinAmountPerRewardUnitBird = 69 * 10 ** 18; // 69 ERC20 coins per rewardUnit, may be changed later on
        coinAmountPerRewardUnitCape = 172.5 * 10 ** 18; // 172.5 ERC20 coins per rewardUnit, may be changed later on
        numberOfBlocksPerRewardUnit = 18782; // 24 hours per reward unit , may be changed later on
        welcomeBonusAmountBird = 100 * 10 ** 18; // 100 tokens welcome bonus, only paid once per tokenId
        welcomeBonusAmountCape = 250 * 10 ** 18; // 250 tokens welcome bonus, only paid once per tokenId
    }

    function stakedNFTSByUserBird(address owner) external view returns (uint256[] memory){
        return stakePortfolioByUserBird[owner];
    }

        function stakedNFTSByUserCape(address owner) external view returns (uint256[] memory){
        return stakePortfolioByUserCape[owner];
    }

    function mint(address to, uint256 amount) public onlyRole(MINTER_ROLE) {
        _mint(to, amount);
    }

    function burn(address from, uint256 amount) external onlyRole(BURNER_ROLE) {
        super._burn(from, amount);
    }

    function pendingRewardsBird(address owner, uint256 tokenId) public view returns (uint256){
        StakeInfo memory info = stakeLogBird[owner][tokenId];

        if(info.lastHarvestBlock < contractCreationBlock || info.currentlyStaked == false) {
            return 0;
        }
        uint256 blocksPassedSinceLastHarvest = block.number - info.lastHarvestBlock;
        if (blocksPassedSinceLastHarvest < numberOfBlocksPerRewardUnit * 2) {
            return 0;
        }
        uint256 rewardAmount = blocksPassedSinceLastHarvest / numberOfBlocksPerRewardUnit - 1;
        return rewardAmount * coinAmountPerRewardUnitBird;

    }

        function pendingRewardsCape(address owner, uint256 tokenId) public view returns (uint256){
        StakeInfo memory info = stakeLogCape[owner][tokenId];

        if(info.lastHarvestBlock < contractCreationBlock || info.currentlyStaked == false) {
            return 0;
        }
        uint256 blocksPassedSinceLastHarvest = block.number - info.lastHarvestBlock;
        if (blocksPassedSinceLastHarvest < numberOfBlocksPerRewardUnit * 2) {
            return 0;
        }
        uint256 rewardAmount = blocksPassedSinceLastHarvest / numberOfBlocksPerRewardUnit - 1;
        return rewardAmount * coinAmountPerRewardUnitCape;

    }
    function stakeBird(uint256 tokenId) public {
        IERC721(collectionAddressBird).safeTransferFrom(_msgSender(), address(this), tokenId);
        require(IERC721(collectionAddressBird).ownerOf(tokenId) == address(this),
            "PIZZA: Error while transferring token");
        StakeInfo storage info = stakeLogBird[_msgSender()][tokenId];
        info.stakedAtBlock = block.number;
        info.lastHarvestBlock = block.number;
        info.currentlyStaked = true;
        if(tokensStakedByUserBird[_msgSender()] == 0){
            amountOfStakersBird += 1;
        }
        tokensStakedByUserBird[_msgSender()] += 1;
        tokensStakedBird += 1;
        stakePortfolioByUserBird[_msgSender()].push(tokenId);
        uint256 indexOfNewElement = stakePortfolioByUserBird[_msgSender()].length - 1;
        indexOfTokenIdInStakePortfolioBird[tokenId] = indexOfNewElement;
        if(!welcomeBonusCollectedBird[tokenId]) {
            _mint(_msgSender(), welcomeBonusAmountBird);
            welcomeBonusCollectedBird[tokenId] = true;
        }

        emit NFTStakedBird(_msgSender(), tokenId);
    }

        function stakeCape(uint256 tokenId) public {
        IERC721(collectionAddressCape).safeTransferFrom(_msgSender(), address(this), tokenId);
        require(IERC721(collectionAddressCape).ownerOf(tokenId) == address(this),
            "PIZZA: Error while transferring token");
        StakeInfo storage info = stakeLogCape[_msgSender()][tokenId];
        info.stakedAtBlock = block.number;
        info.lastHarvestBlock = block.number;
        info.currentlyStaked = true;
        if(tokensStakedByUserCape[_msgSender()] == 0){
            amountOfStakersCape += 1;
        }
        tokensStakedByUserCape[_msgSender()] += 1;
        tokensStakedCape += 1;
        stakePortfolioByUserCape[_msgSender()].push(tokenId);
        uint256 indexOfNewElement = stakePortfolioByUserCape[_msgSender()].length - 1;
        indexOfTokenIdInStakePortfolioCape[tokenId] = indexOfNewElement;
        if(!welcomeBonusCollectedCape[tokenId]) {
            _mint(_msgSender(), welcomeBonusAmountCape);
            welcomeBonusCollectedCape[tokenId] = true;
        }

        emit NFTStakedCape(_msgSender(), tokenId);
    }

    function stakeBatchBird(uint256[] memory tokenIds) external {
        for(uint currentId = 0; currentId < tokenIds.length; currentId++) {
            if(tokenIds[currentId] == 0) {
                continue;
            }
            stakeBird(tokenIds[currentId]);
        }
    }

    function stakeBatchCape(uint256[] memory tokenIds) external {
        for(uint currentId = 0; currentId < tokenIds.length; currentId++) {
            if(tokenIds[currentId] == 0) {
                continue;
            }
            stakeCape(tokenIds[currentId]);
        }
    }

    function unstakeBatchBird(uint256[] memory tokenIds) external {
        for(uint currentId = 0; currentId < tokenIds.length; currentId++) {
            if(tokenIds[currentId] == 0) {
                continue;
            }
            unstakeBird(tokenIds[currentId]);
        }
    }

        function unstakeBatchCape(uint256[] memory tokenIds) external {
        for(uint currentId = 0; currentId < tokenIds.length; currentId++) {
            if(tokenIds[currentId] == 0) {
                continue;
            }
            unstakeCape(tokenIds[currentId]);
        }
    }

    function unstakeBird(uint256 tokenId) public {
        if(pendingRewardsBird(_msgSender(), tokenId) > 0){
            harvestBird(tokenId);
        }
        StakeInfo storage info = stakeLogBird[_msgSender()][tokenId];
        info.currentlyStaked = true;
        IERC721(collectionAddressBird).safeTransferFrom(address(this), _msgSender(), tokenId);
        require(IERC721(collectionAddressBird).ownerOf(tokenId) == _msgSender(),
            "PIZZA: Error while transferring token");
        if(tokensStakedByUserBird[_msgSender()] == 1){
            amountOfStakersBird -= 1;
        }
        tokensStakedByUserBird[_msgSender()] -= 1;
        tokensStakedBird -= 1;
        stakePortfolioByUserBird[_msgSender()][indexOfTokenIdInStakePortfolioBird[tokenId]] = 0;
        emit NFTUnstakedBird(_msgSender(), tokenId);
    }

    function unstakeCape(uint256 tokenId) public {
        if(pendingRewardsCape(_msgSender(), tokenId) > 0){
            harvestCape(tokenId);
        }
        StakeInfo storage info = stakeLogCape[_msgSender()][tokenId];
        info.currentlyStaked = true;
        IERC721(collectionAddressCape).safeTransferFrom(address(this), _msgSender(), tokenId);
        require(IERC721(collectionAddressCape).ownerOf(tokenId) == _msgSender(),
            "PIZZA: Error while transferring token");
        if(tokensStakedByUserCape[_msgSender()] == 1){
            amountOfStakersCape -= 1;
        }
        tokensStakedByUserCape[_msgSender()] -= 1;
        tokensStakedCape -= 1;
        stakePortfolioByUserCape[_msgSender()][indexOfTokenIdInStakePortfolioCape[tokenId]] = 0;
        emit NFTUnstakedCape(_msgSender(), tokenId);
    }

    function harvestBird(uint256 tokenId) public {
        StakeInfo storage info = stakeLogBird[_msgSender()][tokenId];
        uint256 rewardAmountInERC20Tokens = pendingRewardsBird(_msgSender(), tokenId);
        if(rewardAmountInERC20Tokens > 0) {
            info.lastHarvestBlock = block.number;
            _mint(_msgSender(), rewardAmountInERC20Tokens);
            emit RewardsHarvestedBird(_msgSender(), rewardAmountInERC20Tokens);
        }
    }

    function harvestCape(uint256 tokenId) public {
        StakeInfo storage info = stakeLogCape[_msgSender()][tokenId];
        uint256 rewardAmountInERC20Tokens = pendingRewardsCape(_msgSender(), tokenId);
        if(rewardAmountInERC20Tokens > 0) {
            info.lastHarvestBlock = block.number;
            _mint(_msgSender(), rewardAmountInERC20Tokens);
            emit RewardsHarvestedCape(_msgSender(), rewardAmountInERC20Tokens);
        }
    }

    function harvestBatchBird(address user) external {
        uint256[] memory tokenIds = stakePortfolioByUserBird[user];

        for(uint currentId = 0; currentId < tokenIds.length; currentId++) {
            if(tokenIds[currentId] == 0) {
                continue;
            }
            harvestBird(tokenIds[currentId]);
        }
    }

    function harvestBatchCape(address user) external {
        uint256[] memory tokenIds = stakePortfolioByUserCape[user];

        for(uint currentId = 0; currentId < tokenIds.length; currentId++) {
            if(tokenIds[currentId] == 0) {
                continue;
            }
            harvestCape(tokenIds[currentId]);
        }
    }

    // ADMIN / SETTER FUNCTIONS
    function setNumberOfBlocksPerRewardUnit(uint256 numberOfBlocks) external onlyRole(CONTRACT_ADMIN_ROLE){
        numberOfBlocksPerRewardUnit = numberOfBlocks;
    }

    // parameter value must be supplied with 18 zeros.....
    // e.g.: 3 token = setCoinAmountPerRewardUnit(3000000000000000000)
    function setCoinAmountPerRewardUnitBird(uint256 coinAmount) external onlyRole(CONTRACT_ADMIN_ROLE){
        coinAmountPerRewardUnitBird = coinAmount;
    }

    function setCoinAmountPerRewardUnitCape(uint256 coinAmount) external onlyRole(CONTRACT_ADMIN_ROLE){
        coinAmountPerRewardUnitCape = coinAmount;
    }

    // same as setCoinAmountPerRewardUnit()
    function setWelcomeBonusAmountBird(uint256 coinAmount) external onlyRole(CONTRACT_ADMIN_ROLE){
        welcomeBonusAmountBird = coinAmount;
    }

    function setWelcomeBonusAmountCape(uint256 coinAmount) external onlyRole(CONTRACT_ADMIN_ROLE){
        welcomeBonusAmountCape = coinAmount;
    }

    function setCollectionAddressBird(address newAddress) external onlyRole(CONTRACT_ADMIN_ROLE){
        require (newAddress != address(0), "VIBE: update to zero address not possible");
        collectionAddressBird = newAddress;
    }

    function setCollectionAddressCape(address newAddress) external onlyRole(CONTRACT_ADMIN_ROLE){
        require (newAddress != address(0), "VIBE: update to zero address not possible");
        collectionAddressCape = newAddress;
    }
