      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@chainlink/contracts/src/v0.8/VRFConsumerBase.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

interface ILuchaPinatas {
  function balanceOf(address account, uint256 id) external view returns (uint256);
	function mint(address account, uint256 id, uint256 amount, bytes memory data) external;
	function mintBatch(address to, uint256[] memory ids, uint256[] memory amounts, bytes memory data) external;
	function burn(address account, uint256 id, uint256 value) external;
	function isApprovedForAll(address account, address operator) external view returns (bool);
}

interface ILuchaWearables {
	function mintBatch(address to, uint256[] memory ids, uint256[] memory amounts, bytes memory data) external;
}

interface ILuchaPriceOracle {
	function luchaPerMatic() external view returns(uint256);
}

interface IUniswapV2Pair {
	function allowance(address owner, address spender) external view returns (uint);
	function transferFrom(address from, address to, uint value) external returns (bool);
}

contract LuchaPinatasAztec is VRFConsumerBase, AccessControl, Pausable, ReentrancyGuard {
	using SafeMath for uint256;

	IERC20 public luchaToken = IERC20(0x6749441Fdc8650b5b5a854ed255C82EF361f1596);
	ILuchaPinatas public luchaPinatas = ILuchaPinatas(0x118cA8903e25537151C991b40e943D4C73e0876E);
	ILuchaWearables public luchaWearables = ILuchaWearables(0xE80E28a0eec4CB5242c77fDF86cE11A8F97F7e07);
	ILuchaPriceOracle public luchaPriceOracle = ILuchaPriceOracle(0x6C99F370a4C1942f3942c7bb3e37117117688051);

	address public multiSig = 0x0Cb11b92Fa5C30eAfe4aE84B7BB4dF3034C38b9d;

	address internal linkToken = 0xb0897686c545045aFc77CF20eC7A532E3120E0F1;
	address internal vrfCoordinator = 0x3d2341ADb2D31f1c5530cDC622016af293177AE0;
	bytes32 internal keyHash = 0xf86195cf7690c55907b2b611ebb7343a6f649bff128701cc542f0569e2c549da;
	uint256 internal fee = 0.0001 ether;

	uint256 public pinataId = 2;
	uint256 public maticPrice = 55 ether;
	uint256 public remainingMaticSupply = 3725;
	uint256 public remainingLuchaSupply = 500;

	bool public pinatasBreakable;

	struct BreakPinataDetails {
		address account;
		uint256 breakAmount;
  }

	mapping(bytes32 => BreakPinataDetails) internal breakPinataDetails;

	constructor() VRFConsumerBase(vrfCoordinator, linkToken) {
		_setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
	}

	function mint(uint256 amount, uint256 quote, bool lucha) external payable whenNotPaused nonReentrant {
		require(amount > 0 && amount <= 5, "Mint amount must be 1-5");

		if (lucha) {
			require(amount <= remainingLuchaSupply, "Amount exceeds remaining Lucha supply");
			remainingLuchaSupply -= amount;
			uint256 luchaAmount = luchaPriceOracle.luchaPerMatic().mul(maticPrice.div(1 ether).mul(amount));
			require(luchaAmount <= quote, "Slippage exceeded");
			luchaToken.transferFrom(msg.sender, multiSig, luchaAmount);
		} else {
			require(amount <= remainingMaticSupply, "Amount exceeds remaining Matic supply");
			require(msg.value == maticPrice.mul(amount), "Incorrect MATIC amount");
			remainingMaticSupply -= amount;
			(bool success, ) = payable(multiSig).call{value: msg.value}("");
      require(success, "Transfer failed.");
		}

		luchaPinatas.mint(msg.sender, pinataId, amount, "");
	}

	function breakPinata(uint256 amount) external payable whenNotPaused nonReentrant {
		require(pinatasBreakable, "The party has not started yet");
		require(amount > 0 && amount <= 3, "Break amount must be 1-3");
		require(luchaPinatas.balanceOf(msg.sender, pinataId) >= amount, "Insufficient pinata balance");
		require(luchaPinatas.isApprovedForAll(msg.sender, address(this)), "Not approved to burn");
		require(LINK.balanceOf(address(this)) >= fee, "Not enough LINK in contract");

		luchaPinatas.burn(msg.sender, pinataId, amount);

		bytes32 requestId = requestRandomness(keyHash, fee);

		breakPinataDetails[requestId] = BreakPinataDetails({
			account: msg.sender,
			breakAmount: amount
		});
	}

	function fulfillRandomness(bytes32 requestId, uint256 randomness) internal override {
		address account = breakPinataDetails[requestId].account;
		uint256 amount = breakPinataDetails[requestId].breakAmount;
		uint256 cleanLength;

		uint256[] memory ids = new uint256[](amount);
		uint256[] memory amounts = new uint256[](amount);

    for (uint256 i = 0; i < amount; i++) {
			uint256 randomNum = uint256(keccak256(abi.encode(randomness, i))) % 4500 + 1;
			uint256 id;
			bool duplicate;

			if (randomNum > 4000) {
				id = 26;
			} else if (randomNum > 3500) {
				id = 27;
			} else if (randomNum > 3000) {
				id = 28;
			} else if (randomNum > 2500) {
				id = 29;
			} else if (randomNum > 2000) {
				id = 30;
			} else if (randomNum > 1750) {
				id = 31;
			} else if (randomNum > 1500) {
				id = 32;
			} else if (randomNum > 1250) {
				id = 33;
			} else if (randomNum > 1000) {
				id = 34;
			} else if (randomNum > 750) {
				id = 35;
			} else if (randomNum > 650) {
				id = 36;
			} else if (randomNum > 550) {
				id = 37;
			} else if (randomNum > 450) {
				id = 38;
			} else if (randomNum > 350) {
				id = 39;
			} else if (randomNum > 250) {
				id = 40;
			} else if (randomNum > 210) {
				id = 41;
			} else if (randomNum > 170) {
				id = 42;
			} else if (randomNum > 130) {
				id = 43;
			} else if (randomNum > 90) {
				id = 44;
			} else if (randomNum > 50) {
				id = 45;
			} else if (randomNum > 40) {
				id = 46;
			} else if (randomNum > 30) {
				id = 47;
			} else if (randomNum > 20) {
				id = 48;
			} else if (randomNum > 10) {
				id = 49;
			} else if (randomNum > 0) {
				id = 50;
			}

			for (uint256 j = 0; j < ids.length; j++) {
				if (ids[j] == id) {
					duplicate = true;
					amounts[j]++;
					break;
				}
			}

			if (!duplicate) {
				for (uint256 j = 0; j < ids.length; j++) {
					if (ids[j] == 0) {
						cleanLength++;
						ids[j] = id;
						amounts[j]++;
						break;
					}
				}
			}
    }

		uint256[] memory cleanIds = new uint256[](cleanLength);
		uint256[] memory cleanAmounts = new uint256[](cleanLength);

		for (uint256 i = 0; i < cleanLength; i++) {
			cleanIds[i] = ids[i];
			cleanAmounts[i] = amounts[i];
		}

		luchaWearables.mintBatch(account, cleanIds, cleanAmounts, "");
  }

	function setPinatasBreakable(bool value) external onlyRole(DEFAULT_ADMIN_ROLE) {
		pinatasBreakable = value;
	}

	function setMaticPrice(uint256 value) external onlyRole(DEFAULT_ADMIN_ROLE) {
		maticPrice = value;
	}

	function setLinkFee(uint256 value) external onlyRole(DEFAULT_ADMIN_ROLE) {
		fee = value;
	}

	function pause() external onlyRole(DEFAULT_ADMIN_ROLE) {
		_pause();
	}

	function unpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
		_unpause();
	}

	function withdraw(address _tokenContract, uint256 _amount) external onlyRole(DEFAULT_ADMIN_ROLE) {
		IERC20 tokenContract = IERC20(_tokenContract);
		tokenContract.transfer(msg.sender, _amount);
  }
}
