    "src/common/Treasury.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.21;

import { Ownable } from "solady/auth/Ownable.sol";
import { ITreasury } from "@/common/ITreasury.sol";

/// @title Treasury
/// @author Mathieu Bour for Pooky Labs Ltd.
///
/// @notice Base class for contracts that are made to receive native currency.
/// The destination address is controller by the contract owner.
abstract contract Treasury is Ownable, ITreasury {
  /// The native currency destination address.
  address public treasury;

  constructor(address _treasury) {
    treasury = _treasury;
  }

  /// @notice Forward the funds to the treasury wallet at the end of the transaction.
  /// Since `treasury` is a trusted address, this modifier should not lead to any re-entrancy issue.
  modifier forwarder() {
    _;

    uint256 value = address(this).balance;
    (bool sent,) = treasury.call{ value: value }("");
    if (!sent) {
      revert TransferFailed(treasury, value);
    }
  }

  /// Change the native currency destination address.
  /// @param _treasury The new treasury address.
  function changeTreasury(address _treasury) external onlyOwner {
    treasury = _treasury;
  }
}
"
    },
    "src/common/Signer.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.21;

import { OwnableRoles } from "solady/auth/OwnableRoles.sol";
import { ECDSA } from "solady/utils/ECDSA.sol";

/// @title Signer
/// @author Mathieu Bour for Pooky Labs Ltd.
///
/// @dev Provide the `verify` function and the `onlyVerify` modifier to child contracts.
abstract contract Signer is OwnableRoles {
  using ECDSA for bytes32;

  uint256 public constant SIGNER = _ROLE_42;

  /// Thrown when the signature is invalid.
  error InvalidSignature();

  constructor(address signer) {
    _grantRoles(signer, SIGNER);
  }

  /// @notice Ensure that `data` has been signed by a `SIGNER` using the `proof`.
  function verify(bytes memory data, bytes calldata proof) internal view {
    // Generate the signed message from the tokenId and currentPXP
    bytes32 hash = keccak256(data).toEthSignedMessageHash();

    if (!hasAllRoles(hash.recoverCalldata(proof), SIGNER)) {
      revert InvalidSignature();
    }
  }

  /// @notice Modifier version of the `verify` function.
  modifier onlyVerify(bytes memory data, bytes calldata proof) {
    verify(data, proof);
    _;
  }
}
"
