pragma solidity ^0.8.13;

import "./ONFT1155/ONFT1155.sol";

/// @title Interface of the UniversalONFT standard.
contract UniversalONFT1155 is ONFT1155 {
    /// @notice Constructor for the UniversalONFT.
    /// @param _uri               The base uri of the token metadata.
    /// @param _layerZeroEndpoint Handles message transmission across chains.
    constructor(string memory _uri, address _layerZeroEndpoint) ONFT1155(_uri, _layerZeroEndpoint) {}

    /// @notice Mints `_amounts` of item `_ids` to address `_to`.
    /// @param  _to      The address to mint items on.
    /// @param  _ids     The ids of tokens to be minted.
    /// @param  _amounts The amounts of tokens to be minted.
    function mintBatch(address _to, uint256[] memory _ids, uint256[] memory _amounts, bytes memory _data)
        external
        onlyOwner
    {
        _mintBatch(_to, _ids, _amounts, _data);
    }

    /// @notice Mints `_amount` of the `_tokenId` to `msg.sender`.
    /// @param  _tokenId The id of the token to be minted.
    /// @param  _amount  The amount of the token to be minted.
    function mint(uint256 _tokenId, uint256 _amount, bytes memory _data) external onlyOwner {
        _mint(msg.sender, _tokenId, _amount, _data);
    }

    /// @notice Sets the `_uri` of the contract.
    /// @param  newURI New URI.
    function setURI(string memory newURI) external onlyOwner {
        _setURI(newURI);
    }
} 

