pragma solidity ^0.8.0;

import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol";
import "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";

contract Oracle is Initializable{
    IUniswapV3Pool public pool;
    AggregatorV3Interface public constant aggregator = AggregatorV3Interface(0xAB594600376Ec9fD91F8e885dADF0CE036862dE0);

    constructor() {}

    function initialize() public initializer {}

    function getPrice() public view returns (uint256) {
        (,int256 price, , ,) = aggregator.latestRoundData();

        return uint256(price) / 100;
    }

    function calculateUsdtAmount(uint256 nativeAmount) public view returns (uint256) {
        uint256 usdtAmount = nativeAmount * getPrice() / 1e18;
        return usdtAmount;
    }

    function calculateNativeAmount(uint256 usdtAmount) public view returns (uint256) {
        uint256 nativeAmount = usdtAmount * 1e18 / getPrice();
        return nativeAmount;
    }
}
